void hitWallOfadj(in vec2 c,in float prev_cell_height,in vec3 i,inout float h, inout float bdist,inout vec4 hit_nt,inout bool hit,inout vec3 hs,out vec3 n){  
 hit_nt=vec4(n.xy,0.,n.z);
