 hs=vec3(.3,1.,.7);
 //distance to wall top: ... //distance to wall bottom:
 bdist=min(h-i.z,i.z-prev_cell_height);
 vec2 p=i.xy-c;//distance to wall outer side corner
 p=p-n.xy*dot(p,n.xy);
 n=min(n,abs(length(p)-.5/p322));
 }

