 vec3 rz=normalize(tgt-o);
 vec3 rx=normalize(cross(rz,up));
 vec3 ry=cross(rx,rz);
 float s=1.+dot(uvn,uvn)*1.;//radial distortion
 d=mat3(rx,ry,rz)*normalize(vec3(u*s,f));
 float thetax=-.35-.2*cos(.031513*iTime); 
 float thetay=-.2*iTime;
 if(any(greaterThan(iMouse.xy,vec2(10.)))){
  thetax=(iMouse.y-.5*iResolution.y)*-1./iResolution.y;
  thetay=(iMouse.x-.5*iResolution.x)*6./iResolution.x; 
 }/**/
 float cx=cos(thetax),sx=sin(thetax),cy=cos(thetay),sy=sin(thetay);
 mat3 Rx=mat3(1,0,0,0,cx,sx,0,-sx,cx),
 Ry=mat3(cy,0,-sy,0,1,0,sy,0,cy),
 R=mat3(0,0,1,-1,0,0,0,1,0),
 Rt=transpose(R);
 d=Rt*Ry*Rx*R*d;
 o=Rt*Ry*Rx*R*(o-tgt)+tgt;}

