            d -= step(dot(nn,nn), r*r);
            d -= step(dot(ns,ns), r*r);
            d -= step(dot(ne, ne),r*r);
            d -= step(dot(nw, nw),r*r);

            d -= step(dot(wn,wn), r*r);
            d -= step(dot(ws,ws), r*r);
            d -= step(dot(we, we),r*r);
            d -= step(dot(ww, ww),r*r);

            d -= step(dot(sn,sn), r*r);
            d -= step(dot(ss,ss), r*r);
            d -= step(dot(se, se),r*r);
            d -= step(dot(sw, sw),r*r);
        }
        else
        {
        	d += step(dot(en,en), r*r);
            d += step(dot(es,es), r*r);
            d += step(dot(ee, ee),r*r);
            d += step(dot(ew, ew),r*r);

            d += step(dot(nn,nn), r*r);
