float map( vec2 p ) 
{
	float f = textureLod( iChannel0, p/iChannelResolution[0].xy, 0.0 ).x;

	f *= sqrt( textureLod( iChannel2, (0.03*p + 2.0*gAnimTime)/256.0, 0.0 ).x );
