vec3 calcNormal( in vec3 pos, in float ic )
{
	return mix( normalize(vec3(pos.x,0.0,pos.z)), vec3(0.0,1.0,0.0), ic );
}

vec4 castRay( in vec3 ro, in vec3 rd )
{
	vec2 pos = floor(ro.xz);
	vec2 ri = 1.0/rd.xz;
	vec2 rs = sign(rd.xz);
	vec2 ris = ri*rs;
	vec2 dis = (pos-ro.xz+ 0.5 + rs*0.5) * ri;

	vec4 res = vec4( -1.0, 0.0, 0.0, 0.0 );

    // traverse regular grid (in 2D)
	vec2 mm = vec2(0.0);
	for( int i=0; i<400; i++ ) 
	{

		float ma = map(pos);

        // intersect capped cylinder		
		vec3  ce = vec3( pos.x+0.5, 0.0, pos.y+0.5 );
		vec3  rc = ro - ce;
		float a = dot( rd.xz, rd.xz );
		float b = dot( rc.xz, rd.xz );
		float c = dot( rc.xz, rc.xz ) - 0.249;
		float h = b*b - a*c;
		if( h>=0.0 )
		{
            // cylinder			
			float s = (-b - sqrt( h ))/a;
			if( s>0.0 && (ro.y+s*rd.y)<ma )
			{
				res = vec4( s, 0.0, pos );
    			break; 
			}
            // cap			
			s = (ma - ro.y)/rd.y;
			if( s>0.0 && (s*s*a+2.0*s*b+c)<0.0 )
			{
				res = vec4( s, 1.0, pos );
				break;
			}
		}

        // step to next cell		
		mm = step( dis.xy, dis.yx ); 
		dis += mm*ris*0.3;
        pos += mm*rs;
	}


	return res;
}

float castShadowRay( in vec3 ro, in vec3 rd )
{
	vec2 pos = floor(ro.xz);
	vec2 ri = 1.0/rd.xz;
	vec2 rs = sign(rd.xz);
	vec2 ris = ri*rs;
	vec2 dis = (pos-ro.xz+ 0.5 + rs*0.5) * ri;
	float t = -1.0;
	float res = 1.0;

    // first step we check noching	
	vec2 mm = step( dis.xy, dis.yx ); 
	dis += mm * ris;
    pos += mm * rs;

    // traverse regular grid (2D)	
	for( int i=0; i<16; i++ ) 
	{
		float ma = map(pos);

        // test capped cylinder		
		vec3  ce = vec3( pos.x+0.5, 0.0, pos.y+0.5 );
		vec3  rc = ro - ce;
		float a = dot( rd.xz, rd.xz );
		float b = dot( rc.xz, rd.xz );
		float c = dot( rc.xz, rc.xz ) - 0.249;
		float h = b*b - a*c;
		if( h>=0.0 )
		{
			float t = (-b - sqrt( h ))/a;
			if( (ro.y+t*rd.y)<ma )
			{
				res = 0.0;
    			break; 
			}
		}
		mm = step( dis.xy, dis.yx ); 
		dis += mm * ris;
        pos += mm * rs;
	}

	return res;
}

vec3 cameraPath( float t )
{
    // procedural path	
    vec2 p  = 200.0*sin( 0.01*t*vec2(1.2,1.0) + vec2(0.1,0.9) );
	     p += 100.0*sin( 0.02*t*vec2(1.1,1.3) + vec2(1.0,4.5) );
	float y = 15.0 + 4.0*sin(0.05*t);

	// collision
	float h;
	h  = map( p+vec2(-1.0, 0.0) );
	h += map( p+vec2( 1.0, 0.0) );
	h += map( p+vec2( 0.0, 1.0) );
	h += map( p+vec2( 0.0,-1.0) );
	h /= 4.0;
	h += 5.0;
	y = max( y, h );

	return vec3( p.x, y, p.y );
}

vec4 texcyl( sampler2D sam, in vec3 p, in vec3 n )
{
	vec4 x = texture( sam, vec2(p.y,0.5+0.5*atan(n.x,n.z)/3.14) );
	vec4 y = texture( sam, p.xz );
	return mix( x, y, abs(n.y) );
}

vec3 desat( in vec3 col, float a )
{
    return mix( col, vec3(dot(col,vec3(0.333))), a );
}

vec3 lig = normalize(vec3(-0.7,0.25,0.6));

vec3 render( in vec3 ro, in vec3 rd )
{
        // background color	
		float sun = clamp( dot( rd, lig ), 0.0, 1.0 );

		vec3 bgcol = vec3(0.9,0.9,0.8) + 0.3*pow(sun,4.0)*vec3(1.0,1.0,0.0);

        // raytrace
        vec3 col = bgcol;
		vec4 res = castRay( ro, rd );
        vec2 vos = res.zw;
        float t = res.x;
        if( t>0.0 )
        {
            vec3  pos = ro + rd*t;
			float id  = hash1( vos );
			vec3  nor = calcNormal( fract(pos)-0.5, res.y );
			float h = map(vos);

            // material color			
			vec3 mate1 = 0.5 + 0.45*sin( 3.14*id + 0.8 + vec3(0.0,0.5,1.0) );
			vec3 mate2 = 0.5 + 0.45*sin( 6.28*id + vec3(0.0,0.5,1.0) );
			vec3 mate = mix( mate1, mate2, smoothstep( 9.0, 11.0, h) );

			vec3 uvw = pos - vec3(0.0,h,0.0);
			vec3 tex = texcyl( iChannel3, 0.2*uvw + 13.1*hash1(id), nor ).xyz;
			mate *= 0.2+4.0*pow( desat(tex,0.3), vec3(2.0) );
            mate *= 1.5*sqrt(texture( iChannel0, pos.xz/iChannelResolution[0].xy ).xyz);
            mate *= 0.02 + 0.98*smoothstep( 0.1,0.11, hash1(id) );	

            // material cheap/wrong bump			 
            vec3 bn = -1.0 + 2.0*texcyl( iChannel1, 0.2*uvw*vec3(1.0,0.2,1.0) + 13.1*hash1(id), nor ).xyz;
            //nor = normalize(nor + 0.45*bn*(1.0-0.5*nor.y) );

			// procedural occlusion
			float occ = nor.y*0.75;
			occ += 0.5*clamp( nor.x,0.0,1.0)*smoothstep( -0.5, 0.5, pos.y-map(vos+vec2( 1.0, 0.0)) );
			occ += 0.5*clamp(-nor.x,0.0,1.0)*smoothstep( -0.5, 0.5, pos.y-map(vos+vec2(-1.0, 0.0)) );
			occ += 0.5*clamp( nor.z,0.0,1.0)*smoothstep( -0.5, 0.5, pos.y-map(vos+vec2( 0.0, 1.0)) );
			occ += 0.5*clamp(-nor.z,0.0,1.0)*smoothstep( -0.5, 0.5, pos.y-map(vos+vec2( 0.0,-1.0)) );
			occ = 0.2 + 0.8*occ;
			occ *= pow( clamp((0.1+pos.y)/(0.1+map(floor(pos.xz))),0.0,1.0),2.0);
			occ = occ*0.5+0.5*occ*occ;
			float rim = pow( clamp( 1.0+dot(rd,nor), 0.0, 1.0 ),5.0 );

            //-------------
            // lighitng
            //-------------
			float amb = 1.0;
            //-------------
			float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z)) ), 0.0, 1.0 ) * clamp(1.0-pos.y/20.0,0.0,1.0);;
            //-------------
			float sha = 0.0;
			float dif = dot( nor, lig );
			if( dif<0.0 ) dif=0.0; else sha = castShadowRay( pos, lig );
            float spe = pow( clamp( dot(lig,reflect(rd,nor)), 0.0, 1.0 ),3.0 );
            //-------------
			vec3 lin  = 3.00*vec3(1.0,1.0,1.0)*0.7*sqrt(dif)*sha;
			     lin += 0.40*vec3(0.4,1.0,1.7)*amb*occ;
			     lin += 0.60*vec3(0.8,0.5,0.3)*bac*occ;

			col = mate * lin + tex.x*1.5*vec3(1.0,1.0,1.0)*(0.3+0.7*rim)*spe*dif*sha;

            // tone mapping			
			col *= 1.1 + 0.5*dot( rd, lig );

            // fog
            float ff = 1.0 - smoothstep( 0.0, 1.0, pow(t/160.0,1.8) );
			col = mix( col, bgcol, 1.0-ff );
		}
		col += 0.2*pow(sun,8.0)*vec3(1.0,0.7,0.2);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // inputs	
	vec2 q = fragCoord.xy / iResolution.xy;

    vec2 mo = iMouse.xy / iResolution.xy;
    if( iMouse.w<=0.00001 ) mo=vec2(0.0);

    gAnimTime = controlledTime();

	// montecarlo	
	vec3 tot = vec3(0.0);
    #if VIS_SAMPLES<2
	int a = 0;
	{
        vec2 p = -1.0 + 2.0*(fragCoord.xy) / iResolution.xy;
        p.x *= iResolution.x/ iResolution.y;
        float time = 4.0*controlledTime() + 50.0*mo.x;
    #else
	for( int a=0; a<VIS_SAMPLES; a++ )
	{
		vec4 rr = texture( iChannel1, (fragCoord.xy+floor(256.0*hash2(float(a))))/iChannelResolution[1].xy );
        vec2 p = -1.0 + 2.0*(fragCoord.xy+rr.xz) / iResolution.xy;
        p.x *= iResolution.x/ iResolution.y;
		#if VIS_SAMPLES>3
        float time = 4.0*(controlledTime() + 1.0*(0.4/24.0)*rr.w) + 50.0*mo.x;
		#else
        float time = 4.0*(controlledTime()) + 50.0*mo.x;
		#endif
    #endif	

		// camera
        vec3  ro = cameraPath( time );
        vec3  ta = cameraPath( time+5.0 ); ta.y = ro.y - 5.5;
        float cr = 0.2*cos(0.1*time*0.5);

        // build ray
        vec3 ww = normalize( ta - ro);
        vec3 uu = normalize(cross( vec3(sin(cr),cos(cr),0.0), ww ));
        vec3 vv = normalize(cross(ww,uu));
        float r2 = p.x*p.x*0.32 + p.y*p.y;
        p *= (7.0-sqrt(37.5-11.5*r2))/(r2+1.0);
        vec3 rd = normalize( p.x*uu + p.y*vv + 2.5*ww );

        // dof
        #if VIS_SAMPLES>2
        vec3 fp = ro + rd * 17.0;
        ro += (uu*(-1.0+2.0*rr.y) + vv*(-1.0+2.0*rr.w))*0.035;
        rd = normalize( fp - ro );
        #endif


        vec3 col = render( ro, rd );

		tot += col;
	}
	tot /= float(VIS_SAMPLES);


	// gamma	
	tot = pow( clamp(tot,0.0,1.0), vec3(0.45) );

	// vignetting	
	tot *= 0.5 + 0.5*pow( 16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.1 );

	fragColor = vec4( tot, 1.0 );
}


void mainVR( out vec4 fragColor, in vec2 fragCoord, in vec3 fragRayOri, in vec3 fragRayDir )
{
    vec3 cam = cameraPath( controlledTime() );

    gAnimTime = controlledTime()*0.2;

    vec3 ro = fragRayOri + cam + vec3(0.0,-2.0,0.0);
    vec3 rd = fragRayDir*vec3(-1.0,1.0,-1.0);
    vec3 col = render( ro, rd );

    // gamma	
	col = pow( clamp(col,0.0,1.0), vec3(0.44) );

    fragColor = vec4( col, 1.0 );
}

#define ITERMAX 18

float solve(float x, float ex)
{
    float val = 0.0;
    for(int i = 0; i<ITERMAX;i++)
    {
        val = cos(val*val*1.5)+x*sin(val+ex) + val*0.4;
    }

    return clamp(abs(val),0.0,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float funcval = solve(uv.x+0.25,iTime);

    //float scope_intensity = 1.0 - clamp(abs(funcval-uv.y)*16.0,0.0,1.0f);
    float scope_intensity = clamp((funcval-uv.y)*16.0,0.0,1.0);;

	fragColor = vec4(vec3(1.0)*scope_intensity,1.0);
}

#define SIZE 10.

float C,S;
#define rot(a) mat2(C=cos(a),S=sin(a),-S,C)

vec3 ro,rd;
float c;

void rays(vec3 p, vec3 d) {
    vec3 n=cross(rd,d);
    vec3 np=cross(rd,n),nq=cross(d,n);
    float t=dot(ro-p,np)/dot(d,np),
        r=dot(p-ro,nq)/dot(rd,nq);
    float u=t+SIZE*.5;
    float i=floor(u/SIZE);
    u=mod(u,SIZE)-SIZE*.5;
    u=sign(u)*min(abs(u),SIZE*.25);
    u=i*SIZE+u;
    vec3 h=p+d*u;
    float l=length(cross(rd,ro-h));
    c+=step(0.,r)*mix(.01,.005,sin(iTime*10.))/l/l*pow(max(1.-r*.032,0.),.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    c=0.;
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;
    ro=vec3(-1.+sin(iTime*.3+2.)*.5,-1.+sin(iTime*.5+4.)*.5,iTime);
    rd=normalize(vec3(uv,1.));
    rd.xz*=rot(sin(iTime*.3));
    rd.xy*=rot(sin(iTime*.5));
    float o=floor(iTime/SIZE*2.)+6.;
    for(float x=-6.;x<6.;x+=1.)
    {
        for(float y=-6.;y<6.;y+=1.)
        {
            rays(vec3(.5,y,x+o)*SIZE*.5, vec3(1.,0.,0.));
            rays(vec3(y,.5,x+o)*SIZE*.5, vec3(0.,1.,0.));
            rays(vec3(x,y,.5)*SIZE*.5, vec3(0.,0.,1.));
        }
    }
    vec3 clr=mix(vec3(.1,.5,1.), vec3(.2,.6,1.),c)*c;
    clr=sqrt(clr);
	fragColor = vec4(clr,1.);
}

/// rand
float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	/// UV
	vec2 uv= 2.0 * fragCoord / iResolution.xy - vec2(1.0,1.0);
	uv.x *= iResolution.x / iResolution.y;

	/// result
	float r= 0.0;

	/// repat whole process few times
	for(float b=0.0;b<6.0;b++){
		const float w=8.0;
		for( float a=-w;a<=w;a++){
			const float div= 1.0 / 64.0;
			float x= floor( uv.x/div )*div + div/2.0 + a*div;

			float y= mod( iTime+4.0*rand(vec2(x,0.5+b/16.0)), 4.0 );

			/// radius of ball
			float rad= div*(w+0.5) * clamp( 0.5*y+0.5, 0.1, 1.0 );

			/// ball as light as close to center
			float power= clamp( (rad-length(uv-vec2(x,(0.5+0.5*rand(vec2(x,-0.1)))*y-1.0)))/rad, 0.0, 1.0);

			/// darker at bottom and top of scene
			float h= rand(vec2(x,-0.1*b))*y-1.0;
			power *= clamp( 1.0-abs(h), 0.0, 1.0 );

			/// apply this ball
			r += power;
		}
	}

	/// get rid of artifacts, look much better than clamp
	r= 1.0 - 1.0 / (1.0+r);

	/// return
	fragColor = vec4(r,r,r, 1.0);
}

// Complex Number math by julesb
// https://github.com/julesb/glsl-util

#define PI 3.14159265

#define cx_mul(a, b) vec2(a.x*b.x-a.y*b.y, a.x*b.y+a.y*b.x)
#define cx_div(a, b) vec2(((a.x*b.x+a.y*b.y)/(b.x*b.x+b.y*b.y)),((a.y*b.x-a.x*b.y)/(b.x*b.x+b.y*b.y)))
#define cx_modulus(a) length(a)
#define cx_conj(a) vec2(a.x,-a.y)
#define cx_arg(a) atan2(a.y,a.x)
#define cx_sin(a) vec2(sin(a.x) * cosh(a.y), cos(a.x) * sinh(a.y))
#define cx_cos(a) vec2(cos(a.x) * cosh(a.y), -sin(a.x) * sinh(a.y))

vec2 cx_sqrt(vec2 a) {
    float r = length(a);
    float rpart = sqrt(0.5*(r+a.x));
    float ipart = sqrt(0.5*(r-a.x));
    if (a.y < 0.0) ipart = -ipart;
    return vec2(rpart,ipart);
}

vec2 cx_tan(vec2 a) {return cx_div(cx_sin(a), cx_cos(a)); }

vec2 cx_log(vec2 a) {
    float rpart = length(a);
    float ipart = atan(a.y, a.x);
    if (ipart > PI) ipart=ipart-(2.0*PI);
    return vec2(log(rpart),ipart);
}

vec2 cx_mobius(vec2 a) {
    vec2 c1 = a - vec2(1.0,0.0);
    vec2 c2 = a + vec2(1.0,0.0);
    return cx_div(c1, c2);
}

vec2 cx_z_plus_one_over_z(vec2 a) {
    return a + cx_div(vec2(1.0,0.0), a);
}

vec2 cx_z_squared_plus_c(vec2 z, vec2 c) {
    return cx_mul(z, z) + c;
}

vec2 cx_sin_of_one_over_z(vec2 z) {
    return cx_sin(cx_div(vec2(1.0,0.0), z));
}


