// From Stackoveflow
// http://stackoverflow.com/questions/15095909/from-rgb-to-hsv-in-opengl-glsl
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

// My own additions to complex number math
#define cx_sub(a, b) vec2(a.x - b.x, a.y - b.y)
#define cx_add(a, b) vec2(a.x + b.x, a.y + b.y)
#define cx_abs(a) length(a)
vec2 cx_to_polar(vec2 a) {
    float phi = atan(a.y / a.x);
    float r = length(a);
    return vec2(r, phi); 
}

// Complex power
// Let z = r(cos Î¸ + i sin Î¸)
// Then z^n = r^n (cos nÎ¸ + i sin nÎ¸)
vec2 cx_pow(vec2 a, float n) {
    float angle = atan(a.y, a.x);
    float r = length(a);
    float real = pow(r, n) * cos(n*angle);
    float im = pow(r, n) * sin(n*angle);
    return vec2(real, im);
}

mat2 rotate(float a) {
	return mat2(cos(a), -sin(a), sin(a), cos(a));
}

// End utils, here comes the actual fractal

// z^6 + z^3 - 1 
vec2 f(vec2 z) {
    return cx_pow(z, 6.0) + cx_pow(z, 3.0) - 1.0;
} 

// f(z) derivated
// 6*z^5 + 3*z^2
vec2 fPrim(vec2 z) {
    vec2 a = 6.0 * cx_pow(z, 5.0);

	vec2 b = 3.0 * cx_pow(z, 2.0);
    return cx_add(a, b);
}

const int maxIterations = 90;
vec2 one = vec2(1, 0);
vec3 newtonRapson(vec2 z) {
  vec2 oldZ = z;
  float s = 0.0;
  for(int i = 0; i < maxIterations; i++){
    z = cx_sub(z, cx_div(f(z), fPrim(z))); 
    if(abs(oldZ.x - z.x) < 0.0001 && abs(oldZ.y - z.y) < 0.0001) {
      break;
    }

    vec2 w = cx_div(one, cx_sub(oldZ, z));
    float wAbs = cx_abs(w);

    s += exp(-wAbs);
    oldZ = z;
  }
  return vec3(s, cx_to_polar(z));
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = (sin(iTime/3.0)*0.5+0.5)*3.0 + 1.0;
    vec2 centered = (fragCoord - iResolution.xy * 0.5) / iResolution.xy * zoom;
	vec2 rotated = centered * rotate(iTime/2.0);
    vec3 result = newtonRapson(rotated);
    float c = 1.0-result.x/float(maxIterations)*7.0;    
    vec3 color = hsv2rgb(vec3(result.z*3.0 + iTime/15.0, 1.0, c));    
    fragColor = vec4(color, 1.0);
}

// Based on LJ magics

vec3 ro,rd,mp;
float md;

mat2 r2d(float a){float c=cos(a),s=sin(a);return mat2(c,s,-s,c);}
float soc(vec3 p){vec3 n=normalize(sign(p));return min(min(dot(p.xy,n.xy),dot(p.yz,n.yz)),dot(p.xz,n.xz));}

float map(vec3 p){
    p.xy*=r2d(iTime*.7);
    p.yz*=r2d(iTime*.5);
	p.xz*=r2d(iTime*.3);
    return soc(max(abs(p)-.2,1e-6))-.1*sqrt(2.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
