// Modified spikeball demo from Patapom, 
//   see  https://www.shadertoy.com/view/lds3WH
// which code was stolen from pouet.net and adapted
//-------------------------------------------------------

#define t iTime*0.1

float spikeball(vec3 p, vec3 v)
{
  vec3 c[19];
  c[0] = vec3(1,0,0);
  c[1] = vec3(0,1,0);
  c[2] = vec3(0,0,1);
  c[3] = vec3(.577,.577,.577);
  c[4] = vec3(-.577,.577,.577);
  c[5] = vec3(.577,-.577,.577);
  c[6] = vec3(.577,.577,-.577);
  c[7] = vec3(0,.357,.934);
  c[8] = vec3(0,-.357,.934);
  c[9] = vec3(.934,0,.357);
  c[10] = vec3(-.934,0,.357);
  c[11] = vec3(.357,.934,0);
  c[12] = vec3(-.357,.934,0);
  c[13] = vec3(0,.851,.526);
  c[14] = vec3(0,-.851,.526);
  c[15] = vec3(.526,0,.851);
  c[16] = vec3(-.526,0,.851);
  c[17] = vec3(.851,.526,0);
  c[18] = vec3(-.851,.526,0);

  float MinDistance = 9999.;
  for ( int i=3; i < 19; i++ )
  {
    float d = clamp( dot( p, c[i] ), -1.0, 1.0 );
    vec3 proj = d * c[i];
    d = abs( d );

    float Distance2Spike = length( p - proj );
    float sd = 1.4+ 0.6*sin(t*8.);
    float SpikeThickness = 01. * exp( -sd*d ) + 0.0;
    float Distance = Distance2Spike - SpikeThickness;
    MinDistance = min( MinDistance, Distance );
  }
  return MinDistance;	
}

