#define R(p, a) p=cos(a)*p+sin(a)*vec2(p.y,-p.x)

float Distance( vec3 p, vec3 v )
{
	vec3 q = p;
	R( p.yz, t );
	R( p.xz, 2.0*t + p.x*sin(t)*0.2 );
	float d = spikeball(p, v);
//return d;   // black background
	float nd = dot( q + vec3(0.0, 3.0, 0.0), vec3(0.0, 1.0, 0.0));
	return min( nd, d );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord.xy / iResolution.xy;
  vec2 aspect = vec2( iResolution.x / iResolution.y, 1 );
  vec3 p = vec3( 0, 0, 12 );
  vec3 v = vec3( (2.0 * uv - 1.0) * aspect, 0 ) ;
  v = 0.8*normalize(v - p*0.7);

  // Raymarching loop
  float r, l, a, s, ml=0.0001;
  for ( int i=0; i < 64; i++ )
  {
    l = Distance(p, v);
    p += l*v;
    l = abs(l);
    r += l;
    if (l < ml*r) break;
  }    

  // Compute normal
  vec2 epsilon = vec2( 0.001,0.0 );
  float pd = Distance(p, v);
  vec3 n = vec3( Distance(p+epsilon.xyy, v) - pd,
                 Distance(p+epsilon.yxy, v) - pd,
                 Distance(p+epsilon.yyx, v) - pd);
  float c = 1.0 + dot(normalize(n),v);
  fragColor = vec4(c,c,c, 1.0); 
}

