// From Stackoveflow
// http://stackoverflow.com/questions/15095909/from-rgb-to-hsv-in-opengl-glsl
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


// Actual fractal begins here

// z^3 - 1  
vec2 f(vec2 z) {
  vec2 z3 = cx_mul(z, cx_mul(z, z));
  return vec2(z3.x - 1.0, z3.y);
} 

// f(z) derivated
// 3z^2  
vec2 fPrim(vec2 z) {
  vec2 z2 = cx_mul(z, z);
  return vec2(3.0*z2.x, 3.0*z2.y);
}


int maxIterations = 50;
vec2 one = vec2(1, 0);
float newtonRapson(vec2 z) {
  vec2 oldZ = z;
  float s = 0.0;
  for(int i = 0; i < maxIterations; i++){
    z = cx_sub(z, cx_div(f(z), fPrim(z))); 
    if(abs(oldZ.x - z.x) < 0.001 && abs(oldZ.y - z.y) < 0.001) {
      return s;
    }

    vec2 w = cx_div(one, cx_sub(oldZ, z));
    float wAbs = cx_abs(w);

    s += exp(-wAbs);
    oldZ = z;
  }
  return float(maxIterations);
}


