	float sn = sin(iTime);
    vec2 rotated = vec2(scaled.x * cs - scaled.y * sn, scaled.x * sn + scaled.y * cs);
    float hue = newtonRapson(rotated) / float(maxIterations)*9.0;
    vec3 c = hsv2rgb(vec3(hue, 1.0, 1.0));
    fragColor = vec4(c, 1.0);
}

