const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST         = 0.0;
const float MAX_DIST         = 2000.0;
const float EPSILON          = 0.0001;

/**
 * Signed distance function for a sphere centered at the origin with radius 1.0;
 */
float boxSDF( vec3 p, vec3 b )
{
    return length(max(abs(p)-b,0.0));
}


// Repeat in three dimensions
vec3 pMod3(inout vec3 p, vec3 size) {
	vec3 c = floor((p + size*0.5)/size);
	p = mod(p + size*0.5, size) - size*0.5;
	return c;
}

/**
 * Signed distance function describing the scene.
 * 
 * Absolute value of the return value indicates the distance to the surface.
 * Sign indicates whether the point is inside or outside the surface,
 * negative indicating inside.
 */
float sceneSDF(vec3 p)
{
    pMod3(p, vec3(5,5,5));
    return boxSDF(p, vec3(0.5));
}

vec3 estimateNormal(vec3 p)
{
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

/**
 * Lighting contribution of a single point light source via Phong illumination.
 * 
 * The vec3 returned is the RGB color of the light's contribution.
 *
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 * lightPos: the position of the light
 * lightIntensity: color/intensity of the light
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phoneContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                            vec3 lightPos, vec3 lightIntensity)
{ 
    vec3 N = estimateNormal(p); // Estimate normal of surface
    vec3 L = normalize(lightPos - p); // Point from point on surface to light
    float light_dist = distance(eye, p);
    vec3 V = normalize(eye - p); // Viewing vector, used to diffuse reflected light
    vec3 R = normalize(reflect(-L, N)); // Reflect light to the normal

    float dotLN = dot(L,N); // cosine angle between light direction and normal direction
    float dotRV = dot(R,V); // cosine angle between reflection direction and viewing direction

    // Light is coming from behind the normal of the face, pitch black
    if (dotLN < 0.)
    {
        return vec3(0.0);
    }
    // Reflected light points away from the camera, so there are no direct light. Only ambient light and diffuse color
    if (dotRV < 0.)
    {
        // This value maxes when dotLN = 1, which is when L(light) and N(normal) are equal. 100% of the light is reflected back
        return min(1., abs(30./light_dist)) * lightIntensity * (k_d * dotLN);
    }
    return min(1., abs(30./light_dist)) * lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
    }

/**
 * Lighting via Phong illumination.
 * 
 * The vec3 returned is the RGB color of that point after lighting is applied.
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 globalAmbentLight = 0.9 * vec3(1.);
    vec3 color = globalAmbentLight * k_a; // Multiply brightness by color to get ambient color

    // Light 1
    vec3 light1Pos = vec3(0.,1.,3.);
    vec3 light1Insentity = vec3(0, 67.1/100., 43.5/100.);
    color += phoneContribForLight(k_d, k_s, alpha, p, eye, light1Pos, light1Insentity);


    // Light 2
    vec3 light2Pos = vec3(-1.,-1.,1.);
    vec3 light2Insentity = vec3(50./100., 59.2/100., 0);
    color += phoneContribForLight(k_d, k_s, alpha, p, eye, light2Pos, light2Insentity);

    // Light 3
    vec3 light3Pos = vec3(-0.5,2,-3.);
    vec3 light3Insentity = vec3(4.7/100., 35.3/100., 65.1/100.);
    color += phoneContribForLight(k_d, k_s, alpha, p, eye, light3Pos, light3Insentity);

    return color;
}

/**
 * Return the shortest distance from the eyepoint to the scene surface along
 * the marching direction. If no part of the surface is found between start and end,
 * return end.
 * 
 * eye: the eye point, acting as the origin of the ray
 * marchingDirection: the normalized direction to march in
 * start: the starting distance away from the eye
 * end: the max distance away from the ey to march before giving up
 */
float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end)
{
    // Start depth
    float depth = start;
    // Keep looking for where the marching ray hits a surface
    for (int i = 0; i < MAX_MARCHING_STEPS; ++i)
    {
        // Get the distance from marching ray point to surface of box
        float dist = sceneSDF(eye + marchingDirection * depth);
        // If we've hit near the surface, return this distance
        if (dist < EPSILON)
        {
            return depth;
        }
        // Didn't find anything, let's go to where we found something
        depth += dist;
        // We're at the end, stop
        if (depth > end)
        {
            return end;
        }
    }
    // Ran out of steps before we hit the end, just return end
    return end;
}

/**
    fov:         Field of View of camera
    screen_size: Screen size
    fragCoord:   Screen coord of pixel
    return:      Direction of rendering ray of the projection camera
 */
vec3 rayDirection(float fov, vec2 screen_size, vec2 fragCoord)
{
	vec2 xy = fragCoord - screen_size / 2.0;
    float z = (screen_size.y / 2.) / tan(radians(fov) / 2.);
    return normalize(vec3(xy,-z));
}

/**
 * Return a transform matrix that will transform a ray from view space
 * to world coordinates, given the eye point, the camera target, and an up vector.
 *
 * This assumes that the center of the camera is aligned with the negative z axis in
 * view space when calculating the ray marching direction. See rayDirection.
 */
mat3 lookAtMatrix(vec3 eye, vec3 center, vec3 up) 
{
    // Based on gluLookAt man page
    // Forward/Look at vector
    vec3 f = normalize(center - eye);
    // Right vector
    vec3 v = normalize(cross(f, up));
    // Camera local up Vector
    vec3 u = cross(v, f);
    return mat3(
        vec3(v),
        vec3(u),
        vec3(-f)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 camera_space_dir = rayDirection(60., iResolution.xy, gl_FragCoord.xy);
    vec3 eye = 
        vec3(
            cos(iTime*0.25) * 100.,
            cos(iTime) * 30.,
            (sin(iTime)+5.) * 1.5
            );

   vec3 world_space_dir = lookAtMatrix(eye, vec3(0.0, 0.0, 0.0), vec3(0,1,0)) * camera_space_dir;
    // Find shortest distance surface
    float dist = shortestDistanceToSurface(
        eye,
        world_space_dir, 
        MIN_DIST, 
        MAX_DIST);
    if (dist > MAX_DIST - EPSILON)
    {
        fragColor = vec4(0.2 * vec3(1.), 1);
        return;
    }

    // We've hit a surface
    // Phong shading time!!
    // Surface point
    vec3 p = eye + dist * world_space_dir;

    vec3 K_ambientColor = vec3(0.2, 0.2, 0.2);
    vec3 K_diffuseColor = vec3(0.7, 0.2, 0.2);
    vec3 K_specularColor = vec3(1.0, 1.0, 1.0);
    float shineness = 20.0;

    vec3 color = phongIllumination(K_ambientColor, K_diffuseColor, K_specularColor, shineness, p, eye);
    fragColor = vec4(color,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv * 2.0 - 1.0;
    float t = iTime;
    float tt = texture(iChannel0, uv).x;

    tt = pow(tt, 0.4 * nuv.x);

	fragColor.x = sin(10.0 * (uv.x * sin(t * 0.3) + uv.y * cos(t * 0.01)) + t);
    fragColor.y = sin(nuv.x * 0.3 * sin(t * 1.3) + nuv.y * 0.4 * sin(t * 0.2));

    float cx = nuv.x * 0.3 * sin(t * 0.2);
    float cy = nuv.y * 0.5 * cos(t * 0.4) * tt * 0.1;
    fragColor.z = sin(sqrt(520.0 * (cx * cx + cy * cy) + 1.0) + t * 3.0);

    fragColor.x = fragColor.x + fragColor.z;
    fragColor.y = fragColor.x * fragColor.x * 0.3 - fragColor.z * 0.2;
    fragColor.z = fragColor.x * 0.2;

    fragColor.x -= length(nuv) * 4.0;
    fragColor.y += cy * nuv.x;
    fragColor.z *= cx;

    fragColor.xyz += vec3(0.1, 0.1, 0.4);
    fragColor.xyz += length(fragColor.xyz) * 0.1 * tt;
    fragColor.x += fragColor.x * tt * 2.0;
}

float PI = 3.1415926535;

float sdsphere(vec3 p, float r)
{
    return length(p) - r;
}

float sdtorus(vec3 p, vec2 t)
{
  vec2 q = vec2(length(p.xz) - t.x, p.y);
  return length(q) - t.y;
}

float displacement(vec3 p, float c)
{
	return sin(c * p.x) * sin(c * p.y) * sin(c * p.z);
}

float sdspheredisplace(vec3 p, float r, float c)
{
    float d1 = sdsphere(p, r);
    float d2 = displacement(p, c);
    return d1 + d2;
}

float map(vec3 p)
{
    float a = sdtorus(p + vec3(-0.25, 0.0, 0.0), vec2(0.1, 0.01));
    float b = sdtorus(p + vec3(+0.25, 0.0, 0.0), vec2(0.1, 0.01));

	return min(a, b);
}

float march(vec3 ro, vec3 rd)
{
	float t = 0.0;
    for(int i = 0; i < 64; ++i)
    {
        vec3 p = ro + rd * t;
        float d = map(p);
        t += d * 0.5;
    }
    return t;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv * 2.0 - 1.0;
    nuv.x *= iResolution.x / iResolution.y;

    vec2 rot = vec2(iTime * 1.0, iTime * 1.0);
    mat2 rotm = mat2(cos(rot.y), -sin(rot.x), sin(rot.x), cos(rot.y));

    float tt0 = texture(iChannel0, vec2(0.0, 0.0)).x;
    float tts = 
        texture(iChannel0, vec2(0.1, 0.0)).x +
        texture(iChannel0, vec2(0.2, 0.0)).x +
        texture(iChannel0, vec2(0.3, 0.0)).x +
        texture(iChannel0, vec2(0.4, 0.0)).x +
        texture(iChannel0, vec2(0.5, 0.0)).x +
        texture(iChannel0, vec2(0.6, 0.0)).x +
        texture(iChannel0, vec2(0.7, 0.0)).x +
        texture(iChannel0, vec2(0.8, 0.0)).x +
        texture(iChannel0, vec2(0.9, 0.0)).x;

    tts /= 9.0;


    vec3 ro = vec3(cos(iTime) * 0.0, sin(iTime) * 0.0, -0.5 + sin(iTime) * 0.4 * tt0);
    vec3 rd = normalize(vec3(nuv, 1.0));

    rd.xy = rd.xy * rotm;

    float d = march(ro, rd);
    float r = step(d, 0.99);

    vec4 bbg = vec4(0.1, 0.4, 0.5, 1.0);
    vec4 bg = vec4(0.0, 0.2, 0.7, 1.0);
    vec4 fg = vec4(0.2, 1.5, 0.0, 1.0);

    fragColor = bbg + bg * pow(tts * .25, 0.4) + fg * r * d;

    float c0 = sin(d);
    float c1 = sin(d);
    float c2 = abs(sin(uv.x - 0.5)) + abs(sin(uv.y - 0.5));

    fragColor.xy += tts * tt0 * (2.5 + tts * 0.3) * c0 * fragColor.y;
    fragColor.z *= c2 * fragColor.y * 3.0 * (1.0 + tts);

    float t = iTime;
}

const float EPSILON = 0.0001;
const float MAX_DIST = 100.0;

float remap(float low1, float high1, float low2, float high2, float value){
    return low2 + (value - low1) * (high2 - low2) / (high1 - low1);
}

/**
 * Rotation matrix around the Y axis.
 */
mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

/**
 * Constructive solid geometry intersection operation on SDF-calculated distances.
 */
float intersectSDF(float distA, float distB) {
    return max(distA, distB);
}

/**
 * Constructive solid geometry union operation on SDF-calculated distances.
 */
float unionSDF(float distA, float distB) {
    return min(distA, distB);
}

/**
 * Constructive solid geometry difference operation on SDF-calculated distances.
 */
float differenceSDF(float distA, float distB) {
    return max(distA, -distB);
}

float SphereSDF(vec3 p, float size){
	return length(p) - size;
}

float CubeSDF(vec3 p, vec3 size){
    vec3 d = abs(p) - size;

    float insideDistance = min( max(max(d.x, d.y),d.z), 0.0);

    float outsideDistance = length( max(d, 0.0) );

    return insideDistance + outsideDistance;
}

float TorusSDF( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float SceneSDF(vec3 p){

    p = rotateY(iTime * 0.5) * p;

    float final = CubeSDF(p, vec3(0.01));;

    float count = 5.0;
    float gap = 7.0;
    float width = gap * (count-1.0);

    for(float i = 0.0; i < count; i += 1.0){
        for(float j = 0.0; j < count; j += 1.0){
            float x = gap * i - width * 0.5;
            float z = gap * j - width * 0.5;
            float angle = 5.0;
            float y = sin(i * angle + iTime) + cos(j * angle + iTime);
            y *= 0.75;
            vec3 offset = vec3(x, y, z);
            float torus = TorusSDF(p + offset, vec2(1,0.45));
            final = unionSDF(final, torus);
        }
    }



    //final = torus;

    return final;
}

vec3 EstimateNormal(vec3 p){
	return normalize(vec3(
        SceneSDF(vec3(p.x+EPSILON, p.y, p.z)) - SceneSDF(vec3(p.x-EPSILON, p.y, p.z)),
        SceneSDF(vec3(p.x, p.y+EPSILON, p.z)) - SceneSDF(vec3(p.x, p.y-EPSILON, p.z)),
        SceneSDF(vec3(p.x, p.y, p.z+EPSILON)) - SceneSDF(vec3(p.x, p.y, p.z-EPSILON))
        ));
}

/**
 * Using the gradient of the SDF, estimate the normal on the surface at point p.
 */
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        SceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - SceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        SceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - SceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        SceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - SceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

vec3 RayDirection(float fieldOfView, vec2 fragCoord, vec2 size){
	vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

float ShortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end){
	float depth = start;
    for(int i = 0; i < 255; i++){
    	float dist = SceneSDF(eye + depth * marchingDirection);
        if( dist <= EPSILON){
            return depth;
        }
        depth += dist;
        if(depth >= end){
        	return end;
        }
    }
    return end;
}

/**
 * Return a transform matrix that will transform a ray from view space
 * to world coordinates, given the eye point, the camera target, and an up vector.
 *
 * This assumes that the center of the camera is aligned with the negative z axis in
 * view space when calculating the ray marching direction. See rayDirection.
 */
mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}

