                          0.0 + 0.0 * sin(iTime/2.0),
                          -3.0 + 0.0 * sin(iTime));

    mat4 viewToWorld = viewMatrix(eye, lookAtPos, vec3(0,1,0) );
    vec3 worldDir = ( viewToWorld * vec4(rayDir,0) ).xyz;

    float dist = ShortestDistanceToSurface(eye, worldDir, 0.0, 100.0);

    vec3 ambientColor = vec3(0.5, 0.3, 0.3);
    vec3 color = vec3(1,0,0);

    //vec3 lightPos = vec3(0.5, 0.5, 0.5);
    vec3 lightPos = vec3(4.0 * sin(iTime),
                         6.0,
