const int MAX = 8;
const float PI = 3.1415927;
const vec3 BGCOLOR = vec3(0.0);
const float INF =  9999999.0;
vec3 LIGHT_DIR = vec3(0.0, 1.0, 0.5);


struct Sphere {
    vec3 p;
    float r;
};

struct Ray {
	vec3 o;
	vec3 d;
};


Sphere sphereContainer[MAX];
int sphereCnt = 0;


mat3 rotationMatrix(vec3 axis, float angle) {
    axis = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float oc = 1.0 - c;

    return mat3(oc * axis.x * axis.x + c,           oc * axis.x * axis.y - axis.z * s,  oc * axis.z * axis.x + axis.y * s,
                oc * axis.x * axis.y + axis.z * s,  oc * axis.y * axis.y + c,           oc * axis.y * axis.z - axis.x * s,
                oc * axis.z * axis.x - axis.y * s,  oc * axis.y * axis.z + axis.x * s,  oc * axis.z * axis.z + c);
}

// Src - http://blog.hvidtfeldts.net/index.php/2011/06/distance-estimated-3d-fractals-part-i/
float MandelBulbDE(vec3 pos) {
    pos = mod(pos, 4.0) - 2.0;
	vec3 z = pos;
	float dr = 1.0;
	float r = 0.0;
    int Iterations = 4;
    float Bailout = 3.0;
    float Power = 8.0;
	for (int i = 0; i < Iterations ; i++) {
		r = length(z);
		if (r>Bailout) break;

		// convert to polar coordinates
		float theta = acos(z.z/r);
		float phi = atan(z.y,z.x);
		dr =  pow(r, Power-1.0)*Power*dr + 1.0;

		// scale and rotate the point
		float zr = pow( r,Power);
		theta = theta*Power;
		phi = phi*Power;

		// convert back to cartesian coordinates
		z = zr*vec3(sin(theta)*cos(phi), sin(phi)*sin(theta), cos(theta));
		z+=pos*(cos(iTime) / 1.5 + 1.2);
	}
	return 0.5*log(r)*r/dr;
}


float traceDE(Ray ray) {
	float totalDistance = 0.0;
	int steps;
    int MaxRaySteps = 50;
    float MinimumDistance = 0.0005;
	for (steps=0; steps < MaxRaySteps;++steps) {
		vec3 p = ray.o + totalDistance * ray.d;
		float distance = MandelBulbDE(p);
		totalDistance += distance;
		if (distance < MinimumDistance) break;
	}
	return 1.0 - float(steps) / float(MaxRaySteps);
}


float iSphere(Ray ray, Sphere sphere, out vec3 color) {
    //ray.o = mod(ray.o, 5.0);
    //ray.d = mod(-ray.d, 5.0);


    float dx = ray.o.x - sphere.p.x;
    float dy = ray.o.y - sphere.p.y;
    float dz = ray.o.z - sphere.p.z;

    float a = ray.d.x * ray.d.x + ray.d.y * ray.d.y + ray.d.z * ray.d.z;
    float b = 2.0 * (ray.d.x * dx + ray.d.y * dy + ray.d.z * dz);
    float c = dx * dx + dy * dy + dz * dz - sphere.r * sphere.r;
    float d = b * b - 4.0 * a * c;

    if (d > 0.0) {
        float t0 = (-b + sqrt(d)) / 2.0;
        float t1 = (-b - sqrt(d)) / 2.0;
        float t = max(t0, t1);
        vec3 ip = ray.o + ray.d * t;
        vec3 normal = normalize(ip - sphere.p);

        vec3 sphereColor = vec3(1.0, 1.0, 0.0);
        color = clamp(vec3(sphereColor) * dot(normal, LIGHT_DIR), vec3(0.0), vec3(1.0));
        color += sphereColor * vec3(0.1);
        return t;
    }

    return INF;
}


void setupScene() {
    Sphere sphere0;
    sphere0.p = vec3(0.0, 0.0, 0.0);
    sphere0.r = 1.0;

	sphereContainer[0] = sphere0;
    sphereCnt = 1;
    LIGHT_DIR.x = sin(iTime);
    LIGHT_DIR.y = cos(iTime);
}

Ray getRay(vec2 uv) {
	Ray ray;
    float t = iTime / 5.0;
    mat3 rot = rotationMatrix(vec3(1.0, 1.0, 1.0), t / 10.0);

    vec3 ro = vec3(sin(t), cos(t / 32.0) * 15.0, sin(t) / 45.0) * 10.0;

	ray.o = ro;
	ray.d = normalize(vec3(1.0, uv));

    float angle = t / 5.0;
    float s = sin(angle), c = cos(angle);
    mat2 rot2 = mat2(c, -s, s, c);
    ray.d.xy *= rot2;
    ray.d.yz *= rot2;

	return ray;
}

vec3 trace(Ray ray) {
    vec3 closestColor = BGCOLOR;
    float closestDist = INF;

    for(int i = 0;i < sphereCnt;++i) {
        vec3 currentColor;
        float currentDist = iSphere(ray, sphereContainer[i], currentColor);
        if (closestDist > currentDist) {
            closestDist = currentDist;
            closestColor = currentColor;
        }
    }
    return closestColor;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float ar = iResolution.x / iResolution.y;
	vec2 uv = fragCoord.xy / iResolution.y - vec2(ar * 0.5, 0.5);


    setupScene();
    vec3 colorSum = vec3(0.0);
    float kernelSize = 2.0;
    for(float x = 0.0;x < kernelSize;++x) {
        for(float y = 0.0;y < kernelSize;++y) {
            vec2 ruv = uv + vec2(x / kernelSize, y / kernelSize) / iResolution.xy;
    		Ray ray = getRay(ruv);
    		colorSum += trace(ray);
        }
    }
    colorSum /= kernelSize * kernelSize;


    Ray ray = getRay(uv);
    vec3 mandelbulbColor = vec3(1.0, 0.0, 0.5) * traceDE(ray) * 1.1 + 0.1;

	fragColor = vec4(mandelbulbColor, 1.0);
}

mat2 rz2(float a){float c=cos(a),s=sin(a);return mat2(c,s,-s,c);}

float random(vec2 st) { 
    return fract(sin(dot(st.xy, vec2(12.9898,78.233)))* 43758.5453123);
}

float map(vec3 p){
    p.xy*=rz2(iTime*.3);
    p.yz*=rz2(iTime*.5);
    p.xy*=rz2(iTime*.7);
    vec3 q=mod(p+2.5,5.)-2.5;
    vec3 f=floor((p-2.5)/5.);
    float fft=texture(iChannel0,vec2(0.05,0.25)).x;
    float r=random(vec2(random(f.xy),f.z));
    vec3 s=vec3(.2+r*.5+fft*.5);
    r*=6.2831853;
    vec3 c;
    c.x=sin(r+iTime*1.3);
    c.y=sin(r+iTime*1.4);
    c.z=sin(r+iTime*1.5);
    c*=.8;
    return min(length(max(abs(q-c)-s,0.))-.1,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 v=uv*(1.-uv);
    uv-=0.5;
    uv.x*=iResolution.x/iResolution.y;
    vec3 ro=vec3(uv,-2.5),rd=vec3(uv,1.),mp=ro;
    int i;
    for(i=0;i<50;++i){
        float md=map(mp);
        if(md<.001)break;
        mp+=rd*md;
    }
    float m=1.-float(i)/50.;
    vec3 c=vec3(.9,.1,.5)*m;
    c *= pow(v.x*v.y * 25., .25);
	fragColor = vec4(c,1.);
}

