mat2 rot2(float a) {
	float s = sin(a*6.28);
	float c = cos(a*6.28);
	return  mat2(c, -s, s, c);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float GF(vec2 p, vec2 c, float r, float m)
{
    float d = distance(p,c)-r;
    float g = .0;
    if (d>0.0)
    {
        g = m/d;
    }
    return g;
}

vec2 rotPos(int j)
{
    float cr = 0.0;
    if (PSys[j].base>-1)
    {
        cr = PSys[PSys[j].base].R+PSys[j].H;
    }
    float t = (.1*iTime*PSys[j].P + PSys[j].PH)*6.28;
    return vec2(cr*cos(t), cr*sin(t));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float mx = max(iResolution.x, iResolution.y);
	vec2 uv = (fragCoord.xy - iResolution.xy*.5)/mx;
    float gacc = 1.;
    vec3 plc = vec3(.0, .0, .0);
    for (int i = 0; i < N; i++)
    {
        vec2 c = vec2(.0, .0);
        int parentID = i;
            while (parentID > -1)
            {        
                c += rotPos(parentID);
                parentID = PSys[parentID].base;
            }
        float gf = GF(uv, c, PSys[i].R, PSys[i].M);
        plc = mix(hsv2rgb(vec3(PSys[i].C, 1., 1.)),
                  plc,
                  smoothstep(-.001, .0, distance(uv, c)-PSys[i].R));
        gacc *= gf;
    }
    float gnorm = 5.*log2(gacc);
	vec3 c = mix(plc ,vec3(1.0),  smoothstep (.8, 1.2, cos(gnorm)));
    if (iMouse.z>.0)
    {
        vec3 fv = reflect(normalize(vec3(uv, -1.)), normalize(vec3(uv, sqrt(gacc))));
        c = plc+smoothstep(.0, 1.0, fv);
    }

	fragColor = vec4(c, 1.0);
}

#define CS(a) vec2(cos(a), sin(a))
#define PI 3.14159265

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.x;

    vec2 p1 = vec2(p); 
    p1 += CS(iTime) * 0.2;
	float r1 = length(p1) * 8.0; 

    vec2 p2 = vec2(p);
    p2 += CS(iTime + PI) * 0.2;
    float r2 = length(p2) * 8.0;

    float green = 1.0 - r1;
    float red = 1.0 - r2;
	fragColor = vec4(red, green, 0.0, 1.0);
}

