#define TWOPI 			(2.0*3.1415926535)
#define ROTATION(alpha) mat2(cos(alpha),-sin(alpha),sin(alpha),cos(alpha))
#define COLORIZATION(h)	clamp(abs(fract(h+vec4(3.0,2.0,1.0,0.0)/3.0)*6.0-3.0)-1.0,0.0,1.0)

//--------------------------------------------------------------------------
// Ray is defined by M(t) = P + t.d
vec4 compute(vec3 P,vec3 d)
{
	// compute the intersections with the sphere
    // a.t^2 + b.t + c = 0
    float a = dot(d,d);
    float b = 2.0*dot(P,d);
    float c = dot(P,P)-1.0; // sphere of radius 1
    float delta = b*b-4.0*a*c;

    if(delta >= 0.0)
    {
        float t[2];
        t[0] = (-b-sqrt(delta))/(2.0*a);
        t[1] = (-b+sqrt(delta))/(2.0*a);

        vec4 color = vec4(0.0,0.0,0.0,1.0);
        for(int i=0;i<2;i++)
        {
            vec3 M 		= P+t[i]*d;
            float beta 	= acos(M.z);
            float alpha	= atan(M.y,M.x);

            int bool_alpha 	= cos(alpha*24.0) > 0.80 ? 1 : 0;
            int bool_beta	= cos( beta*24.0) > 0.80 ? 1 : 0;
            if(bool_alpha == 1 && bool_beta == 0)
            {
                color  = vec4(1.0,0.0,0.0,1.0);
                color *= i==1 ? 0.2 : 1.0;
                break;
            }
            else if(bool_alpha == 0 && bool_beta == 1)
            {
                color  = vec4(0.0,1.0,0.0,1.0);
                color *= i==1 ? 0.3 : 1.0;
                break;
            }

        } // for()

        return color;
    }
    else
    {
	    return vec4(0.0,0.0,0.0,1.0);
    }
} // compute()

