void mainImage(out vec4 fragColor,vec2 fragCoord)
{
    float m 		= min(iResolution.x,iResolution.y);
	vec2 uv 		= (fragCoord.xy / m - vec2(0.5,0.5))*2.5;
    vec3 P 			= vec3(uv,-5.0);
    vec3 d      	= vec3(0.0,0.0,1.0);

    float alpha1	= iTime*TWOPI/11.0;
    float alpha2	= iTime*TWOPI/5.0;
    P.zx		   *= ROTATION(alpha1);
    P.xy		   *= ROTATION(alpha2);
    d.zx 		   *= ROTATION(alpha1);
    d.xy		   *= ROTATION(alpha2);

	fragColor 		= compute(P,d);

} // mainImage()

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 color = vec3(0.0);

    vec2 pos = vec2(0.5)-uv;

    float r = length(pos)*2.0;
    float a = atan(pos.y,pos.x);

    float f = smoothstep(-3.0, 3.0, cos(a * cos(iTime) + a * sin(iTime/20.0)*10.0));

    color = vec3( 1.-smoothstep(f,f+0.2,r) );

    vec2 gradient = uv * ((sin(iTime)+2.0)/2.0);

	fragColor = vec4(color * vec3(gradient, 0.5), 1.0);
}

#define PI 3.14159265
mat2 rz2(float a){float c=cos(a),s=sin(a);return mat2(c,s,-s,c);}

float random(vec2 p){return fract(sin(dot(p,vec2(12.,78.)))*4375.);}
float sdBox(vec3 p, vec3 b){
    vec3 d=abs(p)-b;
    return min(max(max(d.x,d.y),d.z),0.)+length(max(d,0.));
}

float ribbon(vec3 p){
    p.x+=sin(iTime+p.z)*.1;
    p.y+=.4+sin(iTime*2.+p.z*3.)*0.1-p.z*.1;
    p.xy*=rz2(sin(iTime+p.z)*.3);
    return sdBox(p,vec3(.2,.001,2.));
}

float picket(vec3 p){
    p.z-=2.;
    p.y+=1.;
    p.yz*=rz2(-1.);
    return max(length(p.xy)-.1,abs(p.z)-1.);
}

float terrain(vec3 p){
    p.x+=14.;
    vec2 q=p.xz*vec2(.05,.03);
    vec2 f=floor(q);
    float r=sin(q.x*PI)*sin(q.y*PI)*random(f)*3.;
    vec2 s=p.xz*vec2(.07,.03)*10.;
    s.y+=iTime*3.;
    s.x+=sin(s.y);
    float fft=0.;
    for(int i=0;i<20;++i)
    	fft+=pow(texture(iChannel0,vec2(float(i)*.005,.25)).r,10.);
    float r2=sin(s.x)*sin(s.y)*fft*.3;
    return p.y+1.5-r-r2;
}

float map(vec3 p){
    float d=ribbon(p);
    d=min(d,picket(p));
    d=min(d,terrain(p));
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy-.5*iResolution.xy)/iResolution.y;
	float a=iTime*.5;
    vec3 ro=vec3(uv+vec2(5.,3.)+vec2(cos(a),sin(a))*.5,-10.),rd=vec3(uv,1.),mp=ro;
    for (int i=0;i<50;++i){
        float md=map(mp);
        if(md<.001)break;
        mp+=md*rd;
    }
	fragColor = vec4(length(mp-ro)*.01);
}

vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

// smin courtecy of iq http://iquilezles.org/www/articles/smin/smin.htm
float smin( float a, float b, float k )
{
    a = pow( a, k ); b = pow( b, k );
    return pow( (a*b)/(a+b), 1.0/k );
}

vec4 cells(in vec2 uv, float speed) {

    vec2 i_uv = floor(uv);
    vec2 f_uv = fract(uv);

    float m_dist = 2.;

    for( int y = -1; y<= 1; y++ ) {
        for( int x = -1; x <= 1; x++ ) {
            vec2 neighbour = vec2(float(x), float(y));
            vec2 n = i_uv + neighbour;
            vec2 point = random2(i_uv + neighbour);
            point = 0.5 + 0.5*sin(iTime * speed + 6.2831*point);
            vec2 diff = neighbour + point - f_uv;
            m_dist = smin(m_dist, length(diff) * m_dist, 16.0);
            //m_dist = min(m_dist, length(diff) * m_dist);

        }
    }

    return vec4(m_dist);
}

