void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Get min/max of the resolution (in most case the width).
    float m 	= min(iResolution.x,iResolution.y);
    float M 	= max(iResolution.x,iResolution.y);
    vec2 uv 	= ((fragCoord.xy-0.5*iResolution.xy) / m)*2.5;
    fragColor 	= compute(uv);

    // draw axis and unit circle in white on top of everything
    float width = 0.01;
    if(abs(sqrt(uv.x*uv.x+uv.y*uv.y)-1.0)<width)
        fragColor = vec4(1.0,1.0,1.0,1.0);
    if(abs(uv.x)<width)
        fragColor = vec4(1.0,1.0,1.0,1.0);
    if(abs(uv.y)<width)
        fragColor = vec4(1.0,1.0,1.0,1.0);

} // mainImage()

#define TAU 6.28318

vec2 cmul(in vec2 u, in vec2 v) { return vec2(u.x*v.x - u.y*v.y, u.y*v.x + u.x*v.y); }

float cabs(in vec2 z) { return length(z);      }
float carg(in vec2 z) { return atan(z.y, z.x); }

vec2 polar(float t) { return vec2(cos(t), sin(t)); }
