vec3 hsv2rgb(in vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return vec3(c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y));
}

float sin2(float x) { float s = sin(x); return s*s; }



#define SCALE TAU/2.0
vec2 rectify(vec2 v) { return SCALE*(v+v-iResolution.xy)/iResolution.x; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = rectify(fragCoord.xy);
    vec2 m = 0.01*rectify(iMouse.xy);

    vec2 pan = vec2(
        mix(0.0, -4.5, sin2(0.5*iTime/sqrt(2.0))), // oscillating zoom level 
        0.5*iTime                                  // constant angular pan
    );

    vec2 target = vec2(-1.5, 0.0) - m; // zoom center
    vec2 c = cexp(p.yx+pan)+target;

    vec2 z = c;
    float l = 0.0;
    for (int i = 0; i < 256; ++i) {
        z = cmul(z,z) + c;
        if (dot(z,z) > 4.0) break;
        l += 1.0;
    }

    float s = 1.0+log2(1.0+cabs(z)); 
	vec3 col = hsv2rgb(
    	vec3(
            0.125*s - 0.2*iTime,
            0.5,
            sin2(s+iTime)
        )
    );
    fragColor = vec4(col, 1.0);
}

// http://thndl.com/square-shaped-shaders.html

#define TAU 6.2831853

mat2 rz2(float a){float c=cos(a),s=sin(a);return mat2(c,s,-s,c);}

float st(float t,float n){return min(floor(mod(t,2.)*n)/n,ceil(mod(2.-t,2.)*n)/n);}
float rev(vec2 p){
    float a=atan(p.y,p.x);
    float r=TAU/(st(iTime,4.)*4.+3.);
    return cos(floor(.5+a/r)*r-a)*length(p);
}

float map(vec3 p)
{
    p.xy*=rz2(iTime*.3);
    p.xz*=rz2(iTime*.7);

    vec3 c=vec3(2.);
    p=mod(p+.5*c,c)-.5*c;

    p.yz*=rz2(iTime*.5);
    p.xz*=rz2(iTime*.7);

    float rex=rev(p.xy)-.5;
    float rin=rev(p.xy)-.3;

    return max(max(-rin,rex),abs(p.z)-.1)*.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy -.5*iResolution.xy)/iResolution.y;
    vec3 ro=vec3(uv,-1.),rd=vec3(uv,1.),mp=ro;
    for(int i=0;i<100;++i){
        float md=map(mp);
        if(md<.001)break;
        mp+=rd*md;
    }
	fragColor = vec4(length(mp-ro)*.1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float speed = 5.0;
    float revSpeed = 5.0;

    float pulseWidth = 100.0;
    float revPulseWidth = 12.0;

    vec2 center = iMouse.xy;
    float dist = length(fragCoord-center);
    float len = iResolution.x / 2.0;
    float pct = 1.0-dist/len;

    vec4 finalColor = vec4(pct, pct, pct, 1);
    finalColor.r *= abs(sin(iTime));
    finalColor.g *= abs(cos(iTime));
    finalColor.b *= abs(sin(iTime) + cos(iTime));

    float pulseFactor = abs(sin(dist/pulseWidth-iTime*speed));
	finalColor *= pulseFactor;

    float revPulseFactor = abs(sin(dist/revPulseWidth+iTime*revSpeed));
	finalColor *= revPulseFactor;

    fragColor = finalColor;
}

float udRoundBox( vec3 p, vec3 b, float r ){
  return length(max(abs(p)-b,0.0))-r;
}

float sdBox( vec3 p, vec3 b ){
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float sdSphere(vec3 p, float r){
  return length(p)-r;
}

vec2 map(vec3 p){
  vec3 c = vec3(9.0,9.0,9.0);
  vec3 q = mod(p,c) - 0.5*c;
  float d = udRoundBox(q ,vec3(1.2,0.5,0.7),1.0);
  float limit = -sdBox(p,vec3(32.0));
  d = min(d,limit);
  return vec2(d,limit > d ? 0.0 : 2.0);
}

vec2 trace(in vec3 camPos, in vec3 rayDir){
  float s = 0.0;
  float e = 0.0001;
  float rMax = 128.0;
  float m = 1.0;
  for(int i = 0; i < 64; i++){
    vec3 pos = camPos + s*rayDir;
    vec2 d = map(camPos + s*rayDir);
    if(d.x < e || d.x > rMax) break;
    s += d.x;
    m = d.y;
  }
  if(s > rMax) m = -1.0;
  return vec2(s,m);
}

vec3 gridTexture(vec3 uvw){
  vec3 res = vec3(0.0);
  uvw = sin(uvw *0.5);
  res += 0.2*exp(-16.0*abs(uvw));
  res += 0.1*exp( -4.0*abs(uvw));
  return vec3(length(res));
}

vec3 sampleTexture(vec3 uvw){
  float dir = uvw.z;
  dir = sin((-dir*6.0)+iTime*1.5);
  float d = 1.0*exp(-16.0*abs(dir));
  d += 0.5*exp(-4.0*abs(dir));
  return vec3(0.0,d,d) + (1.0 - d)*vec3(0.05);  
}

//filtering based on https://www.shadertoy.com/view/MdjGR1
const int MaxSamples = 10;
const float detail = 4.0;
vec3 sampleTextureWithFilter(vec3 uvw, vec3 dx, vec3 dy){
  int sx = 1 + int(clamp(detail * length(dx-uvw),0.0,float(MaxSamples-1)));
  int sy = 1 + int(clamp(detail * length(dy-uvw),0.0,float(MaxSamples-1)));
  vec3 color = vec3(0.0);
  for(int i=0;i<MaxSamples;i++){
    for(int j=0;j<MaxSamples;j++){
      if(i<sx && j<sy){
        vec2 st = vec2( float(i), float(j) ) / vec2( float(sx),float(sy) );
        color += sampleTexture(uvw + st.x*(dx-uvw) + st.y*(dy-uvw));  
      }
    }  
  }    
  return color / float(sx*sy);
}

vec4 render(in vec3 camPos, in vec3 rayDir){
  vec2 res = trace(camPos,rayDir);
  if(res.y > -1.0){
    vec3 pos = camPos + rayDir*res.x;
    vec3 ddx_uvw = pos + dFdx( pos ); 
    vec3 ddy_uvw = pos + dFdy( pos );
    //return vec4(sampleTexture(pos),1.0);
    if (res.y < 1.0) return vec4(sampleTextureWithFilter(pos,ddx_uvw,ddy_uvw),1.0);
	return vec4(gridTexture(pos),1.0);
  }
}

mat3 camera(in vec3 position, in vec3 target){
  vec3 w = normalize(target - position);
  vec3 u = normalize(cross(w,vec3(0.0,1.0,0.0)));
  vec3 v = cross(u,w);
  return mat3(u,v,w);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  vec2 uv = fragCoord.xy / iResolution.xy;
  uv = uv * 2.0 - 1.0;
  uv.x *= iResolution.x / iResolution.y;

  vec3 camPos =vec3(8.0) + vec3(4.0*cos(iTime*0.1),0.0,4.0*sin(iTime*0.1));
  vec3 camTarget = vec3(0.0,0.0,0.0);
  mat3 cam = camera(camPos,camTarget);
  vec3 rayDir = cam * normalize(vec3(uv,1.0));
  fragColor = render(camPos,rayDir);
}

