void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 v=uv*(1.-uv);
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;

    vec2 uvn=uv*2.5;
    vec2 iuvn=floor(uvn)+vec2(2.,0.);
    vec2 fuvn=fract(uvn);
    vec3 nb=mix(
        mix(noise(iuvn+vec2(0.,0.)), noise(iuvn+vec2(1.,0.)), fuvn.x),
        mix(noise(iuvn+vec2(0.,1.)), noise(iuvn+vec2(1.,1.)), fuvn.x),
        fuvn.y)*.1;
    vec3 c=(vec3(0.168, 0, 0.2)*.5+nb*3.);

    vec2 suv=uv;
    suv*=rz2(iTime*.02);
    c*=vec3(1./(1.-smoothstep(0.9,1.,texture(iChannel1,suv).r)));

    vec2 uvc=uv-vec2(.4,.2);
    float circle=1.-smoothstep(.25,.252,length(uvc));
    float raytime=uv.y*100.+iTime*2.;
    float thr=-uvc.y*5.-1.;
    float rays= step(thr,sin(raytime));
    circle=min(circle,rays);
    vec3 csun=mix(vec3(0.968, 0.137, 0.094),vec3(1, 0.819, 0.019),uvc.y*3.+.5);
    c=mix(c,csun,circle);

    vec3 ro=vec3(0.,2.,0.),rd=vec3(uv,1),mp=ro;
    rd.yz*=rz2(-.2);
    int i;
    for(i=0;i<50;++i){map(mp);if(m.d<.001){break;}mp+=rd*.5*m.d;}
    if(mp.z<14.)c=m.c;

    c=max(c,0.);
    float cren=fract(uv.y*200.+iTime*.5);
    c+=(smoothstep(.2,.3,cren)-smoothstep(.7,.8,cren))*0.01;
    c=pow(c,vec3(1./2.2));
    c *= pow(v.x*v.y * 25.0, 0.25);
	fragColor = vec4(c,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // RGBA
    vec4 color = vec4(0.5, 1.0, 0.5, 1.0);
    // color.gr = vec2(0.1, 0.5);

    // pixel position
	vec2 pixel = fragCoord.xy / iResolution.xy;

    //color.r = pixel.y;

    // center origin point 
    pixel = (pixel - 0.5) * 1.0;

    // aspect ratio
    pixel.x *= iResolution.x / iResolution.y;

    // time elapsed
    float time = iTime;

    float anim = sin(time);

    //pixel *= abs(anim); // * 3.;

    //pixel -= vec2(.5, 0.);

    //trÃ¨fle derriÃ¨re la vitre bullÃ©e
    pixel += mod(pixel, .05);

    //pixel *= abs(pixel * 0.5);

    // trÃ¨fle

    //float r = sqrt(dot(pixel, pixel)); // Old version
    float r = length(pixel); // New version

    float a = atan(pixel.y, pixel.x) + time * 0.5;
    float s = 0.5 + 0.5 * sin(3.0 * a);
    float t = 0.15 + 0.35 * pow(s, 0.3);
    t += 0.1 * pow(0.5 + 0.5 * cos(6.0 * a), 0.5);
    float h = r/t;

    //float f = 0.0;
    //if(h < 1.0) f = 1.0; // Old Version
    float f = 1. - step(1., h); // New Version

    color.rgb = mix(vec3(1.0), vec3(0.5 * h, 0.5 + 0.5 * h, 0.0), f);

	fragColor = color;
}

// From Stackoveflow
// http://stackoverflow.com/questions/15095909/from-rgb-to-hsv-in-opengl-glsl
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

// Simplex 2D noise
// from https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
vec3 permute(vec3 x) { return mod(((x*34.0)+1.0)*x, 289.0); }

float snoise(vec2 v){
    const vec4 C = vec4(0.211324865405187, 0.366025403784439,
                        -0.577350269189626, 0.024390243902439);
    vec2 i  = floor(v + dot(v, C.yy) );
    vec2 x0 = v -   i + dot(i, C.xx);
    vec2 i1;
    i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
    vec4 x12 = x0.xyxy + C.xxzz;
    x12.xy -= i1;
    i = mod(i, 289.0);
    vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
                     + i.x + vec3(0.0, i1.x, 1.0 ));
    vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy),
                            dot(x12.zw,x12.zw)), 0.0);
    m = m*m ;
    m = m*m ;
    vec3 x = 2.0 * fract(p * C.www) - 1.0;
    vec3 h = abs(x) - 0.5;
    vec3 ox = floor(x + 0.5);
    vec3 a0 = x - ox;
    m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
    vec3 g;
    g.x  = a0.x  * x0.x  + h.x  * x0.y;
    g.yz = a0.yz * x12.xz + h.yz * x12.yw;
    return 130.0 * dot(m, g);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float xnoise = snoise(vec2(uv.x, iTime / 5.0 + 10000.0));
    float ynoise = snoise(vec2(uv.y, iTime / 5.0 + 500.0));
    vec2 t = vec2(xnoise, ynoise) + iMouse.xy / 100.0;
    float s1 = snoise(uv + t / 2.0 + snoise(uv + snoise(uv + t/3.0) / 5.0));
    float s2 = snoise(uv + snoise(uv + s1) / 7.0);
    vec3 hsv = vec3(s1, 1.0, 1.0-s2);
    vec3 rgb = hsv2rgb(hsv);
	fragColor = vec4(rgb, 1.0);
}

