// Value Noise courtesy of Book of Shaders
// https://thebookofshaders.com/11/
float noise2d(vec2 uv) {

    vec2 pos = floor(uv);
    vec2 fractional = fract(uv);

    // four corners
    float a = noise(pos);					// bottom left
    float b = noise(pos + vec2(1., 0.));	// bottom right
    float c = noise(pos + vec2(1., 1.));	// top right
    float d = noise(pos + vec2(0., 1.));	// top left

    vec2 intermix = smoothstep(0., 1., fractional);

    float value = mix(a, b, intermix.x);
    value += (d - a) * intermix.y * (1.0 - intermix.x);
    value += (c - b) * intermix.x * intermix.y;

    return value;
}

// Fractal noise courtesy of iq
// https://www.shadertoy.com/view/XdXGW8
float fractalNoise2d(vec2 uv) {
    uv *= 3.0;
    uv.x -= iTime / 5.0;
    uv.y += sin(iTime / 5.0) * 2.0;

    mat2 rotate = mat2(1.6 - sin(iTime / 100.0) / 10.0, 1.2, -1.2, 1.6);

    float value = 0.5 * noise2d(uv);
    uv *= rotate;
    value += 0.25 * noise2d(uv);
    uv *= rotate;
    value += 0.125 * noise2d(uv);
    uv *= rotate;
    value += 0.0625 * noise2d(uv);

    return value;
}

vec2 rotate2D(vec2 _st, float _angle){
    _st -= 0.5;
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    _st += 0.5;
    return _st;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = fragCoord.xy/iResolution.x;

    float noise = fractalNoise2d(uv);
    uv = rotate2D(uv, noise*300.);

    uv = .5*uv;
    float plasma = noise*(sin(uv.x)+cos(uv.y))*5.;

    vec3 col = vec3(sin((-plasma)+iTime),
                    sin((-plasma+.4)+iTime),
                    sin((-plasma+.5)+iTime));

	fragColor = vec4(col,1.0);
}

#define Scale vec3(.8, .8, .8)
#define K 19.19


const vec3 BlueSky = normalize(vec3(50.0,119.0,223.0));
const vec3 Orange = normalize(vec3(180.0,100.0,36.0))*0.8;

vec3 hash(vec3 p3)
{
	p3 = fract(p3 * Scale);
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy + p3.yxx)*p3.zyx);

}

vec3 noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    return mix(mix(mix( hash(p+vec3(0,0,0)), 
                        hash(p+vec3(1,0,0)),f.x),
                   mix( hash(p+vec3(0,1,0)), 
                        hash(p+vec3(1,1,0)),f.x),f.y),
               mix(mix( hash(p+vec3(0,0,1)), 
                        hash(p+vec3(1,0,1)),f.x),
                   mix( hash(p+vec3(0,1,1)), 
                        hash(p+vec3(1,1,1)),f.x),f.y),f.z);
}

const mat3 m = mat3( 0.00,  0.80,  0.60,
                    -0.80,  0.36, -0.48,
                    -0.60, -0.48,  0.64 );
vec3 fbm(in vec3 q)
{
            vec3 f  = 0.5000*noise( q ); q = m*q*2.01;
            f += 0.2500*noise( q ); q = m*q*2.02;
            f += 0.1250*noise( q ); q = m*q*2.03;
            f += 0.0625*noise( q ); q = m*q*2.01; 
            //f += 0.03125*noise( q ); q = m*q*2.01; 
    return vec3(f);
}

float sdSphere(vec3 p, float s)
{
    return length(p) - s;
}

float sdPlane( vec3 p, vec4 n )
{
  // n must be normalized
  p.y *= pow(sin(fbm(p*0.3).y), 2.0); 
  return dot(p,n.xyz) + n.w;
}

float map(vec3 p)
{
    return sdPlane(p, vec4(0.0, 1.0, 0.0, 1.0));
}

float intersect(in vec3 ro, in vec3 rd)
{
    const int ITERATIONS = 80;
    const float maxD = 60.0;
    const float minD = 0.0001;
    float h = 1.0;
    float t = 0.0;

    for (int i = 0; i < ITERATIONS; ++i)
    {
        if(h < minD || t > maxD)
            break;

        h = map(ro+rd*t);
        t += h;
    }

    if(t > maxD) t = -1.0;

    return t;
}

vec3 calcNormal( in vec3 pos )
{
    vec3 eps = vec3(0.002,0.0,0.0);

	return normalize( vec3(
           map(pos+eps.xyy) - map(pos-eps.xyy),
           map(pos+eps.yxy) - map(pos-eps.yxy),
           map(pos+eps.yyx) - map(pos-eps.yyx) ) );
}

vec3 lighting(vec3 p, vec3 ro, vec3 rd)
{
    vec3 n = normalize(calcNormal(p));
    vec3 l = -normalize(ro + vec3(20.,20.0, 2.0) - p );
    float d = max(dot(-l, n), 0.0);

    float s = pow(max(dot(reflect(-l, n), rd), 0.0), 2.0);

    float a = 1.0 / length(ro - p);

    return vec3(s+d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3(sin(iTime*0.05)*10.0, sin(iTime)*0.2, 2.0 - iTime*0.3);
    vec3 rd = normalize(vec3(uv, -1.0));

    float t = intersect(ro, rd);

    vec3 color;
    vec3 pos = ro+rd*t;
    vec3 normal = calcNormal(pos);

    vec3 t1= texture(iChannel1, vec2(pos.x, pos.z)*0.2).rgb;
    vec3 t2= texture(iChannel0, vec2(pos.x, pos.z)*0.2).rgb;
    vec3 c = mix(t2, t1, max(dot(normal, vec3(0.0, 1.0, 0.0)), -1.0));

    c *= lighting(pos, ro, rd)*t2;

    /**Sky*/
    vec3 FBM = fbm(vec3(vec2(uv.x, uv.y*2.0), 1.0)*2.0+iTime*0.1);

    float scatteringFactor = uv.y+0.3;

    vec3 gradient = mix(normalize(vec3(148, 235, 233)), BlueSky, uv.y/2.0 - 0.5);

    vec3 SkyColor = mix(Orange, gradient, scatteringFactor);

    vec3 fgc = mix(vec3(2.0), vec3(0.5), smoothstep(0.0, 1.0, length(uv-vec2(0.0, 1.0))));
    color = mix(SkyColor*fgc, vec3(0.8), (pow(FBM.x, 3.0))+uv.y);


    /*------------------------------------------------------*/
    c = mix(c, color, smoothstep(0.4, 0.8, t/20.0));
    if(t > 0.0)
    {
        color = vec3(c);
    }

	fragColor = vec4(color,1.0);
}

//signed (& unsigned) distance from p.xy
//to sawtooth function y=mod(x,a)*b;
//hold left mouse button to set; mouse.x->a , mouse.y->b
//
//also has shortcut version to sawtooth function y=fract(x)=mod(x,1)*1;

//set zoom and distance_between_lines (converging to surface).
//#define zoom    (sin(iTime   )*.5+1.)
#define zoom  1.

#define dbl  (5.*sin(iTime*3.)+5.)
//#define dfv   5.

//sawShape.x=wavelength sawshape.y=amplitude range [0..sawShape.y]
//#define sawShape vec2(1,2)
#define sawShape vec2(sin(iTime)+2.,cos(iTime)+1.)

//eightRotation.y=eightRotation.x=cos(3.14/4)=sin(3.14/4)=
//...pow(2,1/2)/2=sqrt(2)/2=0.70710678118654752440084436210485
#define p222 sqrt(0.5) 
//return p, rotated by 45deg
vec2 pR45(in vec2 p){return (p+vec2(p.y,-p.x))*p222;}
//return p, rotated by r;
#define r(p,r) (cos(r)*p+sin(r)*vec2(-p.y,p.x))

//the function y=fract(x)   
//is a sawtooth with wavelength=amplitude=1
//
//the function y=mod(x,b)*c 
//is a sawtooth with wavelength=b, amplitude=c

//distance to sawtooth with wavelength=amplitude=1 is simpler
//than a parametric sawdooth with parametric wavelength.
//below both are solved, for signed and unsidned values.

//a core subroutine is; distance to an infinite-line-segment: ils()

//return distance of (p) to ray that starts at (0,0) and has direction (0,-1);
float ils(vec2 p){return mix(abs(p.y),length(p),step(0.,p.x));}
//vertical utility of ils()
float vert(vec2 p){p.x=fract(p.x);return ils(p.yx-vec2(1,0));}
//45deg diagonal utility of ils()
float diag(vec2 p){p.x=fract(p.x);p=pR45(p);
 return ils(p-vec2(sqrt(2.),0));}

//tt1 and tt2 transform p from a point below the curve 
//to an identical point above the curve.
//(as far as unsigned distance function to its curve is concerned) 

//if(p.y-fract(p.x)<0.){p=-p;p.y+=1.;}//<- is identical to [t1]
#define tt1 p=mix(p,-p,t);p.y+=mix(0.,1.,t);
#define tt11 float t=step(p.y-fract(p.x),0.);tt1
//return unsigned distance to sawtooth-wave, wavelength=1, range.y=[0..1]
float sawtoothU(vec2 p){tt11 return min(vert(p),diag(p));}

//abive uses vert(p),diag(p);
//below function does not use vert(p),diag(p), but its functions.

//return distance to sawtooth-wave, wavelength=1, range.y=[0..1]
float sawtooth(vec2 p){tt11
 p.x=fract(p.x);
 float vert=ils(p.yx-vec2(1,0));
 p=pR45(p);
 float diag=ils(p-vec2(sqrt(2.),0));
 return (1.-t*2.)*min(vert,diag);}

//above 2 functions are special SHORT cases for below 2 functions as in:
//sawtoothU(p)==sawtoothWU(p,vec2(1))
//sawtooth (p)==sawtoothW (p,vec2(1))


float diag2(vec2 p,vec2 w){
 return ils(r(p,-atan(w.y/w.x))-vec2(length(w),0));}

float vert2(vec2 p,vec2 w){p.x=mod(p.x,w.x);
 return ils(p.yx-vec2(w.y,0));}

//subroutine for sawtoothW*()
float sawtooth2s(vec2 p,vec2 w){p.x+=w.x;
 float v=vert2(p,w),d=diag2(p,w),e=diag2(p-vec2(w.x,0),w);
 return min(min(d,e),v);}

#define tt2 p.y-=mix(0.,w.y,t);p=mix(p,-p,t);
#define tt2w p=vec2(mod(p.x,w.x),p.y);float t=step(p.y-p.x*w.y/w.x,0.);tt2
//return unsigned distance of [p] to sawtooth[w] .x=wavelength .y=amplitude
float sawtoothWU(vec2 p,vec2 w){tt2w return sawtooth2s(p,w);}

//return distance of [p] to sawtooth[w] .x=wavelength .y=amplitude
float sawtoothW(vec2 p,vec2 w){tt2w return (1.-t*2.)*sawtooth2s(p,w);}

//subroutine for sawtoothW*()
float sawtooth2s2(vec2 p,vec2 w){p.x+=w.x;
 float v=vert2(p,w),d=diag2(p,w),e=diag2(p-vec2(w.x,0),w);
 return min(min(d,e),v);}
float sawtoothW2(vec2 p,vec2 w){tt2w return (1.-t*2.)*sawtooth2s2(p,w);}

