vec2 rot(vec2 p, float a) {
	return vec2(
		p.x * cos(a) - p.y * sin(a),
		p.x * sin(a) + p.y * cos(a));

}

float map(vec3 p) {
	float tp0 = 4.0 - dot(abs(p), vec3(0,1,0));
	float tp1 = 4.0 - dot(abs(p), vec3(1,0,0));
	float ts = length(mod(p, 2.0) - 1.0) - 0.5;
	float tx = length(mod(p.yz, 2.0) - 1.0) - 0.3;
	float ty = length(mod(p.zx, 2.0) - 1.0) - 0.3;
	float tz = length(mod(p.xy, 2.0) - 1.0) - 0.3;
    float txs = length(mod(p.yz, 4.0) - 2.0) - 1.9;
	float tys = length(mod(p.zx, 4.0) - 2.0) - 1.9;
	float tzs = length(mod(p.xy, 4.0) - 2.0) - 1.9;
    float txp = length(mod(p.yz, 0.2) - 0.1) - 0.04;
	float typ = length(mod(p.zx, 0.2) - 0.1) - 0.04;
	float tzp = length(mod(p.xy, 0.2) - 0.1) - 0.04;
    float t = 10000.0;
	t = min(tp0, t);
	t = min(tp1, t);
	t = max(-tx, t);
	t = max(-ty, t);
	t = max(-tz, t);
    t = max(-txp, t);
	t = max(-typ, t);
	t = max(-tzp, t);
    t = max(-txs, t);
	t = max(-tys, t);
	t = max(-tzs, t);
    t = min(ts, t);
	t = max(-txp, t);
	t = max(-typ, t);
	t = max(-tzp, t);
	t = max(-tx, t);
	t = max(-ty, t);
	t = max(-tz, t);
    return t;
}

vec3 getnormal(vec3 p){
    float d = 0.001;
        return normalize(vec3(
        map(p + vec3(  d, 0.0, 0.0)) - map(p + vec3( -d, 0.0, 0.0)),
        map(p + vec3(0.0,   d, 0.0)) - map(p + vec3(0.0,  -d, 0.0)),
        map(p + vec3(0.0, 0.0,   d)) - map(p + vec3(0.0, 0.0,  -d))
    ));
}

vec4 getcolor(vec2 uv, float z) {
    uv.x += z;
    float rad = 0.3;

	vec3 dir = normalize(vec3(uv, 1.0));
	vec3 pos = vec3(rad, rad, iTime * 3.0 + sin(iTime) * 2.0);
    pos.xy = rot(pos.xy, iTime * 0.5);
    dir.xz = rot(dir.xz, iTime * 0.2);
    dir.yz = rot(dir.yz, iTime * 0.1);

	float t = 0.0;
	for(int i = 0; i < 100; i++) {
		float temp = map(dir * t + pos);
		if(temp < 0.005) break;
		t += temp;
	}
	vec3 ip = dir * t + pos;
	vec3 V = normalize(-ip);
	vec3 N = getnormal(ip);
	vec3 L = normalize(vec3(3,4,-1));
	vec3 H = normalize(L + V);
	float Kd = max(dot(L, N), 0.0);
	float Ks = pow(max(dot(H, N), 0.0), 16.0);
	vec4 D = vec4(Kd) * vec4(1,0.3,0.3,1);
	vec4 S = vec4(Ks) * vec4(1,1,0.3,1);
	vec4 F = vec4(t * 0.1) * vec4(0.4,0.7,2,1);
	return vec4((D + S + F).xyz + dir * 0.2, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float cod = mod(fragCoord.y, 2.0);
    if(mod(fragCoord.x + cod, 2.0) > 0.5) {
        fragColor = vec4(0.0);
        return;
    }


	vec2 uv = ( fragCoord.xy / iResolution.xy )  * 2.0 - 1.0;
    uv.y *= iResolution.y / iResolution.x;
	float Z = 0.007;
	vec4 R = getcolor(uv, -Z);
	vec4 G = getcolor(uv, 0.0);
	vec4 B = getcolor(uv, Z);
	fragColor.x = R.x;
	fragColor.y = G.y;
	fragColor.z = B.z;
	fragColor.w = 1.0;
	float v = fragColor.x * 0.298912 + fragColor.y *  0.586611 + fragColor.z * 0.114478;
	fragColor.xyz *= mix(vec3(0.5,0.6,0.9), vec3(0.5,0.6,0.9).zyx, v);
    fragColor.xyz *= clamp(fragColor.xyz, vec3(0.0), vec3(1.0));
	fragColor.xyz *= clamp(1.0 - dot(uv * 0.7, uv), 0.0, 1.0);
}

#define PI 3.14159265359

vec4 circle(in vec2 position, in vec2 uv, float size, vec3 color) {

    float dist = distance(position, uv); // The distance between the pixel and the circle position

    vec4 circ = vec4(0.0); // Creating a "full" color for the circle
    circ.r = circ.g = circ.b = smoothstep(size, size + 0.01, dist); // Setting up the inverted circle rgb color

//   	circ.rgb -= vec3(1.0) - color; // Remove the color difference from the white
    circ.rgb += color;

    return circ;
}

// Courtesy Robert Penner
// t: current time, b: begInnIng value, c: change In value, d: duration
float easeOutElastic(in float t, in float b, in float c, in float d) {
    float s=1.70158;
    float p=0.0;
    float a=c;
    if (t==0.0) {
    	return b;
    }
    if ((t/=d)==1.0) {
        return b+c;
    }
    if (p == .0) {
        p=d*.3;
    }
    if (a < abs(c)) {
        a=c;
        s=p/4.0;
    } else {
        s = p/(2.0*PI) * asin(c/a);
    }
    return a*pow(2.0,-10.0*t) * sin( (t*d-s)*(2.0*PI)/p ) + c + b;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float speed = 0.4;
    float time = iTime * speed;
	vec2 uv = (fragCoord.xy - .5 * iResolution.xy) / iResolution.y;
    float repeat = 4.0;
    uv *= repeat;
    float radius = 0.4;

    if( mod(floor(time), 2.0) == 0.0 ) {
        float change = (-1.0 + mod(ceil(uv.y), 2.0) * 2.0);
        uv.x += easeOutElastic(fract(time), .0, change, 1.0);
    } else {
        float change = (-1.0 + mod(ceil(uv.x), 2.0) * 2.0);
        uv.y += easeOutElastic(fract(time), .0, change, 1.0);
    }

    vec2 position = vec2(.5);


    fragColor = circle(position, fract(uv), radius, vec3(0.3, 0.3, 0.3));
}

#define TAU 6.2831853
mat2 rz2(float a){float c=cos(a),s=sin(a);return mat2(c,s,-s,c);}

float hlx(vec3 p){
    float m=TAU/6.;
    float a=mod(atan(p.y,p.x)-p.z+iTime,m)-.5*m;
    float l=length(p.xy);
    return length(vec2(a*l,length(p.xy)-1.))-.2;
}

float map(vec3 p){
    p.xy*=rz2(iTime*.3);
    p.yz*=rz2(iTime*.5);
    p.xz*=rz2(iTime*.7);
    float d=min(min(hlx(p.xyz), hlx(p.yzx)),hlx(p.zxy))*.5;
    vec3 n=normalize(sign(p));
    float d2=max(length(max(abs(p)-vec3(2.),0.)),dot(p,n)-2.3);
    return min(d,d2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy-.5*iResolution.xy)/iResolution.y;
    vec3 ro=vec3(uv,sin(iTime)*2.-6.),rd=vec3(uv,1.),mp=ro;
    for(int i=0;i<50;++i){
        float md=map(mp);
        if(md<.001)break;
        mp+=rd*md;
    }
	fragColor = vec4(length(mp-ro)*.08);
}

