float draw_grid(vec2 st){
  float tile_len = N_TILES_Y / iResolution.y;

  // Why doesn't smoothstep induces AA in this case?
  vec2 grid = smoothstep(.5 * tile_len, tile_len, fract(tile_len * st) - THICKNESS);
