#define QUALITES 100
#define EPSILON 0.01
#define Pi 3.141592653589793


mat3 rotY(float a) {
    float c = cos(a), s = sin(a);
    return mat3(c, 0., s, 0., 1., 0., -s, 0., c);
}

mat3 rotX(float a) {
    float c = cos(a), s = sin(a);
    return mat3(1., 0., 0., 0., c, s, 0., -s, c);
}

float udBox( vec3 p, vec3 b ){
  return length(max(abs(p)-b,0.0));
}

float udRoundBox( vec3 p, vec3 b, float r ){
  return length(max(abs(p)-b,0.0))-r;
}

float sdSphere( vec3 p, float s ){
  return length(p)-s;
}

float sdTorus( vec3 p, vec2 t ){
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float smin( float a, float b, float k ){
    a = pow( a, k ); b = pow( b, k );
    return pow( (a*b)/(a+b), 1.0/k );
}

float M(vec3 a, float x){
    float t = mod(iTime, 12.56);
    float roubox = udRoundBox(rotY(t * x) * rotX(t) *  a,vec3(x), x / 15.);
    float spher = sdSphere(a, (sin(iTime * 11.) / (10. / x )+ (1.666 * x))/*x * 1.6*/);
    return max (roubox, -spher);
}

float M2(vec3 a, float x){

    float tor = sdTorus(rotX(iTime * x) * rotY(iTime) * a,vec2(x, x * (sin(iTime * 11.) + x * 1.5) / (5. / x ) * 0.1));
    float spher = sdSphere(a, x);
    return max (tor, -spher);
}

float wplane(vec3 a) {
  return a.y + 4.0;
}

float world1(vec3 a) {
    float minM = M(a, 2.0);
    for(float x = 1.75; x >= 0.25; x -= 0.25)
        minM = min(minM, M(a, x));
    return min(minM, wplane(a));
}

float world2(vec3 a) {    
    float minM = M2(a, 2.0);
    for(float x = 1.75; x >= 0.25; x -= 0.25)
        minM = min(minM, M2(a, x));
    return min(minM, wplane(a));
}

vec3 wcolor(vec3 a) {
    float x = 2.;
    float minimum = M(a, x);
    vec3 color = vec3(length (a) / sqrt(3.), 0.,  1. - length (a) / sqrt(3.));
    for(float x = 1.75; x >= 0.25; x -= 0.25){
        if (minimum > M(a, x)) {
        	minimum = M(a, x);
            color =  vec3(length (a) / sqrt(3.), 0., 1. - length (a) / sqrt(3.));
        }
    }
    if (minimum > wplane(a))
        color = vec3(0.0, 1.0, 0.0);
    color *= 1.5;
    return color;
}

vec3 wnormal(vec3 a) {
  vec2 e = vec2(EPSILON, 0.);
  float w = world1(a);
  return normalize(vec3(
    world1(a+e.xyy) - w,
    world1(a+e.yxy) - w,
    world1(a+e.yyx) - w));
}

float trace(vec3 O, vec3 D) {
    float t = mod(iTime, Pi * 12.);
	float L = 0.;
    float d1, d2;
	for (int i = 0; i < QUALITES; ++i) {
        if (t < Pi * 4.) {
			d1 = world1(O + D*L);
        } else {
            if (t < Pi * 5.) {
            	float x = abs(sin(iTime * 0.5));
                d1 = mix(world1(O + D*L), d1 = world2(O + D*L), x);
        	} else {
            	if (t < Pi * 11.) {
        			d1 = world2(O + D*L);
           		} else {
            		float x = abs(sin(iTime * 0.5));
            	    d1 = mix(world1(O + D*L), d1 = world2(O + D*L), x);            	   
            	}
            }
        }
        //float x = sin(iTime * 0.5) / 3. + 0.33;
		//L += mix(d1, d2, x);

		L += d1;
		if (d1 < EPSILON && d2 < EPSILON) break;
	}
	return L;
}

float occlusion(vec3 at, vec3 normal) {
  float b = 0.;
  for (int i = 1; i <= 4; ++i) {
    float L = .06 * float(i);
    float d = world1(at + normal * L);
    b += max(0., L - d);
  }
  return min(b, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy * 2. - 1.;
	uv.x *= iResolution.x / iResolution.y;

	vec3 O =  vec3(0., 0., 9.);
	vec3 D = normalize(vec3(uv, -2.));

	float path = trace(O, D);    
    vec3 pos = O + D * path;
    vec3 nor = wnormal(pos);

    vec3 color = vec3(.9) * (1.0 - occlusion(pos, nor)) * wcolor(pos);

    color = mix(color, vec3(1.0), smoothstep(0.,30.,path));

    fragColor = vec4(color,1.);
}

float map(vec3 p){
    //vec3 q = fract(p) * 2.0 - 1.0;
    float r = 1.0 * (sin(iTime) + 3.0);
    vec3 q = mod(p,r)-0.5*r;

    vec3 c = vec3(0.0, 0.0, 0.05);

	return min(min(min(length(q) - (1.1 + sin(iTime + length(p * 0.4) * 2.0)) * 0.25, length(q.xz-c.xy)-c.z), length(q.xy-c.xy)-c.z), length(q.yz-c.xy)-c.z);
}

float trace(vec3 o, vec3 r){
	float t = 0.0;
    for(int i = 0; i < 30; ++i){
        vec3 p = o + r * t;
        float d = map(p);
        t += d * 0.8;
    }
    return t;
}

