    r.xz *= mat2(cos(the), -sin(the), sin(the), cos(the));
    r.xy *= mat2(cos(the), -sin(the), sin(the), cos(the));

    vec3 o = vec3(0.0, iTime* 0.5, -3.0 - iTime);

    float t = trace(o, r);

    float fog = 1.0 / (1.0 + t * t * 0.1);

    if(fog < 0.3)fog *= 2.0 * (1.5 + sin(iTime * 0.2));

    vec3 fc = vec3(fog);

	fragColor = vec4(fog * uv.x, fog, fog * uv.y, 1.0);
