const float TAU = 2.* 3.1415926535897932384626433832795;
const float grid4 = 2097152.0,grid3 = 131072.0,grid2 = 1024.0,grid1 = 64.0;

vec4 grid(vec2 f, vec3 pos, float rdy, float t) {
    float ff = min(iResolution.x,iResolution.y)/1024.0;
	vec4 distances = (abs(pos.xz)* TAU).xxyy;
	float referenceBase = log(20.*ff/ (t* pow(abs(rdy),.8)))/ log(10.);
	float nearestBase = floor(referenceBase);
	float partialBase = fract(referenceBase);
	const vec4 gain = vec4(grid4- grid3,grid1,grid3- grid2,grid2- grid1);
	const vec4 off = vec4(grid3,0,grid2,grid1);
	vec4 exponentialBase = partialBase* partialBase* gain+ off;
	vec4 bases = pow(vec4(10),nearestBase+ vec4(-2,1,-1,0));
	vec4 lx = pow(.5+ .5* cos(distances* bases.xyxy),exponentialBase.xyxy);
	vec4 ly = pow(.5+ .5* cos(distances* bases.zwzw),exponentialBase.zwzw);
	vec4 l4 = (1.- lx* vec4(1.- partialBase,partialBase,1.- partialBase,partialBase))* (1.- ly);
	vec2 l2 = l4.xy* l4.zw;
	float l1 = .30078125* (1.- l2.x* l2.y);
#ifdef MINUS
    l1 = -l1;
#endif
	return vec4(vec3(f.x/ iResolution.x,.5,f.y/ iResolution.y)+ l1,1);
}
vec4 mainImageGrid(vec2 f) {
	float rcpResY = 1./ iResolution.y;
	vec2 uv = 2.0* rcpResY* f- vec2(iResolution.x* rcpResY,1);
	vec3 ro = vec3(0,exp2(128.* (sin(iTime* .002)+ step(10.,iMouse.x)*(2.* rcpResY* iMouse.y- 1.))),0);
	vec3 rd = normalize(vec3(uv.x,-1,uv.y));
	float t = ro.y/ -rd.y;
	vec3 pos = ro+ t* rd;
	return grid(f,pos,rd.y,t);
}
void mainImage(out vec4 fragColor, vec2 fragCoord) {
	fragColor = mainImageGrid(fragCoord);
}

#define SPREAD 0.2
#define ORBIT 0.5
#define PLANET_SIZE 0.1
#define RING_BEGIN 0.025
#define RING_THICKNESS 0.1
#define SUN_INTENSITY 1.0
#define MOUSE_INTENSITY 1.0/toMouseDist

#define PLANET_COLOR vec4(0, 1, 0, 1)
#define RING_COLOR vec4(0, 0, 1, 1)
#define BACKGROUND_COLOR vec4(1, 0, 0, 1)

float distanceToSegment( vec2 a, vec2 b, vec2 p )
{
	vec2 pa = p - a, ba = b - a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );
}

float length2(vec2 v) {
    return dot(v, v);
}

float length2(vec3 v) {
    return dot(v, v);
}

// lighting calculation. +SPREAD biases it so the light will fade out farther from
// the centerline.
float lightSphere(float existing, vec3 surfaceNormal, vec3 lightNormal, float intensity) {
    float b = dot( surfaceNormal, -lightNormal )+SPREAD;
    b *= intensity;
    return clamp( max(existing, b + existing/2.0), 0.0, 1.0);
}

float lightRings(float existing, vec2 ringPos, vec2 lightNormal, float intensity) {
    float d = distanceToSegment(vec2(0, 0), lightNormal*10.0, ringPos) / PLANET_SIZE;
    float b = smoothstep(.8,1.0, d)*0.8*intensity;
    return clamp( max(existing, b + existing/2.0), 0.0, 1.0);
}

// make screen coordinates reasonable
vec2 normalizeScreenCoords(vec2 screenCoords) {
    vec2 coords = (2.0*(screenCoords / iResolution.xy)) - 1.0;
    coords.x = coords.x * (iResolution.x / iResolution.y);
	return coords;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 light = vec3(sin(iTime), cos(iTime), 0); // light direction

    // normalise coordinates
    vec2 fragUV = normalizeScreenCoords(fragCoord.xy);

    // center of circle
    vec2 center = light.xy * ORBIT;

    vec2 posRelCircle = fragUV - center;

    float d2 = dot(posRelCircle, posRelCircle); // distance from center squared

    if(d2 <= PLANET_SIZE*PLANET_SIZE) {
        posRelCircle = posRelCircle / PLANET_SIZE;
        float len = length(posRelCircle); // distance from center

    	vec3 normal = normalize(vec3(posRelCircle, cos(len))); // horizontal components + vertical component

        float lightMultiplier = 0.0;

        lightMultiplier = lightSphere(lightMultiplier, normal, light, SUN_INTENSITY);

        if(iMouse.z > 0.0) {
            vec2 toMouseVec = normalizeScreenCoords(iMouse.xy)-center;
            float toMouseDist = length(toMouseVec);
            vec2 mouseNormal = -normalize(toMouseVec);

            float intensity = MOUSE_INTENSITY;

        	lightMultiplier = lightSphere(lightMultiplier, normal, vec3(mouseNormal, 0), intensity);
        }

        lightMultiplier = clamp(lightMultiplier, 0.0, 1.0);
		fragColor = PLANET_COLOR * lightMultiplier;
    } else if(d2 > (PLANET_SIZE+RING_BEGIN)*(PLANET_SIZE+RING_BEGIN) &&
         d2 < (PLANET_SIZE+RING_BEGIN+RING_THICKNESS)*(PLANET_SIZE+RING_BEGIN+RING_THICKNESS)) {
        float lightMultiplier = 0.0;

        lightMultiplier = lightRings(lightMultiplier, posRelCircle, light.xy, SUN_INTENSITY);

        if(iMouse.z > 0.0) {
            vec2 toMouseVec = normalizeScreenCoords(iMouse.xy)-center;
            float toMouseDist = length(toMouseVec);
            vec2 mouseNormal = -normalize(toMouseVec);

            float intensity = MOUSE_INTENSITY;

        	lightMultiplier = lightRings(lightMultiplier, posRelCircle, mouseNormal, intensity);
        }

        lightMultiplier = clamp(lightMultiplier, 0.0, 1.0);

        fragColor = RING_COLOR * lightMultiplier;
    } else {
        fragColor = BACKGROUND_COLOR;
    }
}

