// text
#define grid 16.
#define cell 1./16.
const int kA=177,kB=178,kC=179,kD=180,kE=181,kF=182,kG=183,kH=184,kI=185,kJ=186,kK=187;
const int kL=188,kM=189,kN=190,kO=191,kP=160,kQ=161,kR=162,kS=163,kT=164,kU=165,kV=166;
const int kW=167,kX=168,kY=169,kZ=170,kSpace=80;

vec2 getSymbol (int key)
{
	return vec2(mod(float(key),grid),floor(float(key)/grid));
}

vec2 getLetterUV (vec2 target, vec2 offset)
{
    vec2 uvLetter = target;
    uvLetter.x = uvLetter.x * 0.45 + 0.017;
    uvLetter += offset / grid;
    float crop = step(target.x, cell) * step(target.y, cell);
    crop *= step(0., target.x) * step(0., target.y);
    return uvLetter * crop;
}

float getText (vec2 target)
{
    int symbols[] = int[] ( kH,kO,kN,kE,kY );
    int count = symbols.length();
    vec2 space = vec2(0.5,1);
    vec2 textUV = vec2(0);
    for (int i = 0; i < count; ++i) {
        vec2 offset = vec2(i,0)/grid;
        offset.x -= float(count)/grid/2.;
        offset.y -= cell/2.;
    	textUV += getLetterUV(target - offset, getSymbol(symbols[i]));
    }
    return texture(iChannel1, textUV).r;
}

float signal (vec2 uv, float thin, float freq1, float freq2)
{
    const float height = 0.2;
    uv.y += sin(uv.x*freq1+iTime)*0.2;
    return abs(thin/(sin(uv.x*freq2-iTime)-uv.y/height));
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 color = vec3(0);
    float unit = 1./iResolution.y;
    vec2 uv = 2.*(fragCoord-.5*iResolution.xy)*unit;
    float time = iTime;
    vec3 honey = vec3(0.9, 0.45, 0.2);
    vec3 light = vec3(1.,0.9,0.8);

    float thin = .5;
    float freq1 = 4.;
    float freq2 = 3.;
    vec2 prev = vec2(uv.x-unit, signal(uv-vec2(unit,0), thin, freq1, freq2));
    vec2 next = vec2(uv.x+unit, signal(uv+vec2(unit,0), thin, freq1, freq2));
    vec2 tangent = normalize(next - prev);
    vec2 normal = vec2(tangent.y, -tangent.x);
    vec2 soundUV = vec2(abs(atan(normal.y, normal.x)/3.1459),0.);
    float bounce = texture(iChannel0, soundUV).r;
    freq1 = 4.+bounce*4.;

    vec2 uvText = (fragCoord-.5*iResolution.xy)*unit;
    uvText *= 0.1*vec2(2,1);
    vec2 uvTexture = 0.8*fragCoord/iResolution.xy;
    float lum = texture(iChannel2, uvTexture).r;
    float angle = lum * 3.14159 * 2. + time;
    vec2 offset = vec2(cos(angle), sin(angle));
    uv += offset*0.2*getText(uvText);

    uv.xy += normal * sin(uv.y*3.);
    float plotter1 = signal(uv, thin, freq1, freq2);
    float shade = 0.5+0.5*dot(normal, normalize(vec2(uv.x,plotter1)-(prev+next)/2.));

    thin = .25;
    freq1 = 4.+bounce*10.;
    freq2 = 8.+sin(time);
    float plotter2 = signal(uv, thin, freq1, freq2);

    color = mix(color, honey, clamp(plotter1,0.,1.));
    color = mix(color, honey, shade);
    color = mix(color, light, clamp(plotter2,0.,1.));

	fragColor = vec4(color,1.0);
}

#define CAMERA_DIST (0.7 + 2.0 * iMouse.x / iResolution.x)
#define CAMERA_Y ((-iMouse.y / iResolution.y + 0.5) * 5.0)

#define MARCH_DIST (sqrt(CAMERA_Y * CAMERA_Y + 2.0 * CAMERA_DIST * CAMERA_DIST))

#define INFTY 1e20
#define MARCH_ITERS 700
#define MAX_RAY_DIST 100.0
#define MARCH_EPSILON (0.0001 + 0.0001 * pow(MARCH_DIST, 3.0))
#define MBULB_ITERS 100
#define MBULB_BAILOUT 2.0
#define MBROT_POW 8.0

#define TAU 6.283185

float mbulb(in vec3 p) {
    vec3 z = p;
    float dr = 1.0;
    float r = 0.0;
    for (int i = 0; i < MBULB_ITERS; i++) {
        r = length(z);
        if (r > MBULB_BAILOUT) {
            break;
        }

        float theta = acos(z.z / r);
        float phi = atan(z.y, z.x);
        dr = pow(r, MBROT_POW - 1.0) * MBROT_POW * dr + 1.0;

        float zr = pow(r, MBROT_POW);
        theta *= MBROT_POW;
        phi *= MBROT_POW;
        z = zr * vec3(sin(theta) * cos(phi), sin(phi) * sin(theta), cos(theta));
        z += p;
    }
    return 0.5 * log(r) * r / dr;
}


float sdSphere(in vec3 p, in float s) {
    return length(p) - s;
}

float map(in vec3 p) {
    return mbulb(p);
}

float march(in vec3 ro, in vec3 rd) {
    float dist = INFTY;
    float totalDist = 0.0;
    for (int i = 0; i < MARCH_ITERS; i++) {
        if (totalDist > MAX_RAY_DIST
            || dist < MARCH_EPSILON) {
            break;
        }

        dist = map(ro);
        totalDist += dist;
        ro += dist * rd;
    }

    if (dist < MARCH_EPSILON) {
        return totalDist;
    }
    return INFTY;
}

vec3 normalAt(in vec3 ro) {
    vec2 EV = vec2(MARCH_EPSILON, 0.0);
    return normalize(vec3(map(ro + EV.xyy) - map(ro - EV.xyy),
                          map(ro + EV.yxy) - map(ro - EV.yxy),
                          map(ro + EV.yyx) - map(ro - EV.yyx)));
}

float occlusion(in vec3 p, in vec3 n, in float k) {
    float s = 0.0;
    float delta = 0.5;
    for (float i = 1.0; i <= 5.0; i++) {
        float c = (1.0 / pow(2.0, i)) *
                  (i * delta -
                   map(p + n * i * delta));
        s += clamp(c, 0.0, 1.0);
    }

    return 1.0 - k * s;
}

#define COLOR_MAP_INDEX 2
mat4 COLOR_MAPS[] = mat4[3](
  mat4(0.5, 0.5, 1.0, 0.00,
       0.5, 0.5, 1.0, 0.33,
       0.5, 0.5, 1.0, 0.66,
       0.0, 0.0, 0.0, 0.00),
  mat4(0.5, 0.5, 1.0, 0.30,
       0.5, 0.5, 1.0, 0.20,
       0.5, 0.5, 1.0, 0.20,
       0.0, 0.0, 0.0, 0.00),
  mat4(0.5, 0.5, 1.0, 0.00,
       0.5, 0.5, 1.0, 0.10,
       0.5, 0.5, 1.0, 0.20,
       0.0, 0.0, 0.0, 0.00)
);
vec3 colorAt(vec3 p) {
    float x = length(p);
    mat4 cmap = COLOR_MAPS[COLOR_MAP_INDEX];

    return vec3(
        cmap[0][0] + cmap[0][1] * cos(TAU * (cmap[0][2] * x + cmap[0][3])),
        cmap[1][0] + cmap[1][1] * cos(TAU * (cmap[1][2] * x + cmap[1][3])),
        cmap[2][0] + cmap[2][1] * cos(TAU * (cmap[2][2] * x + cmap[2][3])));
}

#define LIGHT_COUNT 1
vec3 getLight(in int index) {
    if (index == 0) {
        return vec3(0, 5.0, 0);
    }
    return vec3(5.0, 0, 0);
}

#define SPECULAR_POW 8.0
vec3 lighting(in vec3 ro, in vec3 rd, float dist) {
    vec3 n = normalAt(ro);
    vec3 V = -rd;
    float diffuse = 0.0;
    float specular = 0.0;

    for (int i = 0; i < LIGHT_COUNT; i++) {
        vec3 lp = getLight(i);
        vec3 L = normalize(lp - ro);
        vec3 R = reflect(-L, n);
        float dist = length(ro - lp);
        float intensity = 100.0 / (dist * dist);

        float shadow = march(lp, -L);
        if (length((-L * shadow) - ro) < MARCH_EPSILON) {
            break; // No hit, no lighting.
        }

        diffuse = clamp(diffuse + intensity * max(0.0, dot(L, n)), 0.0, 1.0);
        specular = clamp(specular + intensity * max(0.0, pow(dot(R, V), 8.0)), 0.0, 1.0);
    }

    return colorAt(ro)
         * vec3(diffuse + specular)
         * occlusion(ro, n, 1.0);
}

#define CAMERA_SPEED 0.1
void setupCamera(in vec2 uv, out vec3 ro, out vec3 rd) {
    ro = vec3(sin(iTime * CAMERA_SPEED) * CAMERA_DIST,
              (-iMouse.y / iResolution.y + 0.5) * 5.0,
              cos(iTime * CAMERA_SPEED) * CAMERA_DIST);
    vec3 target = vec3(0);
    vec3 up = vec3(0, 1, 0);

    vec3 cameraDir = normalize(target - ro);
    vec3 cameraRight = cross(cameraDir, up);
    vec3 cameraUp = cross(cameraRight, cameraDir);

    rd = normalize(uv.x * cameraRight + uv.y * cameraUp + cameraDir);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = ((fragCoord.xy / iResolution.xy) - vec2(0.5)) * 2.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 ro, rd;
    setupCamera(uv, ro, rd);
    float dist = march(ro, rd);
    if (dist == INFTY) {
        fragColor = vec4(.459, .69, .584, 1.0);
    } else {
        fragColor = vec4(lighting(ro + rd * dist, rd, dist), 1.0);
    }
}

float timeSpeed = 2.0;

float randomVal (float inVal)
{
    return fract(sin(dot(vec2(inVal, 2523.2361) ,vec2(12.9898,78.233))) * 43758.5453)-0.5;
}

vec2 randomVec2 (float inVal)
{
    return normalize(vec2(randomVal(inVal), randomVal(inVal+151.523)));
}

float makeWaves(vec2 uv, float theTime, float offset)
{
    float result = 0.0;
    float direction = 0.0;
    float sineWave = 0.0;
    vec2 randVec = vec2(1.0,0.0);
    float i;
    for(int n = 0; n < 16; n++)
    {
        i = float(n)+offset;
        randVec = randomVec2(float(i));
  		direction = (uv.x*randVec.x+uv.y*randVec.y);
        sineWave = sin(direction*randomVal(i+1.6516)+theTime*timeSpeed);
        sineWave = smoothstep(0.0,1.0,sineWave);
    	result += randomVal(i+123.0)*sineWave;
    }
    return result;
}

