    col = mix( vec3(sin(iTime)+.3,sin(iTime)+.6,.9), col, 0.5 );

	fragColor = vec4(col,1.0);
}

mat2 rz2(float a) {
    float c = cos(a); 
    float s = sin(a);
    return mat2(c, -s, s, c);
}

float map(vec3 p) {
    p.xy *= rz2(iTime * .3);
    p.yz *= rz2(iTime * .4);

    float freq = texture(iChannel0, vec2(.02, .25)).r;
    p = mod(p + 1., 2.) - 1.;
    return length(p) - (.1 + .4*freq); // (mod(freq, .4) + .2) ;
}

