    vec3 rd=normalize(vec3(uv, -1.));

    vec3 mp=ro;
    for (int i=0; i<50; ++i) {
     	float md = map(mp);
        if (md < .0001) break;
        mp += rd * md;
    }

    fragColor = vec4(length(mp - ro) * 0.1);
