void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv * 2.0 - 1.0;

    float ttx = abs(nuv.x * 0.5);
    float tt = texture(iChannel0, vec2(ttx, 0.25)).x;
    float ttf = max(pow(tt, 2.0) - 0.15, 0.0);

    float ttfinal = ttf * abs(nuv.y * 0.5) * step(0.0 - nuv.y, 0.0);

    float t = iTime;

    float ground = step(nuv.y + 0.3 + sin(uv.x * 14.0 + 0.3 * t) * 0.02, 0.0);
    float iground = 1.0 - ground;

    vec2 groundUV = vec2(sin(uv.x * 14.0 + 0.3 * t) * 0.02, uv.y);
    vec3 groundColor = vec3(0.9, 0.8, 0.3) * max(pow(uv.y * 12.0, 0.15) * 1.0, 0.5);
    vec3 groundNoiseColor = vec3(1, 0, 0);
    vec2 groundNoiseUV = vec2(mod(t * 0.8 + uv.x, 1.0), mod(t * 2.2 + uv.y, 0.2));
    vec3 groundNoise = texture(iChannel1, groundNoiseUV).xyz;

    groundNoise.x = pow(groundNoise.x - 0.1, 12.0);

    vec3 bg = vec3(0.2, 0.2, 0.4);

    fragColor.xyz = bg * iground;
    fragColor.xyz += vec3(ttfinal) * vec3(3.0, 2.0, 0.0) * pow((uv.y + 0.5) * 1.6, 4.0) * (uv.y * 0.1);

    fragColor.xyz += ground * groundColor;

    //fragColor.xyz += vec3(0,5.0,0) * iground * groundNoise.x;
    //fragColor.xyz += vec3(1,1,1) * ground * groundNoise.x;
    fragColor.xyz += vec3(1,1,1) * iground * 0.2 * ttf;
    if (iground > 0.9)
    fragColor.xyz += vec3(11,11,222) * groundNoise.x * (0.1 + 0.2 * ttf) * iground;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
