	fragColor = vec4( col, 1.0 );
}

const vec3 u_samples[] = vec3[](
    vec3(0.1, 0.1, 17.0),
    vec3(0.9, 0.5, 13.0),
    vec3(0.7, 0.3, 9.0),
    vec3(0.1, 0.9, 19.0),
    vec3(0.4, 0.2, 9.0),
    vec3(0.3, 0.5, 29.0),
    vec3(0.4, 0.4, 39.0),
    vec3(0.5, 0.7, 12.0)
);

float w_i(vec2 x_i, vec2 x, float p) {
    return 1.0 / pow(distance(x, x_i), p);
}

float u(vec2 x, float p) {
    for(int i = 0; i < u_samples.length(); i++) {
        if(distance(x, u_samples[i].xy * iResolution.xy) <= 1.0) {
            return u_samples[i].z;
        }
    }
    float tmp1 = 0.0;
    float tmp2 = 0.0;
    for(int i = 0; i < u_samples.length(); i++) {
        float wi = w_i(u_samples[i].xy * iResolution.xy, x, p);
        tmp1 += (wi * u_samples[i].z);
        tmp2 += wi;
    }
    return tmp1 / tmp2;
}

float sampleValueMax() {
    float m = 0.0;
    for(int i = 0; i < u_samples.length(); i++) {
        if(m < u_samples[i].z) {
            m = u_samples[i].z;
        }
    }
    return m;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	float uv = u(fragCoord, 9.0+7.0*sin(iTime));
    float maxValue = sampleValueMax();
	fragColor = vec4(uv/maxValue * 0.9, uv/maxValue * 0.6, 1.0 - 0.9*uv/maxValue, 1.0);
}

//forked from https://www.shadertoy.com/view/llfSzH

void mainImage( out vec4 f, vec2 u )
{
    float t = iTime*.3;
    float s = 1.;
    //u.x+=100.;
    vec3 r = vec3(cos(t*s*1.)*3. + s*.5, s*.5,sin(t*s*1.)) + s*.5,
         R = iResolution ;

    u-= R.xy*.5;
    float d = length(u/R.y)*2.;
    float a = sin(t*.1);
    u*= mat2(d,a,-a,d);
    u+=R.xy*.5;

    for( float i = .7; i > .1 ; i-=.009 ) {
        r += vec3( (u+u-R.xy)/R.y, 1) *.5
             * ( f.a = length( mod(r,s) - (s*.5) )+sin(mod(r.z*r.x*r.y,s))*.2 - .3 ) ;
        float aa = atan(r.y,r.z);
        float dd = length(r.zy);
        aa += r.x*.01;

        r.y = sin(aa)*dd;
        r.z = cos(aa)*dd;
        f.bgr=abs(sin(vec3(i)));
        if( f.a < .001 ) break ;
    }
    f.rgb = sin(t+(f.rgb+vec3(0.0,.33,.66))*6.)*.5+.5;

}

#define AA 1
#define zoom 0.5
#define yShift -0.25

const int ITS = 35;
const float pi = 3.1415926536;
const vec2 c1 = vec2(0, 1);
const vec2 c2 = vec2(.866, -.866);
const vec2 c3 = vec2(-.866, -.866);
const float rad1 = 1.0;

vec2 circleInverse(vec2 pos, vec2 center, float rad){
    vec2 d = pos - center;
	return d * rad * rad/dot(d, d) + center;
}

vec3 gasket(vec2 pos){
    float rad2 = iMouse.x / iResolution.x + .5;
	float rad3 = iMouse.y / iResolution.y + .5;
    if(iMouse.xy == vec2(0.)) {rad2 = 1.; rad3 = 1.;}
    float index = 0.;
	for(int i = 0 ; i < ITS; i++){
		if(distance(pos, c1) < rad1){
			pos = circleInverse(pos, c1, rad1);
            index++;
		}
        else if(distance(pos, c2) < rad2){
			pos = circleInverse(pos, c2, rad2);
            index++;
		}
        else if(distance(pos, c3) < rad3){
			pos = circleInverse(pos, c3, rad3);
            index++;
		}
        else if(pos.y < 0.){
			pos = vec2(pos.x, -pos.y);
            index++;
		}
        else return vec3(pos, index);
	}
}

vec3 render(vec2 p){
    p = zoom*(2.0*p - iResolution.xy)/iResolution.y;
    p.y += yShift;
    vec3 n = gasket(p);
    float s = 0.08 * (4.0-length(n.xy)) + n.z;
    if (n.z > 44.0)return vec3(0);
    float arg = pi * s / 20. + iTime;
    vec3 col = sin(arg - vec3(0.5*pi, pi, 0.666666*pi)) * 0.5 + 0.5;
    return col*col;
}

