void mainImage(out vec4 fragColor,vec2 fragCoord)
{
    float m 		= min(iResolution.x,iResolution.y);
	vec2 uv 		= (fragCoord.xy / m - vec2(0.5,0.5))*2.5;
    vec3 P 			= vec3(uv,-5.0);
    vec3 d      	= vec3(0.0,0.0,1.0);

    float alpha1	= iTime*TWOPI/11.0;
    float alpha2	= iTime*TWOPI/5.0;
    P.xy		   *= ROTATION(alpha2);
    P.zx		   *= ROTATION(alpha1);
    d.xy		   *= ROTATION(alpha2);
    d.zx 		   *= ROTATION(alpha1);

	fragColor 		= compute(P,d);

} // mainImage()

