//mirror changes over time (moves or rotates or both)
#define MirrorMove
#define MirrorRotate
//the mirror image is offset to the side like 2 footpriints (transflection==https://en.wikipedia.org/wiki/Glide_reflection)
#define transflectOffset -1.5

/*
//pMod       repeats space, f(x)=x -> fract(x)       =   seesaw-wave
//pModMirror repeats space, f(x)=x -> fract(x)*2.-1. = triangle-wave
//pMod* functions inout-project p into a tile via fract(), with offset,
//... and return a tileIDoffset that is "floor()", with offset
float pMod(inout float p,float s){float c=floor((p/s)+.5);p=(fract((p/s)+.5)-.5)*s;return c;}
float pModMirror(inout float p,float s){float c=pMod(p,s);p*=mod(c,2.)*2.-1.;return c;}
vec2 pMod(inout vec2 p,vec2 s){vec2 c=floor((p/s)+.5);p=(fract((p/s)+.5)-.5)*s;return c;}
vec2 pModMirror(inout vec2 p,vec2 s){vec2 c=pMod(p,s);p*=mod(c,2.)*2.-1.;return c;}
//http://mercury.sexy/hg_sdf/
*/

