//return mirror duplicate of [m]mousePos and [u]FragmentPos on axis y=0;
void mirror(inout vec2 u,inout vec2 m){m.y=abs(m.y);u.y=abs(u.y);}
//mirror(), but the mirror immage is offset by [o]
void transflect(inout vec2 u, inout vec2 m,float o){
 if(m.y<0.){u.x+=step(0.,u.y)*o;}//if (sign(u.y)>0.)u.x+=o;}
 else u.x+=step(u.y,0.)*o;//if (sign(u.y)<0.)u.x+=o;
 mirror(u,m);}

//return distance to a, asymmetric glyph, to tell what symmetry transformatiosns are happening.
float AsymmetricGlyph(vec2 p){p.y=-p.y;p+=.5;
 float a=min(length(p),length(p-.4)*.5);
 p.y+=.2;return max(a,-length(p)+.5);}//the claw! (i was in a hurry)

//single reflection along a half rotated plane -> SINGLE rotation. Applying this matrix twice just gets you mat2(1)
#define r2(r) mat2(sin(r+vec4(1,0,0,-1)*asin(1.)))
//rotate p by angle r: p=r(p,r);
#define r(p,r) (cos(r)*p+sin(r)*vec2(-p.y,p.x))
//vec2 r(vec2 p,float t){return cos(r)*p+sin(r)*vec2(-p.y,p.x);
//}//from https://www.shadertoy.com/view/XlfGzH

#define frame(u) (u-.5*iResolution.xy)/iResolution.y
void mainImage(out vec4 O,in vec2 U){vec2 u=frame(U)*6.;
 vec2 m;
 if (iMouse.z<0.) m=-vec2(2,1);
 else m=frame(iMouse.xy)*6.;
 float time=iTime*.2;
 #ifdef MirrorMove
  float t=sin(time)*2.;u.y+=t;m.y+=t;//move mirror over time.
 #endif
 #ifdef MirrorRotate
  u.x-=.4;m.x-=.4;
  mat2 a=r2(time*.61);m*=a;u*=a;//rotate mirror over time
 #endif
 bool isMitrrored=(sign(m.y)!=sign(u.y));//if(fragment is in mirror image)
 transflect(u,m,transflectOffset);
 float d=AsymmetricGlyph(m-u);
 //d=length(m-u);//simpler less symmetrical distance to mouse position.
 d=fract(d*3.1);//seesaw first derivative, contrast illusion.
 u=fract(u+.02);//shows that the whole domain is transflect-ed, and small offset shows the mirror
 if(isMitrrored)u*=.4;
 u.x*=.7;//less green = better dichromacy contrast.
 O=vec4(d,u,1.);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 position = (fragCoord.y - iResolution.xy );

    float color = 0.33;
	color += sin(position.x * position.y) ;
	color -= sin(iTime)* cos(sin(iTime)*position.y*position.x*sin(position.x))+2.1;
	color += sin(iTime)*position.x*sin(position.y*sin(sin(tan(cos (iTime)))));
	fragColor = vec4( vec3(sin(color-color)*6.0, sin(color+color) , color )+sin(iTime*position.x*(iTime*0.14)),iTime*0.828 );

}

