// TILES controls how many tiles we see!
#define TILES 8.

// Compute the norm-squared transform of our original (straight) coordinate system!
vec2 norm_squared(vec2 st){
  //st *= 2. * (sin(iTime) + 1.1);  // Animate zoom!
  return vec2(-st.t, st.s) / dot(st, st);  // `dot(st, st)` is the norm-squared of the vector `st`
}

// Compute antialias! I don't understand how it works, at all
vec2 antialias(vec2 st){
  vec2 sine_st = sin(st);
  vec2 jacobian = fwidth(sine_st) * 0.7071;  // Jacobian!
  vec2 aa_st = smoothstep(-jacobian, jacobian, sine_st);  // Map smoothly to [0;1]
  return 2. * aa_st - 1.;  // Remap to [-1;1]
}

// Compute an antialiased checkerboard!
// https://www.shadertoy.com/view/Md2XWt
float checkerboard(vec2 st){
  st *= TILES;  // Expand the st-plane to see more tiles!
  st.s += -.4 * iTime;  // Move the tiles along the u-direction, just for teh lulz
  vec2 aa_st = antialias(st);
  float rgb = .5 * aa_st.s * aa_st.t + .56;  // Combine sine waves and remap to [0;1]
  return clamp(rgb, .0, 1.);  // Actually ensure we've remapped to [0;1]...
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
  vec2 st = (2. * fragCoord - iResolution.xy) / iResolution.y;  // Normalize pixel coordinates!
  st = norm_squared(st);  // Comment this out to see a plain grid!
  float rgb = checkerboard(st);

  fragColor.rgb = vec3(rgb) * .94;
}

// "Foggy Duck" by dr2 - 2017
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

float PrSphDf (vec3 p, float s);
float PrEllipsDf (vec3 p, vec3 r);
float PrEllCylDf (vec3 p, vec2 r, float h);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
float Fbm3 (vec3 p);
vec3 VaryNf (vec3 p, vec3 n, float f);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
vec3 VtRot (vec3 p, vec3 a);
vec3 HsvToRgb (vec3 c);

vec3 ltDir, qnBall;
vec2 qBlk;
float tCur, dstFar, rBall, cldFac;
int idObj;
const int idBdy = 1, idWng = 2, idHead = 3, idBk = 4, idEye = 5;
const float pi = 3.14159;

float CloudDens (vec3 p)
{
  return clamp ((Fbm3 (2.5 * p) - 0.2) / (1. - 0.2), 0., 1.);
}

vec3 FogCol (vec3 col, vec3 ro, vec3 rd, float dHit, vec2 bDist)
{
  vec3 p, ps;
  const vec3 cCld = vec3 (1.2, 1.2, 1.1), cLt = vec3 (1.3, 1.3, 1.);
  float d, f, db, fs, ds, t;
  int ii;
  ro = VtRot (ro, vec3 (0.02, 0.033, 0.044));
  rd = VtRot (rd, vec3 (0.02, 0.033, 0.044));
  db = rBall / 20.;
  d = bDist.x;
  ii = 0;
  for (int i = 0; i < 20; i ++) {
    d += db;
    if (d > min (bDist.y, dHit)) break;
    p = ro + d * rd;
    f = CloudDens (p) * (1. - smoothstep (0.9 * rBall, rBall, length (p))) *
       smoothstep (0., 0.1 * rBall, dHit - d);
    if (ii == 0) {
      fs = 0.;
      if (f > 0.) {
        ds = 0.;
        ps = p + (3./2.) * db * rd;
        for (int j = 0; j < 4; j ++) {
          ds += 3. * db;
          ps -= ds * ltDir;
          t = length (ps);
          if (t > rBall) break;
          fs += CloudDens (ps) * (1. - smoothstep (0.9 * rBall, rBall, t));
        }
      }
    }
    col = mix (col, mix (cCld, cLt, fs), clamp (cldFac * f, 0., 1.));
    if (++ ii == 3) ii = 0;
  }
  return col;
}

float ObjDf (vec3 p)
{
  vec3 q;
  vec2 r;
  const vec2 cs = vec2 (cos (0.3), sin (0.3));
  float dMin, d, h, s;
  dMin = dstFar;
  p -= vec3 (0., -0.3, -0.1);
  h = 0.5;
  r = vec2 (0.2, 0.3 + 0.05 * clamp (p.z, -2., 2.));
  s = (length (p.xz / r) - 1.) * min (r.x, r.y);
  d = min (max (s, abs (p.y) - h), length (vec2 (s, p.y)) - h);
  if (d < dMin) { dMin = d;  idObj = idBdy; }
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.5, 0.3, 0.6);
  q.yz = Rot2Cs (q.yz, cs);
  q.xy = Rot2Cs (q.xy, vec2 (cs.x, - cs.y));
  q.xz = Rot2Cs (q.xz, vec2 (cs.x, - cs.y));
  r = vec2 (0.3, 0.5 + 0.08 * clamp (q.z, -2., 2.));
  h = 0.07;
  s = (length (q.yz / r) - 1.) * min (r.x, r.y);
  d = SmoothMin (min (max (s, abs (q.x) - h), length (vec2 (s, q.x)) - h), dMin, 0.05);
  if (d < dMin) { dMin = d;  idObj = idWng; }
  d = SmoothMin (PrEllipsDf (p - vec3 (0., 0.75, -0.4), vec3 (0.4, 0.35, 0.5)),
     dMin, 0.1);
  if (d < dMin) { dMin = d;  idObj = idHead; }
  q = p - vec3 (0., 0.65, -0.9);
  q.zy = Rot2Cs (q.zy, vec2 (cs.x, - cs.y));
  h = 0.15;
  r = vec2 (0.15, 0.04) * (1. - 0.1 * min (2., max (0., 1. - q.z / h)));
  d = PrEllCylDf (q, r, h);
  q.z -= 0.9 * h;
  d = SmoothMin (max (d, - PrEllCylDf (q, r - 0.02, 2. * h)), dMin, 0.01);
  if (d < dMin) { dMin = d;  idObj = idBk; }
  q = p;
  q.x = abs (q.x);
  d = PrSphDf (q - vec3 (0.245, 0.825, -0.6), 0.125);
  if (d < dMin) { dMin = d;  idObj = idEye; }
  return 0.9 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 100; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  const vec3 e = vec3 (0.0001, -0.0001, 0.);
  vec4 v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy),
     ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float MarbVol (vec3 p)
{
  vec3 q;
  float f;
  f = 0.;
  p *= 0.7;
  q = p;
  for (int j = 0; j < 5; j ++) {
    q = abs (q) / dot (q, q) - 0.89;
    f += 1. / (1. + abs (dot (p, q)));
  }
  return f;
}

vec3 DukMarb (vec3 ro, vec3 rd)
{
  vec3 col;
  float t;
  col = vec3 (0.);
  ro = VtRot (ro, - vec3 (0.03, 0.022, 0.026));
  rd = VtRot (rd, - vec3 (0.03, 0.022, 0.026));
  t = 0.;
  for (int j = 0; j < 32; j ++) {
    t += 0.02;
    col = mix (HsvToRgb (vec3 (mod (0.4 * MarbVol (ro + t * rd) +
       0.03 * pi * tCur, 1.), 1., 1. / (1. + t))), col, 0.95);  
  }
  return clamp (col, 0., 1.);
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.0005;
  for (int j = 0; j < 50; j ++) {
    h = ObjDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += 0.01;
    if (sh < 0.03) break;
  }
  return sh;
}

vec2 BallHit (vec3 ro, vec3 rd)
{
  float dbIn, dbOut, b, d;
  b = dot (rd, ro);
  d = b * b + rBall * rBall - dot (ro, ro);
  dbIn = dstFar;
  dbOut = dstFar;
  if (d > 0.) {
    d = sqrt (d);
    dbIn = - b - d;
    dbOut = - b + d;
    qnBall = (ro + dbIn * rd) / rBall;
  }
  return vec2 (dbIn, dbOut);
}

void BlkHit (vec3 ro, vec3 rd)
{
  vec3 v, tm, tp, u, qnBlk;
  float dMin, dn, df, bSize;
  bSize = 2.;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = max (max (tm.x, tm.y), tm.z);
  df = min (min (tp.x, tp.y), tp.z);
  if (df > 0. && dn < df) {
    qnBlk = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
    u = (v + dn) * rd;
    qBlk = vec2 (dot (u.zxy, qnBlk), dot (u.yzx, qnBlk));
  }
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn, vnp, roo, q;
  vec2 bDist;
  const vec2 cs = vec2 (cos (0.3), sin (0.3));
  float dstObj, ltDotVn, sh, glit, fr, rInd;
  cldFac = 0.05 + 0.04 * sin (0.14 * pi * tCur);
  rInd = 1. / (2.5 - 1.4 * cos (0.03 * pi * tCur));
  roo = ro;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += rd * dstObj;
    vn = ObjNf (ro);
    q = ro - vec3 (0., -0.3, -0.1);
    if (idObj != idEye) {
      col = DukMarb (ro, refract (rd, vn, rInd));
      if (idObj == idBdy) {
        col *= 1. - smoothstep (0.1, 0.3, ro.y) * smoothstep (0., 0.7, ro.z) * 0.2 *
           SmoothBump (0.3, 0.5, 0.05, mod (20. * ro.x, 1.));
      } else if (idObj == idWng) {
        q.x = abs (q.x);
        q -= vec3 (0.5, 0.3, 0.6);
        q.yz = Rot2Cs (q.yz, cs);
        q.xy = Rot2Cs (q.xy, vec2 (cs.x, - cs.y));
        q.xz = Rot2Cs (q.xz, vec2 (cs.x, - cs.y));
        col *= 1. - step (0.02, q.x) * smoothstep (0., 0.2, q.z) * 0.2 *
           SmoothBump (0.3, 0.5, 0.05, mod (30. * q.y, 1.));
      } else if (idObj == idBk) {
        col = mix (vec3 (0.6, 0.6, 0.9) * max (0.7 - 0.3 * dot (rd, vn), 0.), col,
          smoothstep (-1.1, -0.97, ro.z));
      }
    } else {
       col = mix (vec3 (0.1), vec3 (1., 0.8, 0.2) * max (0.7 - 0.3 * dot (rd, vn), 0.),
         smoothstep (0.02, 0.04, length (q.yz - vec2 (0.865, -0.645) +
         vec2 (0., 0.017 * sin (0.6 * pi * tCur)))));
    }
    sh = ObjSShadow (ro, ltDir);
    ltDotVn = max (0., dot (vn, ltDir));
    vnp = VaryNf (1000. * ro, vn, 1.5);
    glit = 300. * step (0.01, ltDotVn) *
       pow (max (0., dot (ltDir, reflect (rd, vn))), 16.) *
       pow (1. - 0.6 * abs (dot (normalize (ltDir - rd), vnp)), 8.);
    col = 0.8 * col + (1. - 10. * cldFac) * sh * vec3 (1., 1., 0.5) * (glit +
       0.2 * pow (max (dot (normalize (ltDir - rd), vn), 0.), 256.));
    fr = pow (1. - abs (dot (rd, vn)), 5.);
    col = mix (col, vec3 (0.5) * (0.7 + 0.3 * reflect (rd, vn).y), fr);
  } else {
    dstObj = dstFar;
    col = vec3 (0.1, 0.1, 0.3);
  }
  bDist = BallHit (roo, rd);
  if (bDist.x < dstObj) {
    col = FogCol (col, roo, rd, dstObj, bDist);
    fr = pow (1. - abs (dot (rd, qnBall)), 3.);
    BlkHit (roo + bDist.x * rd, reflect (rd, qnBall));
    qBlk = step (0.1, mod (2. * qBlk + 0.1/2., 1.));
    col = mix (col, vec3 (0.5, 0.5, 0.6) * (0.5 + 0.5 * qBlk.x * qBlk.y), fr);
    col += 0.005 + 0.05 * max (dot (ltDir, qnBall), 0.) +
       0.2 * pow (max (dot (normalize (ltDir - rd), qnBall), 0.), 64.);
  }
  col = pow (clamp (col, 0., 1.), vec3 (0.8));
  return col;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float az, el;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  if (mPtr.z > 0.) {
    az = 3. * pi * mPtr.x;
    el = 1. * pi * mPtr.y;
  } else {
    az = 0.6 * pi * sin (0.03 * pi * tCur);
    el = -0.1 * pi + 0.1 * pi * sin (0.05 * pi * tCur);
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  rd = vuMat * normalize (vec3 (uv, 4.8));
  ro = vuMat * vec3 (0., 0., -8.);
  rBall = 1.5;
  dstFar = 20.;
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrSphDf (vec3 p, float s)
{
  return length (p) - s;
}

float PrEllipsDf (vec3 p, vec3 r)
{
  return (length (p / r) - 1.) * min (r.x, min (r.y, r.z));
}

float PrEllCylDf (vec3 p, vec2 r, float h)
{
  return max ((length (p.xy / r) - 1.) * min (r.x, r.y), abs (p.z) - h);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 VtRot (vec3 p, vec3 a)
{
  a *= pi * tCur;
  p.yz = Rot2D (p.yz, a.x);
  p.zx = Rot2D (p.zx, a.y);
  p.xy = Rot2D (p.xy, a.z);
  return p;
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

const vec4 cHashA4 = vec4 (0., 1., 57., 58.);
const vec3 cHashA3 = vec3 (1., 57., 113.);
const float cHashM = 43758.54;

vec4 Hashv4f (float p)
{
  return fract (sin (p + cHashA4) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec4 t;
  vec2 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv4f (dot (ip, cHashA3.xy));
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Noisefv3 (vec3 p)
{
  vec4 t1, t2;
  vec3 ip, fp;
  float q;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  q = dot (ip, cHashA3);
  t1 = Hashv4f (q);
  t2 = Hashv4f (q + cHashA3.z);
  return mix (mix (mix (t1.x, t1.y, fp.x), mix (t1.z, t1.w, fp.x), fp.y),
              mix (mix (t2.x, t2.y, fp.x), mix (t2.z, t2.w, fp.x), fp.y), fp.z);
}

float Fbm3 (vec3 p)
{
  float f, a, s;
  f = 0.;
  a = 1.;
  s = 0.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv3 (p);
    s += a;
    a *= 0.5;
    p = 2. * p.yzx;
  }
  return f / s;
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  float s;
  const vec3 e = vec3 (0.1, 0., 0.);
  s = Fbmn (p, n);
  g = vec3 (Fbmn (p + e.xyy, n) - s, Fbmn (p + e.yxy, n) - s,
     Fbmn (p + e.yyx, n) - s);
  return normalize (n + f * (g - n * dot (n, g)));
}

#define PI 3.14159265359

#define FREQ 8
vec4 values[FREQ];

float sdBox( vec3 p, vec3 b ){
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

mat2 rot(in float a) {
	return mat2(cos(a),sin(a),-sin(a),cos(a));	
}

// main distance function
float de(vec3 p) {

    //p = mod(p, vec3(20.0)) - vec3(10.0);


    float de = length(p) - 5.0;
    de = sdBox(p, vec3(5.0))-0.3;

    for (int i = 0 ; i < FREQ ; i++) {
        float f = float(i) / float(FREQ-1);
        vec4 value = values[i];

        float theta = f * PI * 1.5;
        vec3 dir = vec3(cos(theta), sin(theta), 0);
        dir.yz *= rot(f*PI*1.0);

        float v = dot(p,dir);

        de += sin(v*2.0*pow(2.0, f) + iTime*1.0)*0.25*value.x;
    }

	return de;
} 

// normal function
vec3 normal(vec3 p) {
	vec3 e = vec3(0.0, 0.001, 0.0);
	return normalize(vec3(
		de(p+e.yxx)-de(p-e.yxx),
		de(p+e.xyx)-de(p-e.xyx),
		de(p+e.xxy)-de(p-e.xxy)));	
}

vec4 myTexture(vec2 texCoord) {
	//return texture(iChannel0, texCoord);
	texCoord *= 5.0;
	vec2 f = fract(texCoord)-vec2(0.5);
	float v = smoothstep(0.0, 0.75, length(f));
	return vec4(mix(vec3(0, 1, 0), vec3(1, 1, 0), v), 1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
	uv.y *= iResolution.y / iResolution.x;

	vec3 from = vec3(-50, 0, 0);
	vec3 dir = normalize(vec3(uv*0.2, 1.0));
	dir.xz *= rot(3.1415*.5);

	vec2 mouse=(iMouse.xy / iResolution.xy - 0.5) * 0.5;
	if (iMouse.z < 1.0) mouse = vec2(0.0);

	mat2 rotxz = rot(iTime*0.0652+mouse.x*5.0);
	mat2 rotxy = rot(0.3-mouse.y*5.0);

	from.xy *= rotxy;
	from.xz *= rotxz;
	dir.xy  *= rotxy;
	dir.xz  *= rotxz;

	float totdist = 0.0;
	bool set = false;
	vec3 norm = vec3(0);

    //fragColor = myTexture(uv);
	//return;


    for (int i = 0 ; i < FREQ ; i++) {
        float f = float(i) / float(FREQ-1);
        values[i] = texture(iChannel0, vec2(f, 0));
		//values[i] = texture( vec2(uFloatAvgFreq,uFloatAvgFreq) , vec2(f, 0));
		//values[i] = myTexture(vec2(f, 0));
    }

	for (int steps = 0 ; steps < 200 ; steps++) {
		if (set) continue;
		vec3 p = from + totdist * dir;

		float dist = de(p)*0.5;

		totdist += dist;
		if (dist < 0.01) {
			set = true;
		}
	}

    vec3 p = from + totdist * dir;
    norm = normal(p);

    if (set) {

        vec3 light = normalize(vec3(-1, 2, 3));
        float l = max(dot(norm, light), 0.0);
       fragColor = vec4(vec3(l), 1.0);
        fragColor.rgb = norm*0.5+0.5;
    } else {
        fragColor = vec4(0, 0.2, 0, 1);

    }




}

