#define N_TILES_Y 2.
#define THICKNESS .008

float draw_grid(vec2 uv){
    vec2 dif  = fract(uv * vec2(N_TILES_Y));
    vec2 grid_2D = floor(dif + vec2(THICKNESS * N_TILES_Y));
    return floor(grid_2D.x + grid_2D.y);

}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;

    // Rotation: left-multiplication by a unit complex-number!
