    float grid = draw_grid(uv);
    fragColor.rgb = vec3(grid);
}

#define AO_DISTANCE (1.0)
#define AO_STEPS_COUNT (6)
#define AO_RAYCOUNT (512)
#define PI (3.14159265359)
#define PI_3 (1.0472)
#define PI_6 (PI / 6.0)
#define PI_8 (0.7853981 * 0.5)
#define TWOPI (6.283185307)
#define CAPSULE_RADIUS (0.09)

//#define VS_CAPSULEAO


vec2 hash2( float n ) { return fract(sin(vec2(n,n+1.0))*vec2(43758.5453123,22578.1459123)); }

vec4 hash4( vec2 p ) { return fract(sin(vec4( 1.0+dot(p,vec2(37.0,17.0)), 
                                              2.0+dot(p,vec2(11.0,47.0)),
                                              3.0+dot(p,vec2(41.0,29.0)),
                                              4.0+dot(p,vec2(23.0,31.0))))*103.0); }

vec3 mul(mat3 m, vec3 v)
{
    return m * v;
}

// intersect capsule
float capIntersect( in vec3 ro, in vec3 rd, in vec3 pa, in vec3 pb, in float r )
{
    vec3  ba = pb - pa;
    vec3  oa = ro - pa;

    float baba = dot(ba,ba);
    float bard = dot(ba,rd);
    float baoa = dot(ba,oa);
    float rdoa = dot(rd,oa);
    float oaoa = dot(oa,oa);

    float a = baba      - bard*bard;
    float b = baba*rdoa - baoa*bard;
    float c = baba*oaoa - baoa*baoa - r*r*baba;
    float h = b*b - a*c;
    if( h>=0.0 )
    {
        float t = (-b-sqrt(h))/a;

        float y = baoa + t*bard;

        // body
        if( y>0.0 && y<baba ) return t;

        // caps
        vec3 oc = (y<=0.0) ? oa : ro - pb;
        b = dot(rd,oc);
        c = dot(oc,oc) - r*r;
        h = b*b - c;
        if( h>0.0 )
        {
            return -b - sqrt(h);
        }
    }
    return -1.0;
}

float sdPlane( vec3 p )
{
	return p.y;
}

float sdSphere( vec3 p, float s )
{
    return length(p)-s;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
	vec3 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h ) - r;
}

float sdCylinder( vec3 p, vec3 c )
{
  return length(p.xz-c.xy)-c.z;
}

vec2 opUnion( vec2 d1, vec2 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}

float iPlane( in vec3 ro, in vec3 rd )
{
    return (-1.0 - ro.y)/rd.y;
}

vec2 map(in vec3 pos)
{
    vec2 dm = vec2(10000.0, -1.0);

    dm = vec2(sdPlane(pos), 1.0);

    for(int i=0; i<4; ++i)
    {
        float fi = float(i);     

        float x = float(i - ((i/4)*4));
        float y = float(i/4) * 1.0;    	

        dm = opUnion( dm, vec2(sdCapsule(pos,vec3(x - 2.25,y+1.25 + sin(iTime + fi),-0.5), 
                                             vec3(x - 0.75,y+1.25 + cos(iTime + fi), 1.0),
                                         0.1 + CAPSULE_RADIUS * fi), 4.0 ));
    }       

    return dm;
}

vec2 castRay( in vec3 ro, in vec3 rd )
{
    float tmin = 0.01;
    float tmax = 20.0;

	float precis = 0.002;
    float t = tmin;
    float m = -1.0;
    for( int i=0; i<50; i++ )
    {
	    vec2 res = map( ro+rd*t );
        if( res.x<precis || t>tmax ) break;
        t += res.x;
	    m = res.y;
    }

    if( t>tmax ) m=-1.0;
    return vec2( t, m );
}

vec3 calcNormal( in vec3 pos )
{
	vec3 eps = vec3( 0.001, 0.0, 0.0 );
	vec3 nor = vec3(
	    map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	    map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	    map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
}

float raytracedAO(in vec3 pos, in vec3 normal, in vec4 random, in float maxDistance)
{
    vec3  ru  = normalize( cross( normal, vec3(0.0,1.0,1.0) ) );
    vec3  rv  = normalize( cross( ru, normal ) );    

    float occ = 0.0;
    for( int i=0; i<AO_RAYCOUNT; i++ )
    {
        vec2  aa = hash2( random.x + float(i)*203.1 );
        float ra = sqrt(aa.y);
        float rx = ra*cos(6.2831*aa.x); 
        float ry = ra*sin(6.2831*aa.x);
        float rz = sqrt( 1.0-aa.y );
        vec3  dir = vec3( rx*ru + ry*rv + rz*normal );      

        for(int u=0; u<4; ++u)
        {
            float fi = float(u);     

            float x = float(u - ((u/4)*4));
            float y = float(u/4) * 1.0;    	

            float distance = capIntersect(pos, normalize(dir), vec3(x - 2.25,y+1.25 + sin(iTime + fi),-0.5), 
                                             vec3(x - 0.75,y+1.25 + cos(iTime + fi), 1.0),
                                         0.1 + CAPSULE_RADIUS * fi);

            if (distance > 0.01)                           
            {
                occ+= 1.0;
                break;
            }
        }                
    }
    occ /= float(AO_RAYCOUNT);

    return clamp(1.0 - occ, 0.0, 1.0);
}


// https://www.shadertoy.com/view/lsjXDc
float acosFast( float y )
{
    float x = abs( clamp(y,-1.0,1.0) );
    float z = (-0.168577*x + 1.56723) * sqrt(1.0 - x);
    return mix( 0.5*3.1415927, z, sign(y) );
}

vec3 closestPointOnLine(in vec3 a, in vec3 b, in vec3 point)
{
    vec3 ab = b - a;
    float t = dot(point-a, ab) / dot(ab, ab);
    return a + t * ab;
}

vec3 closestPointOnSegment(in vec3 a, in vec3 b, in vec3 point)
{
    vec3 ab = b - a;
    float t = dot(point-a, ab) / dot(ab, ab);
    return a + clamp(t, 0.0, 1.0) * ab;
}    

float Fpo(float d, float l)
{
    return l/(d*(d*d + l*l)) + atan(l/d)/(d*d);
}

float Fwt(float d, float l)
{
    return l*l/(d*(d*d + l*l));
}

float I_diffuse_line(vec3 p1, vec3 p2)
{
    // tangent
    vec3 wt = normalize(p2 - p1);

    // clamping
    if (p1.z <= 0.0 && p2.z <= 0.0) return 0.0;
    if (p1.z < 0.0) p1 = (+p1*p2.z - p2*p1.z) / (+p2.z - p1.z);
    if (p2.z < 0.0) p2 = (-p1*p2.z + p2*p1.z) / (-p2.z + p1.z);

    // parameterization
    float l1 = dot(p1, wt);
    float l2 = dot(p2, wt);

    // shading point orthonormal projection on the line
    vec3 po = p1 - l1*wt;

    // distance to line
    float d = length(po);

    // integral
    float I = (Fpo(d, l2) - Fpo(d, l1)) * po.z +
              (Fwt(d, l2) - Fwt(d, l1)) * wt.z;
    return I/PI;
}

float sphereAO(in vec3 spherePosition, in float radius, in vec3 point, in vec3 normal)
{
    vec3 sphereUnormL = spherePosition - point;
    vec3 sphereL = normalize ( sphereUnormL );
    float sqrSphereDistance = dot ( sphereUnormL , sphereUnormL );

    float sphereOcc = clamp(dot(sphereL, normal), 0.0, 1.0) *
           ((radius * radius)/sqrSphereDistance);

    return sphereOcc;
}

float lineAO(in vec3 a, in vec3 b, float radius, in vec3 point, in vec3 normal)
{
	 // no ambient occlusion if we're in the capsule
    if (sdCapsule(point, a, b, radius) < 0.0)
        return 1.0;    	

    vec3  T1  = normalize( cross( normal, vec3(0.0,1.0,1.0) ) );
    vec3  T2  = normalize( cross( T1, normal ) );    
    mat3 toObjectSpace = transpose(mat3(T1, T2, normal));


    vec3 p1 = mul(toObjectSpace, a-point);
    vec3 p2 = mul(toObjectSpace, b-point);

    float occ = radius * I_diffuse_line(p1, p2);

    vec3 spherePosition = closestPointOnSegment(a, b, point);

    // hack: AO of the hemisphere ~= sphereAO / 2
    occ += sphereAO(spherePosition, radius, point, normal) / 2.0;    

    return 1.0 - min(1.0, occ);
}



float analyticalLineAO(in vec3 point, in vec3 normal)
{
    float occ = 1.0;

    for(int i=0; i<4; ++i)
    {
        float fi = float(i);
        float x = float(i - ((i/4)*4));
        float y = float(i/4) * 1.0;    	

        occ *= lineAO(vec3(x - 2.25,y+1.25 + sin(iTime + fi),-0.5), 
                         vec3(x - 0.75,y+1.25 + cos(iTime + fi), 1.0),
                         0.1 + CAPSULE_RADIUS * fi, point, normal);
    }

    return occ;    
}


