float remap(float value, float inputMin, float inputMax, float outputMin, float outputMax)
{
    return (value - inputMin) * ((outputMax - outputMin) / (inputMax - inputMin)) + outputMin;
}

vec2 M(){
    float aspectRatio = iResolution.x / iResolution.y;
	float mx = (iMouse.x / iResolution.x - 0.5) * 2.0;
    float my = (iMouse.y / iResolution.y - 0.5) * 2.0;
    return vec2(mx,my);
}

vec3 LightDirection(vec2 xy){
    float R = 1.0;
    float sensitivity = 1.0;
    float x = xy.x * sensitivity;
    float y = xy.y * sensitivity;
    float var = R*R - x*x - y*y;
	float z = sqrt( var );
    if( var < 0.0 )
        z = 0.0;
    return normalize(vec3(x,y,z));  
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float R = 0.5;
    float atten = 1.1;

	vec2 uv = fragCoord.xy / iResolution.xy;
    float aspectRatio = iResolution.x / iResolution.y;

    uv -= 0.5;
    uv.x *= aspectRatio;

    float x = uv.x;
    float y = uv.y;

    // region wobbly
    float t = iTime * 2.0;
    float freq = 10.0;
    float amplitude = remap( sin(t), -1.0, 1.0, 0.01, 0.04);
    float waves = sin(y * freq + t*5.0) + cos(x * freq + t*2.0);
    waves = (waves + 1.0) *  0.5; //normalize 0-1

    R += waves * amplitude - 0.05;
    // endof wobbly

    float nx = x;
    float ny = y;
    float nz = sqrt( R*R - x*x - y*y );
    vec3 n = vec3(nx,ny,nz);

    vec3 color = vec3(0);

    float d = sqrt( dot( uv, uv ) );
    float mask = d < R ? 1.0 : 0.0;

    vec3 lightDirection = LightDirection( M() );

    float diffuse = dot(n,lightDirection);
	diffuse = max(0.0, diffuse) * atten;


    float rim = 1.0 - dot(n, vec3(0,0,1) ) * 3.0;
	rim = max(0.0, rim);
    vec3 rimColor = vec3(1,0,0) * 0.2;


    vec3 lightColor = vec3(0.9, 0.9, 0.7);
    vec3 ambientColor = vec3(0.11, 0.13, 0.15);
    color += vec3(lightColor * diffuse + rim * rimColor + ambientColor);

    color *= mask;

    fragColor = vec4(color,1);

    // DEBUG
	//fragColor = vec4(n,1);

}

