           SmoothBump (0.3, 0.5, 0.05, mod (20. * ro.x, 1.));
      } else if (idObj == idWng) {
        q.x = abs (q.x);
        q -= vec3 (0.5, 0.3, 0.6);
        q.yz = Rot2Cs (q.yz, cs);
        q.xy = Rot2Cs (q.xy, vec2 (cs.x, - cs.y));
        q.xz = Rot2Cs (q.xz, vec2 (cs.x, - cs.y));
        col *= 1. - step (0.02, q.x) * smoothstep (0., 0.2, q.z) * 0.2 *
           SmoothBump (0.3, 0.5, 0.05, mod (30. * q.y, 1.));
      } else if (idObj == idBk) {
        col = mix (vec3 (0.6, 0.6, 0.9) * max (0.7 - 0.3 * dot (rd, vn), 0.), col,
          smoothstep (-1.1, -0.97, ro.z));
      }
    } else {
       col = mix (vec3 (0.1), vec3 (0.8, 0.7, 0.2) * max (0.7 - 0.3 * dot (rd, vn), 0.),
         smoothstep (0.02, 0.04, length (q.yz - vec2 (0.875, -0.65))));
    }
    sh = ObjSShadow (ro, ltDir);
    ltDotVn = max (0., dot (vn, ltDir));
    vnp = VaryNf (1000. * ro, vn, 2.);
    glit = 500. * step (0.01, ltDotVn) *
       pow (max (0., dot (ltDir, reflect (rd, vn))), 16.) *
       pow (1. - 0.6 * abs (dot (normalize (ltDir - rd), vnp)), 8.);
    col += sh * vec3 (1., 1., 0.5) * (glit +
       0.3 * pow (max (dot (normalize (ltDir - rd), vn), 0.), 256.));
    fr = pow (1. - abs (dot (rd, vn)), 5.);
    col = mix (col, vec3 (0.5) * (0.7 + 0.3 * reflect (rd, vn).y), fr);
  } else {
    col = vec3 (0.5) * (0.7 + 0.3 * rd.y);
  }
  col = pow (clamp (col, 0., 1.), vec3 (0.9));
  return col;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float az, el;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.15 * pi;
  if (mPtr.z > 0.) {
    az += 2.5 * pi * mPtr.x;
    el += 0.3 * pi * mPtr.y;
    el = clamp (el, -0.27 * pi, -0.03 * pi);
  } else {
    az += 3.5 * pi * sin (0.014 * pi * tCur);
    el += 0.12 * pi * sin (0.2 * pi * tCur);
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  rd = vuMat * normalize (vec3 (uv, 4.));
  ro = vuMat * vec3 (0., 0., -5.);
  ro.y += 0.15;
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
  fragColor = vec4 (ShowScene (ro, rd), 1);
}

float PrSphDf (vec3 p, float s)
{
  return length (p) - s;
}

float PrEllipsDf (vec3 p, vec3 r)
{
  return (length (p / r) - 1.) * min (r.x, min (r.y, r.z));
}

float PrEllCylDf (vec3 p, vec2 r, float h)
{
  return max ((length (p.xy / r) - 1.) * min (r.x, r.y), abs (p.z) - h);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

const vec4 cHashA4 = vec4 (0., 1., 57., 58.);
const vec3 cHashA3 = vec3 (1., 57., 113.);
const float cHashM = 43758.54;

vec4 Hashv4f (float p)
{
  return fract (sin (p + cHashA4) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec4 t;
  vec2 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv4f (dot (ip, cHashA3.xy));
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  float s;
  const vec3 e = vec3 (0.1, 0., 0.);
  s = Fbmn (p, n);
  g = vec3 (Fbmn (p + e.xyy, n) - s, Fbmn (p + e.yxy, n) - s,
     Fbmn (p + e.yyx, n) - s);
  return normalize (n + f * (g - n * dot (n, g)));
}

//--------------------------------------------------------------------------
#define TWOPI 			(2.0*3.1415926535)
#define ROTATION(alpha) mat2(cos(alpha),-sin(alpha),sin(alpha),cos(alpha))
#define COLORIZATION(h)	clamp(abs(fract(h+vec4(3.0,2.0,1.0,0.0)/3.0)*6.0-3.0)-1.0,0.0,1.0)
#define torus_R 		0.6
#define torus_r 		0.5

//--------------------------------------------------------------------------
// complex multiplication (ax + i*ay) * (bx + i*by)
vec2 complex_mul(vec2 a,vec2 b)
{
    return vec2(a.x*b.x-a.y*b.y,a.x*b.y+a.y*b.x);
} // complex_mul()

//--------------------------------------------------------------------------
// square root
vec2 complex_sqrt(vec2 z)
{
    float r2 	= z.x*z.x + z.y*z.y;
    float theta	= atan(z.y,z.x);
    return sqrt(sqrt(r2))*vec2(cos(0.5*theta),sin(0.5*theta));
} // complex_sqrt()

//--------------------------------------------------------------------------
// cubic root
vec2 complex_cbrt(vec2 z)
{
    float r2 	= z.x*z.x + z.y*z.y;
    float theta	= atan(z.y,z.x);
    return pow(r2,1.0/6.0)*vec2(cos(theta/3.0),sin(theta/3.0));
} // complex_cbrt()

//--------------------------------------------------------------------------
vec2 complex_conj(vec2 z)
{
    return vec2(z.x,-z.y);
} // complex_conj()

//--------------------------------------------------------------------------
// solve the equation A*u^3 + B*u^2 + C*u + D = 0
void solve3(float A,float B,float C,float D,out vec2 u1,out vec2 u2,out vec2 u3)
{
    // First step consists on shifting u = v-B/(3*A)
    // in order to write the equation v^3 + p*v + q = 0
    // (no second term)
    float p 	= (-B*B/(3.0*A)+C)/A;
    float q 	= (2.0*B*B*B/(27.0*A*A)-B*C/(3.0*A)+D)/A;

    float delta = q*q/4.0+p*p*p/27.0;
    vec2 j  	= vec2(cos(TWOPI/3.0),sin(TWOPI/3.0)); // j^3=1
    vec2 j2 	= complex_conj(j);

    if(delta < 0.0) // three real roots
    {
        vec2 x 	= complex_cbrt(vec2(-0.5*q,0.0)+complex_sqrt(vec2(delta,0.0)));
		u1 		= vec2(2.0*(				x).x	,0.0);
		u2 		= vec2(2.0*(complex_mul(j,	x)).x	,0.0);
		u3 		= vec2(2.0*(complex_mul(j2,	x)).x	,0.0);
    }
    else if(delta > 0.0) // one real root and two (conjugated) complex roots
    {
        vec2 x 	= complex_cbrt(vec2(-0.5*q+sqrt(delta),0.0));
        vec2 y  = -p/3.0*complex_conj(x)/dot(x,x);
        u1 		= 					x	+					y	;
        u2 		= complex_mul(j,	x)	+	complex_mul(j2,	y)	;
        u3 		= complex_mul(j2,	x)	+	complex_mul(j,	y)	;
    }
    else // trivial cases
    {
        if(0.0 == p && 0.0 == q)
        {
            u1 	= vec2(0.0,0.0);
            u2 	= vec2(0.0,0.0);
            u3 	= vec2(0.0,0.0);
        }
        else
        {
            u1 	= vec2( 3.0*q/p,0.0);
            u2 	= vec2(-1.5*q/p,0.0);
            u3 	= vec2(-1.5*q/p,0.0);
        }
    }

    // From v to u:
    u1.x -= B/(3.0*A);
    u2.x -= B/(3.0*A);
    u3.x -= B/(3.0*A);

} // solve3()


//--------------------------------------------------------------------------
// solve the equation a*u^3 + b*u^2 + c*u + d = 0
void solve4(float A,float B,float C,float D,float E,out vec2 t1,out vec2 t2,out vec2 t3,out vec2 t4)
{
    // We need to find the roots of the polynomial of degree 4.
    // First step consists on shifting t = s - B/(4.0*A) such that
    // s^4 + p*s^2 + q*s + r = 0 (no cubic terms)
    float p = (-3.0*B*B/(8.0*A)+C)/A;
    float q = (B*B*B/(8.0*A*A)-B*C/(2.0*A)+D)/A;
    float r = (-3.0*B*B*B*B/(256.0*A*A*A)+B*B*C/(16.0*A*A)-B*D/(4.0*A)+E)/A;

    // Now we must solve u^3 + 2*p*u^2 + (p^2-4*r)*u - q^2 = 0
	vec2 u1,u2,u3;
    solve3(1.0,2.0*p,p*p-4.0*r,-q*q,u1,u2,u3);

    // Before finalizing, we need to compute the square root of u1, u2, and u3
    // such that sqrt(u1)*sqrt(u2)*sqrt(u3) = -s
    vec2 v1 = complex_sqrt(u1);
    vec2 v2 = complex_sqrt(u2);
    vec2 v3 = complex_sqrt(u3);
    if(complex_mul(v1,complex_mul(v2,v3)).x*q > 0.0)
    {
        v3 = -v3; // take the other square root of u3
    }

    // We finally got our four roots:
    t1 = 0.5*( v1+v2+v3);
    t2 = 0.5*( v1-v2-v3);
    t3 = 0.5*(-v1+v2-v3);
    t4 = 0.5*(-v1-v2+v3);

    // From s to t:
    t1.x -= B/(4.0*A);
    t2.x -= B/(4.0*A);
    t3.x -= B/(4.0*A);
    t4.x -= B/(4.0*A);

} // solve4()


//--------------------------------------------------------------------------
// Ray is defined by M(t) = P + t.d
vec4 compute(vec3 P,vec3 d)
{
    // Parametrization of the torus by phi and theta angles.
    // x = (R+r*cos(theta))*cos(phi)
    // y = (R+r*cos(theta))*sin(phi)
    // z = r*sin(theta)

    // U*t^2 + V*t + W = 2*r*R*cos(theta)
	float U = dot(d,d);
    float V = 2.0*dot(P,d);
    float W = dot(P,P) - (torus_R*torus_R+torus_r*torus_r);

    // A*t^4 + B*t^3 + C*t^2 + D*t + E = 0
    float A = U*U;
    float B = 2.0*U*V;
    float C = V*V + 2.0*U*W + 4.0*torus_R*torus_R*d.z*d.z;
    float D = 2.0*V*W + 8.0*torus_R*torus_R*P.z*d.z;
    float E = W*W + 4.0*torus_R*torus_R*(P.z*P.z-torus_r*torus_r);

    vec2 t[4]; // complex roots
    solve4(A,B,C,D,E,t[0],t[1],t[2],t[3]);

    float sorted_t[4];
    int   nb_intersection = 0;
    for(int i=0;i<4;i++)
    {
        if(abs(t[i].y)<0.005) // if the root is real, we got an intersection
        {
            nb_intersection ++;
            sorted_t[nb_intersection-1] = t[i].x;
            for(int j=nb_intersection-2;j>=0;j--)
            {
                if(sorted_t[j] > sorted_t[j+1])
                {
                    float tmp 		= sorted_t[j];
                    sorted_t[j] 	= sorted_t[j+1];
                    sorted_t[j+1] 	= tmp;
                }
            } // for()
        }
    } // for()

    // colorization
    vec4 color = vec4(0.0,0.0,0.0,1.0);
    for(int i=0;i<nb_intersection;i++)
    {
        vec3 	M 		= P+sorted_t[i]*d;
        float 	phi 	= atan(M.y,M.x);
        float   theta   = atan(M.z,sqrt(M.x*M.x+M.y*M.y)-torus_R);

        float a = 5.0*phi+15.0*theta+iTime*TWOPI/0.75;
        // make sure the angle is between 0 and 2.pi
		a = mod(TWOPI+mod(a,TWOPI),TWOPI);

        vec4 c;
        if(a < TWOPI/6.0)
            c = vec4(1.0,0.0,0.0,1.0);
        else if(a < 2.*TWOPI/6.0)
            c = vec4(1.0,1.0,1.0,1.0);
        else if(a < 3.*TWOPI/6.0)
            c = vec4(0.0,0.0,1.0,1.0);
        else
            continue; // be transparent

        color = c;
        break; // don't do translucidity
    } // for()


    return vec4(color.rgb,1.0);
} // compute()

