#define TAU 6.28318530718

#define ITER 200
#define QUA .001
#define NORK 5e-4

float map(vec3 p)
{
    p.xy += vec2(cos(p.z),sin(p.z))/2.;
	vec3 cen = round(p);
    vec3 v = abs(cen-p);
	return max(v.x,v.y)-.1;
}

vec3 normal(vec3 p, float k)
{
	vec2 e = vec2(.0,k);
	return normalize(vec3(
		map(p + e.yxx) - map(p - e.yxx),
		map(p + e.xyx) - map(p - e.xyx),
		map(p + e.xxy) - map(p - e.xxy)
	));
}

float tracer(vec3 ro, vec3 rd)
{    
    float t = .0, d;
    for(int i=0; i < ITER; i++){        
        d = map(ro + rd*t);
        if (d < QUA*t) break;
        t += .9 * d;
    } 
    return t;
}

vec3 fog(vec3 col, float t)
{
    vec3 fcol = vec3(.1);
    return mix( col, fcol, 1.0-exp(-t));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    /*int m = int(iResolution.x);
    uint xor = ( uint(fragCoord.x) ^ uint(fragCoord.y) ) % uint(m);
    if(xor != uint(iFrame%m))discard;*/

	vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;

	float ang = atan(uv.x,uv.y) - iTime/8.;
	uv = vec2(cos(ang),sin(ang))*length(uv);

	vec3 ro = vec3(vec2(.5),iTime);
    vec3 rd = normalize(vec3(uv,2));

    float t = tracer(ro,rd);
	vec3 sp = ro + rd*t;

	vec3 sn = normal(sp,NORK);
    vec3 ld = normalize(ro-sp);
	float diff = max(dot(sn,ld),0.);

    vec3 col = (sn.xyz+1.)/2.;
    col *= (3.+diff)/4.;
    col = fog(col, t/8.);

	fragColor = vec4(col,1.);
}

float sdsphere(vec3 p, float r)
{
    return length(p) - r;
}

float map(vec3 p)
{
    return max(
        sdsphere(p - vec3(0.05, 0.0, 0.0), 0.94),
        sdsphere(p + vec3(0.05, 0.0, 0.0), 0.94)
    );
}

float march(vec3 ro, vec3 rd)
{
	float t = 0.0;
    for(int i = 0; i < 32; ++i)
    {
        vec3 p = ro + rd * t;
        float d = map(p);
        t += d * 0.5;
    }
    return t;
}

vec3 ray(vec2 ro, vec3 rd)
{
	vec3 up = vec3(0,1,0);
    vec3 right = cross(rd, up);
    return rd + right * ro.x + up * ro.y;
}

