#define EPSILON 0.002
#define MAX_STEPS 32
#define FAR_CLIP 200.0
#define RADIUS .95

float map(vec3 p)
{
    vec3 s = vec3(2.0);
    vec3 m = mod(p, s) - 0.5 * s;
    return length(m) - sin(iTime * RADIUS) * 0.5 - 0.5;
}

vec3 getNormal(vec3 p)
{
    return normalize(vec3(
	map(vec3(p.x + EPSILON, p.y, p.z)) - map(vec3(p.x - EPSILON, p.y, p.z)),
	map(vec3(p.x, p.y + EPSILON, p.z)) - map(vec3(p.x, p.y - EPSILON, p.z)),
	map(vec3(p.x, p.y, p.z + EPSILON)) - map(vec3(p.x, p.y, p.z - EPSILON))
	));
}

vec3 trace(vec3 o, vec3 r)
{
    float t = 0.0;
    vec3 p;
    for (int i = 0; i < MAX_STEPS; i++)
    {
        p = o + (r * t);
        float d = map(p);

        if (d < EPSILON)
        {
            break;
        }

        t += d * 0.5;

        if (t > FAR_CLIP)
        {
            return vec3(0.0);
        }
    }

    return (getNormal(p) + vec3(1.0)) / 2.0;
}

