    vec3 focus = vec3(0.0, 1.0, iTime + 1.0);

    vec3 lookDir = normalize(focus - o);
    vec3 up = vec3(0.0, 1.0, 0.0);
    vec3 right = cross(lookDir, up);
    float fov = 3.;

    vec3 r = normalize(right * uv.x + up * uv.y + fov * lookDir);

    vec3 t = trace(o, r);

    vec3 col = vec3(0.0, 0.75, 0.5);

    vec3 fc = t;

	fragColor = vec4(fc,1.0);
}

float circle(vec2 p, vec2 center, float radius) {

    float border = .05;

    return smoothstep(radius * (1. - border), radius * (1. + border), length(p - center));
}

float squareWave(float x, float period) {
	return step(period * .5, mod(x, period));
}

float reduceAtZero(float x) {
	return x * x / (x + 1.) / (x + 1.);
}

vec2 twistedPolarCoords(vec2 p, float twistFactor) {
    float r = length(p);
    float a = atan(p.y / p.x);

    a += r * twistFactor;

    return vec2(r * cos(a), r * sin(a));
}

float scene(vec2 p) {

    float displacement = sin((iTime + 2. * p.x + p.y) * 2.5);
    float displacement2 = iTime / 10.;

    p.y += displacement2;

    p = mod(p, .2);

    return circle(p, vec2(0.1 + 0.05 * displacement, 0.1), .05);
}

float fNot1(float x) {
    return 1. - x;
}

float fAnd1(float x, float y) {
    return x * y;
}

float fOr1(float x, float y) {
    return x + y - x * y;
}

float fXor1(float x, float y) {
	return fOr1(fAnd1(x, fNot1(y)), fAnd1(fNot1(x), y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
