    float scene1 = scene(p);
    float scene2 = scene(p.yx);

    float slowDownAtStart = reduceAtZero(iTime / 3.) * reduceAtZero(iTime / 3.);

    float square_size = .5  + .495 * sin(iTime / 3.) + 1.5 -  1.5 * slowDownAtStart;
    vec2 polarTwist = p; //twistedPolarCoords(p, 2. + square_size / 1.55 * 2.);
    float scene3 = (mod(polarTwist.x - square_size / 2., square_size) + mod(polarTwist.y - square_size / 2., square_size)) / square_size / 2.;

    float timeAdjustment = iTime / 5. * slowDownAtStart + .1 * sin(iTime) * slowDownAtStart;

    float radius = .25 + .03 * sin(sin(iTime / 10.) - 1.1 * sin(length(p))) * reduceAtZero(iTime / 5.) + 4. * (1. - reduceAtZero(iTime / 5.));
    float s4 = squareWave(length(p) - timeAdjustment, radius);

    float angle = iTime * .332;
    float s5 = squareWave(length(p - .2 * vec2(cos(angle), sin(angle))) - timeAdjustment, radius);

    float r = fXor1(scene1, fXor1(s4, scene2));

	fragColor = vec4(vec3(
        r * r,
        fXor1(fXor1(scene3 * .75, s5), fXor1(fNot1(s4), scene2)),
        fXor1(s4, fXor1(scene1, scene3))
    ), 1.0);
}

