            vec2 diff2 = neighbour + point2 - f_uv;
            vec2 diff3 = neighbour + point3 - f_uv;

            m_dist = min(m_dist, length(diff)*m_dist);
            m_dist = min(m_dist, length(diff2)*m_dist);
            m_dist = min(m_dist, length(diff3)*m_dist);

        }
    }

    attr = smoothstep(0.01, 0.999, second_m_dist);

    return vec4(sqrt(m_dist*5.));
}

