// training for modeling shapes
// using koltes code as base https://www.shadertoy.com/view/XdByD3
// using iq articles

#define TAU 6.2831853
#define t iTime
mat2 rz2(float a){float c=cos(a),s=sin(a);return mat2(c,s,-s,c);}

float sphere (vec3 p, float r) { return length(p)-r; }
float cube (vec3 p, vec3 r) { return length(max(abs(p)-r,0.)); }
float iso (vec3 p, float r) { return dot(p, normalize(sign(p)))-r; }
float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}
float smin (float a, float b, float r)
{
    float h = clamp(.5+.5*(b-a)/r,0.,1.);
	return mix(b,a,h) - r*h*(1.-h);
}

float map1 (vec3 p)
{
    float size = 1.;
    p += vec3(0,-1.5,0);
    float od = length(p);
    p.xy *= rz2(t*.2);
    p.yz *= rz2(t*.3);
    p.xz *= rz2(t*.4);
    float shape1 = max(-sphere(p,size+.1), iso(p,size));
    p.xy *= rz2(t*1.2);
    p.yz *= rz2(t*1.5);
    p.xz *= rz2(t*2.);   
    float shape2 = max(length(p)-size-.1, -iso(p,size));
	return min(shape1, shape2);
}

float map2 (vec3 p)
{
    vec3 pr = p;
    float count = TAU/5.;
    float a = atan(pr.z,pr.x)+count*.5-t*.3;
    float l = length(pr.xz)-.5;
    a = mod(a,count)-count*.5;
    pr.xz = vec2(cos(a),sin(a))*l;
    float base = max(-sdBox(pr,vec3(1.7)),cube(pr,vec3(2.,1,.1)));
    return smin(base, iso(pr-vec3(1.85,1,0),0.2),0.1);
}

float ripple (vec3 p)
{
    float a = atan(p.z,p.x);
    float fade = 1.-smoothstep(1.,3.5,length(p));
    float base = max(0.,p.y+(sin(a*16.+sin(length(p))*16.+t*2.))*.05*fade);
    return max(base, -sphere(p,1.2));
}

float scene (vec3 p)
{
    float d = smin( map2(p), ripple(p), .9 );
    d = max(sphere( p, 2.9 ), d);
    d = min(map1( p ), d);
    return d;
}

