#define YELLOW vec3(1.,1.,0.)
#define BLUE vec3(0.,0.,1.)

struct Rect
{
    float width;
    float height;
    float x;
    float y;
    vec3  color;
    float rotation;

    vec2 csys; //it's own coordinate system
};

Rect CreateRect(float width, float height, float x, float y, vec3 color)
{
    Rect rect;

    rect.width = width;
    rect.height = height;
    rect.x = x;
    rect.y = y;
    rect.color = color;

    return rect;
}

void DrawRect(inout Rect rect,vec2 p, inout vec3 pix)
{

   	float dx = p.x - rect.x;
    float dy = p.y - rect.y;

    dx = cos(rect.rotation)*(p.x - rect.x) + sin(rect.rotation)*(p.y - rect.y);
    dy = -sin(rect.rotation)*(p.x - rect.x) + cos(rect.rotation)*(p.y - rect.y);

    float fL = - rect.width/2.;  //far left
    float fR = + rect.width/2.;  //far right
    float fT = + rect.height/2.; //far top
    float fB = - rect.height/2.; //far bottom

    //anti-aliasing
    float del;
    del =  smoothstep(fL - 0.005, fL + 0.005,dx);
    del *= 1. - smoothstep(fR - 0.005, fR + 0.005,dx);
    del *=  smoothstep(fB - 0.005, fB + 0.005,dy);
    del *= 1. - smoothstep(fT - 0.005, fT + 0.005,dy);

    float d = 0.005;

    if(dx > fL-d && dx < fR+d 
    && dy < fT+d && dy > fB-d)
    {
        pix = mix(pix,rect.color,del);
    }

    rect.csys = vec2(dx,dy);
}

float GetPathCoord(float p1, float p2, float percent)
{
   return p1 + ((p2 - p1) * percent);
}

const int NUM_PATHS = 6;
vec2 BezierPaths[NUM_PATHS];

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 r = 2.* vec2(fragCoord.xy - .5*iResolution.xy)/iResolution.y;
    vec3 pixel = vec3(0.);

	BezierPaths[0] = vec2(0.,1.);
    BezierPaths[1] = vec2(0.0,0.8);
    BezierPaths[2] = vec2(0.,0.);
    BezierPaths[3] = vec2(0.4,0.0);
    BezierPaths[4] = vec2(0.45,0.0);

    BezierPaths[5] = vec2(1.,0.);


    //Draw the bezier path points

    for(int i = 0; i < NUM_PATHS; i++)
    {
        Rect tmp_rect = CreateRect(0.05,0.05,BezierPaths[i].x, BezierPaths[i].y,YELLOW);
        DrawRect(tmp_rect,r,pixel);
    }

    float percent = (mod(iTime,4.0))/ 4.0;

    //Bezier path calculation for multiple paths
	int runs = NUM_PATHS-1;
	vec2 prev[NUM_PATHS];

   	for(int i = 0; i < NUM_PATHS; i++)
    {
        prev[i] = BezierPaths[i];
    }

    vec2 tmp[NUM_PATHS];
    for(int i = 0; i < runs; i++)
    {
        for(int k = 0; k < runs-i; k++)
        {
            tmp[k].x = GetPathCoord(prev[k].x , prev[k+1].x, percent);
            tmp[k].y = GetPathCoord(prev[k].y , prev[k+1].y, percent);
        }

        for(int k = 0; k < runs-i; k++)
        {
            prev[k] = tmp[k];
        }
    }

    //Draw Bezier Point which follows the path
    Rect rp = CreateRect(0.05,0.05,tmp[0].x,tmp[0].y,BLUE);
	DrawRect(rp,r,pixel);

    fragColor = vec4(pixel,1.);
}

