void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   //https://www.shadertoy.com/view/ldjSzd
    bool iMouseDown = !(iMouse.z < 0.);//thanks aaaidan

    vec2 uv = fragCoord.xy / iResolution.x;
    float height = iResolution.y / iResolution.x;

    vec2 normMouse = iMouse.xy / iResolution.x;

    //corrected for preview per FabriceNeyret
    if(!iMouseDown || iMouse == vec4(0.)) { normMouse = vec2(0.5,0.5*height); }

    vec2 dirMouse = normMouse-uv;
    vec2 clamped = 1.0-step(abs(dirMouse), vec2(1.0)/iResolution.x); 
    //todo: make 1px wide instead of 2, round to upper right

	vec3 color = vec3(uv,0.5+0.5*sin(iTime));
    vec3 negColor = 1.0-color;
    vec3 choosenColor = mix(negColor,color,clamped.x*clamped.y);


    fragColor =vec4(choosenColor,1.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float radius = 0.4 * min(iResolution.x, iResolution.y);
    vec3 blur = 30.0 + 28.0 * sin(iTime * vec3(1, 1.1, 1.2));
    vec2 c = 0.5 * iResolution.xy;
    float d0 = max(0.0, distance(fragCoord, c) - radius);
    vec3 dist = smoothstep(-0.25 * blur, blur, vec3(d0));
    vec2 pt = fragCoord - c;
    float amul = 1.0 + 0.5 * sin(iTime * 0.002) * sin(iTime + atan(pt.y, pt.x));
    float alpha = 1.0 - smoothstep(0.0, amul * max(max(blur.x, blur.y), blur.z), d0);
	fragColor = vec4(
        alpha * dist,
        1.0
    );
}

// Computes the inverse of a given quaternion
vec4 QtnInverse(vec4 qtn)
{
    return vec4(qtn.xyz * -1.0, qtn.w);
}

// Compute the product of two quaternions
vec4 QtnProduct(vec4 qtnA, vec4 qtnB)
{
    vec3 vecA = qtnA.w * qtnB.xyz;
    vec3 vecB = qtnB.w * qtnA.xyz;
    vec3 orthoVec = cross(qtnA.xyz, qtnB.xyz);

    return vec4(vecA + vecB + orthoVec,
                 (qtnA.w * qtnB.w) - dot(qtnA.xyz, qtnB.xyz));
}

// Perform rotation by applying the given quaternion to
// the given vector
vec3 QtnRotate(vec3 vec, vec4 qtn)
{
    vec4 qv = QtnProduct(qtn, vec4(vec, 0.0));
    return QtnProduct(qv, QtnInverse(qtn)).xyz;
}

// Manual implementation of [sinh] for devices with early
// versions of GLSL ES
float Sinh(float area)
{
    float e = 2.718;
    return (pow(e, area) - pow(e, area * -1.0)) / 2.0;
}

// Manual implementation of [cosh] for devices with early
// versions of GLSL ES
float Cosh(float area)
{
    float e = 2.718;
    return (pow(e, area) + pow(e, area * -1.0)) / 2.0;    
}

float TrigDF(vec3 coord, vec3 trigPos)
{
    coord -= trigPos;
    coord = QtnRotate(coord, vec4(vec3(0.9, 0.0, 0.0) * sin(iTime), cos(iTime)));
    float coordAccum = coord.x * coord.y + coord.z;
    return length(coord) - Cosh(coordAccum) / Sinh(coordAccum);
}

vec3 GetNormal(vec3 samplePoint, float eps, vec3 trigPos)
{
    float normXA = TrigDF(vec3(samplePoint.x + eps, samplePoint.y, samplePoint.z), trigPos);
    float normXB = TrigDF(vec3(samplePoint.x - eps, samplePoint.y, samplePoint.z), trigPos);

    float normYA = TrigDF(vec3(samplePoint.x, samplePoint.y + eps, samplePoint.z), trigPos);
    float normYB = TrigDF(vec3(samplePoint.x, samplePoint.y - eps, samplePoint.z), trigPos);

    float normZA = TrigDF(vec3(samplePoint.x, samplePoint.y, samplePoint.z + eps), trigPos);
    float normZB = TrigDF(vec3(samplePoint.x, samplePoint.y, samplePoint.z - eps), trigPos);

    return normalize(vec3(normXA - normXB,
                          normYA - normYB,
                          normZA - normZB));
}

vec3 RayDir(float fovRads, vec2 viewSizes, vec2 pixID)
{
    vec2 xy = pixID - (viewSizes / 2.0);
    float z = viewSizes.y / tan(fovRads / 2.0);
    return normalize(vec3(xy, z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float eps = 0.00001;
    float currRayDist = 0.0;
	vec3 rayDir = RayDir(3.14 / 2.0, iResolution.xy, fragCoord);
    vec3 trigPos = vec3(0.0, 0.0, 4.0);
    for (int i = 0; i < 255; i += 1)
    {
        vec3 eyePos = vec3(0.0, 0.0, -3.0);
    	vec3 rayVec = eyePos + (rayDir * currRayDist);
        float trgDist = TrigDF(rayVec, trigPos);
        if (trgDist < eps)
        {   
            float nDL = dot(GetNormal(rayVec, eps, trigPos), 
                            normalize(vec3(1.0, 1.0, -1.0)));
            fragColor = vec4(nDL, nDL, nDL, 1.0);
            return;
        }

        currRayDist += trgDist;
    }

	fragColor = vec4(0.8, 0.8, 0.8, 1.0);
}

//Learning from : http://www.michaelwalczyk.com/blog/2017/5/25/ray-marching

//Contains some experimental terms to simulate global illumination
//on backside of object via a variation
//on specular highlights, and the use of Fresnel terms
//for both light and shadow on the outside of objects.

//Comments encouraged - code will be commented for teaching purposes soon.

//--CaliCoastReplay

float distance_from_sphere(in vec3 world_point, in vec3 sphere_center, float radius)
{
    return length(world_point - sphere_center) - radius;
}


float map_the_world(in vec3 world_point)
{

 //   float sphere_0 = distance_from_sphere(p, vec3(-0.4 + cos(iTime), 0.0, 0.0), 1.5);    
 //   float sphere_1= distance_from_sphere(p, vec3(2.4 + sin(iTime), 1.0, 1.5), 1.5);
 //   float sphere_2= distance_from_sphere(p, vec3(-0.0 + cos(iTime+1.57), 2.0,3.0),1.5);

    float sphere_0 = distance_from_sphere(world_point, vec3(-0.4, 0.0, 0.0), 1.5);    
    float sphere_1= distance_from_sphere(world_point, vec3(2.4, 1.0, 1.0), 1.5);
    float sphere_2= distance_from_sphere(world_point, vec3(-0.0, 2.0,2.0),1.5);

    return min(min(sphere_0, sphere_1), sphere_2);
}

vec3 calculate_normal(in vec3 world_point)
{
    const vec3 small_step = vec3(0.001, 0.0, 0.0);

    float gradient_x = map_the_world(world_point + small_step.xyy)
        - map_the_world(world_point - small_step.xyy);
    float gradient_y = map_the_world(world_point + small_step.yxy) 
        - map_the_world(world_point - small_step.yxy);
    float gradient_z = map_the_world(world_point + small_step.yyx) 
        - map_the_world(world_point - small_step.yyx);

    vec3 normal = vec3(gradient_x, gradient_y, gradient_z);

    return normalize(normal);
}

vec3 ray_march(in vec3 ray_origin, in vec3 ray_direction)
{
    float total_distance_traveled = 0.0;
    const int NUMBER_OF_STEPS = 64;
    const float MINIMUM_HIT_DISTANCE = 0.001;
    const float MAXIMUM_TRACE_DISTANCE = 1000.0;

    for (int i = 0; i < NUMBER_OF_STEPS; ++i)
    {
        vec3 current_position = ray_origin + total_distance_traveled * ray_direction;

		float distance_to_closest = map_the_world(current_position);

        if (distance_to_closest < MINIMUM_HIT_DISTANCE) 
        {
            vec3 normal = calculate_normal(current_position);

            vec3 light_positions[3];
            light_positions[0] = vec3(1.0+sin(iTime)*5.0, -3.0+3.0*cos(iTime/3.0), 4.0 + 1.0 *sin(iTime/5.0));
            light_positions[1] = vec3(1.0-sin(iTime/2.0)*2.0, -1.0-cos(iTime/2.0), 7.0 + 1.0 -sin(iTime/4.0));
            light_positions[2] = vec3(2.0-sin(iTime/2.0)*2.0, -5.0-sin(iTime/4.0), 2.0 + 1.0 -sin(iTime/1.0));
            float light_intensities[3];
            light_intensities[0] = 0.8;
            light_intensities[1] = 0.4;
            light_intensities[2] = 0.7;
            vec3 direction_to_view = normalize(current_position - ray_origin);

            vec3 col = vec3(0.0);

            for (int j = 0; j < 3; j++)
            {
                vec3 direction_to_light = normalize(current_position - light_positions[j]);
                vec3 light_reflection_unit_vector =
                	 reflect(direction_to_light ,normal);                

                float diffuse_intensity = 0.6*pow(max(0.0, dot(normal, direction_to_light)),5.0);            
                float ambient_intensity = 0.2;            
                float specular_intensity = 
                    1.15* pow(clamp(dot(direction_to_view, light_reflection_unit_vector), 0.0,1.0), 50.0);
                float backlight_specular_intensity =             
                    0.2* pow(clamp(dot(direction_to_light, light_reflection_unit_vector),0.0,1.0), 3.0); 
                float fresnel_base = 1.0 + dot(direction_to_view, normal);
                float fresnel_intensity = 0.10*pow(fresnel_base, 0.3);
                float fresnel_shadowing = pow(fresnel_base, 5.0);            
                float fresnel_supershadowing = pow(fresnel_base, 50.0);
                float attenuation =  pow(total_distance_traveled,2.0)/180.0;


            	vec3 colFromLight = vec3(0.0);
                colFromLight += vec3(0.89, 0.0, 0.0) * diffuse_intensity;
                colFromLight += vec3(0.3, 0.1, 0.1) * ambient_intensity;
                colFromLight += vec3(1.0) * specular_intensity;            
                colFromLight += vec3(1.0,0.5,0.5) * backlight_specular_intensity;            
                colFromLight += vec3(1.0, 0.1, 0.2) * fresnel_intensity;
                colFromLight -= vec3(0.0, 1.0, 1.0) * fresnel_shadowing ;
                colFromLight -= vec3(0.0, 1.0, 1.0) * fresnel_supershadowing * col * col;
                colFromLight += vec3(.3, 0.1, 0.1) - attenuation ; 
               	colFromLight /= 1.2;
                colFromLight *= light_intensities[j];
                col += colFromLight;
            }
            return col;
        }


        if (total_distance_traveled > MAXIMUM_TRACE_DISTANCE)
        {
            break;
        }
        total_distance_traveled += distance_to_closest;
    }
    return vec3(0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy - iResolution.xy / 2.) / iResolution.y;

    vec3 camera_position = vec3(0.5, 0.5 + sin(iTime)/2.0, -6.0+cos(iTime));
    vec3 ray_origin = camera_position;
    vec3 ray_direction = vec3(uv, 1.0);

    vec3 shaded_color = ray_march(ray_origin, ray_direction);

    fragColor = vec4(shaded_color, 1.0);
}

#define PI 3.1415926535897932384626433832795

float map(float v, float low1, float high1, float low2, float high2) {
	return (v-low1)/(high1-low1)*(high2-low2);
}

vec2 xyzToLonLat(vec3 v) {
    vec3 p = normalize(v);
    float lat = map(asin(p.y), PI*0.5, -PI*0.5, 0.0, 1.0);
    float lon = map(atan(p.x, -p.z), PI, -PI, 0.0, 1.0);
    return vec2(lon, lat);
}

vec3 lonLatToXYZ(vec2 lonLat) {
  float lon = map(lonLat.x, 0.0, 1.0, -PI, PI);
  float lat = map(lonLat.y, 0.0, 1.0, -PI*0.5, PI*0.5);
  float x = sin(lat)*sin(lon);
  float y = cos(lat);
  float z = sin(lat)*cos(lon);
  return vec3(x,y,z);
}

vec3 xRot(vec3 v, float theta) {
  float x = v.x;
  float y = v.y*cos(theta) - v.z*sin(theta);
  float z = v.y*sin(theta) + v.z*cos(theta);
  return vec3(x,y,z);
}

vec3 yRot(vec3 v, float theta) {
  float x = v.z*sin(theta) + v.x*cos(theta);
  float y = v.y;
  float z = v.z*cos(theta) - v.x*sin(theta);
  return vec3(x,y,z);
}

vec3 zRot(vec3 v, float theta) {
  float x = v.x*cos(theta) - v.y*sin(theta);
  float y = v.x*sin(theta) + v.y*cos(theta);
  float z = v.z;
  return vec3(x,y,z);
}

vec2 equiRemap(vec2 lonLat, vec2 delta) {
    vec3 v = lonLatToXYZ(lonLat);
	v = yRot(v,delta.x);
    v = xRot(v,delta.y);
    return xyzToLonLat(v);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float graticuleSize = 0.05;
    float graticuleWeight = 0.003;
    //line to be visualised

    //normalized lonLat
	vec2 lonLat = fragCoord.xy / iResolution.xy;
    lonLat = equiRemap(lonLat, vec2(iTime*0.2,iTime*0.4));
	fragColor = vec4(lonLat,0.0,1.0);
    vec2 graticuleDist = mod(lonLat + vec2(graticuleWeight*0.5),vec2(graticuleSize));
    if (graticuleDist.x < graticuleWeight || graticuleDist.y < graticuleWeight) {
    	fragColor += vec4(0.5);
    }

    //visualising a rhumb line, x is lon, y is lat
    //normalized over 
    vec2 p0 = vec2(0.0,0.0);
    vec2 p1 = vec2(7.0,1.0);
    float slope = (p1.y-p0.y)/(p1.x-p0.x);
    float theta = atan(p1.y-p0.y,p1.x-p0.x);
    float lineHalfWeight = 0.01*(1.0+cos(theta));
    bool b0 = atan(mod(lonLat.y,slope)-p0.y,lonLat.x-p0.x+lineHalfWeight) < theta;
    bool b1 = atan(mod(lonLat.y,slope)-p0.y,lonLat.x-p0.x-lineHalfWeight) > theta;
    if (b0 && b1 ){
    	fragColor = vec4(0.0, 0.0, 1.0, 1.0);
    }
}

#define M_PI2 1.570796326
#define MOD .001
#define FREQ .8

float cosinus (float angle, float amplitude) { return cos(angle / FREQ) * amplitude; }

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
	vec2 lM = vec2(max(iResolution.x, iResolution.y), min(iResolution.x, iResolution.y));
	vec2 pos = ( fragCoord -.5 * lM) / lM.x;

	float advance = pos.x + .5;

	float thickness = MOD / 1.5;
	float val = iTime * .4 + (advance / MOD) * M_PI2;
	float size = thickness * 5. * (cosinus(val, 1.) + 2.);

	pos.x = mod(advance, MOD);
	bool isIn = abs(pos.y) - thickness < 0.;
	if (distance(pos, vec2(0., sin(val / FREQ) * .05)) < size && (!isIn || cosinus(val, 1.) > .1 * M_PI2)) 
        fragColor = vec4(cosinus(val, -1.), cosinus(val, 1.), .8, 1.);
	else if (isIn) {
		float delta = abs(sin(advance - iTime));
		fragColor = vec4(.2, .1 + .1 * delta, .4 + .3 * delta, 1.);
	}
	else fragColor = vec4(0.);
}

