float dfpart(vec3 pos, float rep,float interp){
    vec3 rep3 = vec3(rep,rep,rep);
	pos = mod(pos+rep3,rep3*2.0)-rep3;
    float d=rep;
    /*d = min(d,length(pos.xy));
    d = min(d,length(pos.yz));
    d = min(d,length(pos.zx));
    return rep*.5-d;*/
    return rep*(1.15+interp*.27)-length(pos);
}

float hash(vec2 v){
	return fract(fract(sin(dot(v,vec2(51.651244215,2.141625)*7.12311)*412.1234)*517.5126751)*711.123173173+iTime);
}

float df(vec3 pos){
    float interp = cos(iTime*0.1);
    interp = interp*interp*interp;
    float d = 0.0,e=16.0;
    for (float i=.0; i<4.; i++){
    	d = max(d,dfpart(pos,e,interp));
        if (d>e*.125) break;
        e*=.2712;
    }
    return d;
}

vec3 nf(vec3 p){
    vec2 e = vec2(.0,.001);
    float c = df(p);
    return normalize(vec3(df(p-e.yxx)-df(p+e.yxx),df(p-e.xyx)-df(p+e.xyx),df(p-e.xxy)-df(p+e.xxy)));
}

void rot(inout vec2 v,float a){
	float c=cos(a),s=sin(a);
    v*=mat2(c,s,-s,c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.yy - vec2(.85,.5);
    float t = iTime + hash(uv)/60.0;
    vec3 p = sin(vec3(t*.172, t*.271, t*.314)*2.0)+t;
    float luv = length(uv);
    vec3 dir = normalize(vec3(uv.xy,1.0-luv*luv*.6));

    vec3 lightp = vec3(t) + normalize(sin(p*.1))*5.0 + dir*15.0;

    rot(dir.xy,t*.1);
    rot(dir.yz,t*.05);
    rot(dir.zx,t*.025);
    p+=dir*hash(uv)*.1;
    float it;

    for(float i=0.0; i<100.0; i+=1.){
    	float d = df(p);
        p+=d*dir;
        it = i;
        if (d<.01){
        	break;
        }
    }

    vec3 d2 = normalize(lightp-p);
    float td = .01;
    vec3 p2 = p+d2*(td+td*hash(uv));
    float occlusion = 1.0;
    float mtd = distance(lightp,p);

    for(float i=0.0; i<100.0;i++){
        float d = df(p2);
    	p2 += d*d2;
        td += d;
        if (td>mtd || occlusion<.0) break;
        occlusion = min(occlusion,d/(td*.05));
    }
    occlusion=max(occlusion,0.0);

    float diffuse = dot(nf(p),-d2)*.5+.5;
    vec3 lcolor = vec3(.9,.3,.1)*.5;
    vec3 color= diffuse*lcolor/(1.0+mtd*mtd*.0005)*4.0*occlusion+lcolor/(1.0+mtd*mtd*.0005)*.5;
    color += (vec3(1)-lcolor)*(td*.001+occlusion+it/200.0)*.5;
	color *= (1.0-length(uv));
    color = vec3(1.8)*color/(vec3(1)+color);
    color = pow(color,vec3(0.8));

    //color = nf(p)*.5+.5;
	fragColor = vec4(color,1.0);
}

float circle(vec2 uv, float r, float blur) {
 	float d = length((uv)*5.0);
    float c = smoothstep(r, r-blur, d);
    return c;
}

mat2 rotate2d(vec2 uv, float angle) {
 	return mat2(-cos(angle),sin(angle),-sin(angle),-cos(angle));   
}

mat2 scale2d(vec2 scale) {
	return mat2(scale.x,0.0,0.0,scale.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    float f = texture(iChannel0, vec2(20.0)).r;

    uv *= 6.0;

    float c = circle(uv, 5.5*sin(iTime)+6.0+f, 0.2);
    vec3 color = vec3(c);
    vec3 color2 = color;
    vec2 leftCircle = uv + vec2(0.5*sin(iTime),0.0);
    vec2 rightCircle = uv + vec2(-0.5*sin(iTime),0.0);
    float d = length(leftCircle);
    float d2 = length(rightCircle);

    uv = rotate2d(uv,100.0*sin(iTime/10.0))*uv;
    uv = scale2d(vec2(5.0*sin(iTime/5.0)+2.5))*uv;

    color *= vec3(fract(d*20.0*f),fract(d*20.0*f),uv.x);
    color2 *= vec3(fract(d2*20.0*f),uv.x,fract(d2*20.0*f));
    color += color2;
    color += vec3(0.0,sin(uv.y*20.0)*sin(iTime/5.0),sin(uv.y*20.0));

	fragColor = vec4(color,1.0);
}

/*

single pass tracing of a "bezier-patch"
and its 4*4 control points;

https://www.shadertoy.com/view/MtsXzl
Shows that these are seamless.
...There multiple iterations are just multiple octaves of noise
...to vary amplitude and frequency over time more than here.
... here frequency is constant, duh.

2 cubic bezier splines (4 CVs) over 2 domains
1 quadratic interpolation of 2 domain's cubic beziers.

this is a re-post of 
https://www.shadertoy.com/view/ltsXzl
with global "float a=b" changed to "#define a (b)"
This increases compatibility and compile time.


Textures disabled, they are just distracting.

cubic spline has nice first and second derivative.
first derivative at corner points
tends to be close to 0, but unlike smoothstep() it is not ==0.
that would be possible, but look worse for no good reason.

Using ray marching to render a cubic hermite rectangle. 
Raytrace bounding box, raymarch interior. 
The control points of the rectangle are 1d, 
which makes it easier to render, 
but limits the control points to only moving on the Y axis.
*/


/*
3 modesof computing the bilinear smoothstep()
mode==0 ; original, likely slowest
mode==1 ; likely faster, a few less multiplications
mode==-1; likely fastest, like mode1, but with type vec3()
*/
#define mode 0

#define SHOW_BOUNDINGBOX   0
#define SHOW_CONTROLPOINTS 1

float scale = 0.5;
float offset = 0.5;

#define CP00 ( (sin(iTime*0.30) * 0.5 + 0.5) * scale + offset)
#define CP01  ((sin(iTime*0.10) * 0.5 + 0.5) * scale + offset)
#define CP02  ((sin(iTime*0.70) * 0.5 + 0.5) * scale + offset)
#define CP03  ((sin(iTime*0.52) * 0.5 + 0.5) * scale + offset)
#define CP10  ((sin(iTime*0.20) * 0.5 + 0.5) * scale + offset)
#define CP11  ((sin(iTime*0.40) * 0.5 + 0.5) * scale + offset)
#define CP12  ((sin(iTime*0.80) * 0.5 + 0.5) * scale + offset)
#define CP13  ((sin(iTime*0.61) * 0.5 + 0.5) * scale + offset)
#define CP20  ((sin(iTime*0.50) * 0.5 + 0.5) * scale + offset)
#define CP21  ((sin(iTime*0.90) * 0.5 + 0.5) * scale + offset)
#define CP22  ((sin(iTime*0.60) * 0.5 + 0.5) * scale + offset)
#define CP23  ((sin(iTime*0.32) * 0.5 + 0.5) * scale + offset)
#define CP30  ((sin(iTime*0.27) * 0.5 + 0.5) * scale + offset)
#define CP31  ((sin(iTime*0.64) * 0.5 + 0.5) * scale + offset)
#define CP32  ((sin(iTime*0.18) * 0.5 + 0.5) * scale + offset)
#define CP33  ((sin(iTime*0.95) * 0.5 + 0.5) * scale + offset)

#define FLT_MAX 3.402823466e+38

//=======================================================================================


#if mode==0
 float CubicHermite (float A, float B, float C, float D, float t){
	float t2 = t*t;
    float t3 = t*t*t;
    float a = -A/2.0 + (3.0*B)/2.0 - (3.0*C)/2.0 + D/2.0;
    float b = A - (5.0*B)/2.0 + 2.0*C - D / 2.0;
    float c = -A/2.0 + C/2.0;
   	float d = B;    
    return a*t3 + b*t2 + c*t + d;
 }
//above is easy to modify, below likely performs faster
#endif

