bool sameSide(vec3 p1,vec3 p2, vec3 a,vec3 b) {
    vec3 cp1 = cross(b-a, p1-a);
    vec3 cp2 = cross(b-a, p2-a);
    return (dot(cp1, cp2) >= 0.);
}

bool pointInTriangle(vec3 p, vec3 a,vec3 b,vec3 c) {
    return (
        sameSide(p,a, b,c) &&
        sameSide(p,b, a,c) &&
        sameSide(p,c, a,b)
    );
}

