    mat2 r = rotate2d(PI/20. + t / PI);
    bool e = false;
    int iter = 30;

    vec3 bias = vec3(0., .16666, .0);

    vec3 a = vec3(  0, .5, 0) + bias;
    vec3 b = vec3(-.5,-.5, 0) + bias;
